// Helper function to draw dashed lines
function drawDashedLine(x1, y1, x2, y2, dash = 5, space = 5) {
  const total = dist(x1, y1, x2, y2);
  const angle = atan2(y2 - y1, x2 - x1);
  let drawn = 0;
  while (drawn < total) {
    const end = min(drawn + dash, total);
    const sx = x1 + cos(angle) * drawn;
    const sy = y1 + sin(angle) * drawn;
    const ex = x1 + cos(angle) * end;
    const ey = y1 + sin(angle) * end;
    line(sx, sy, ex, ey);
    drawn += dash + space;
  }
}

