  ellipse(cx, cy, r * 2);
  fill(255);
  textSize(16);
  textAlign(CENTER, CENTER);
  text('Earth', cx, cy);

  // Syene radial
  stroke(0);
  strokeWeight(1);
  drawDashedLine(cx, cy, cx + r, cy);

  // Label Syene
  fill(0);
  textSize(12);
  noStroke();
  text('Syene', cx + r + 5, cy - 5);

  // Alexandria point
  const alexAngle = angle; // degrees
  const alexX = cx + r * cos(alexAngle);
  const alexY = cy + r * sin(alexAngle);

  // Alexandrias radial
  stroke(0);
  strokeWeight(1);
  drawDashedLine(cx, cy, alexX, alexY);

  // Horizontal line through Alexandria
  drawDashedLine(cx - r, alexY, cx + r, alexY);

  // Label Alexandria
  text('Alexandria', alexX + 5, alexY - 5);

  // Angle arc and label
  const radialAngle = atan2(cy - alexY, cx - alexX); // in radians
  const radialAngleDeg = radialAngle * 180 / PI;
  const arcR = 30;
  noFill();
  stroke(0);
  strokeWeight(2);
  arc(alexX, alexY, arcR * 2, arcR * 2, radialAngleDeg, radialAngleDeg + angle);

  // Angle value
  fill(0);
  noStroke();
  textSize(12);
  const midAngle = radialAngleDeg + angle / 2;
  const labelX = alexX + arcR * cos(midAngle);
  const labelY = alexY + arcR * sin(midAngle);
  text(angle.toFixed(1) + '°', labelX, labelY);

  // Small triangle pointing downward at Alexandria
  push();
  translate(alexX, alexY);
  rotate(radialAngleDeg + angle + 90); // orient so it points outward
  fill(0);
  noStroke();
  triangle(-5, 0, 5, 0, 0, -10);
  pop();

  // Sun's rays on the right side
  stroke('#DAA520');
  strokeWeight(2);
  for (let y = 40; y < height - 40; y += 15) {
    line(width - 40, y, width - 10, y);
    // Arrow head
    push();
    fill('#DAA520');
    triangle(width - 10, y - 3, width - 10, y + 3, width - 15, y);
    pop();
  }
}

// Zoomed sector view drawing
function drawZoomView() {
  const cx = 150;
  const cy = 350;
  const r = 200;

  // Earth as full circle for visual reference
  fill('#468296');
  noStroke();
  ellipse(cx, cy, r * 2);

  // Symmetric scenario: a pie slice not fully visible but circle shown
  // Syene radial (horizontal)
  stroke(0);
  strokeWeight(1);
  drawDashedLine(cx, cy, cx + r, cy);

  // Alexandria radial
  const alexAngle = angle;
  const alexX = cx + r * cos(alexAngle);
  const alexY = cy + r * sin(alexAngle);
  drawDashedLine(cx, cy, alexX, alexY);

  // Labels
  fill(0);
  textSize(12);
  noStroke();
  text('Syene', cx + r + 5, cy - 5);
  text('Alexandria', alexX + 5, alexY - 5);

  // Angle arc at center
  const arcR = 50;
  noFill();
  stroke(0);
  strokeWeight(2);
  arc(cx, cy, arcR * 2, arcR * 2, 0, angle);

  // Angle value
  fill(0);
  noStroke();
  textSize(12);
  const midAngle = angle / 2;
  const labelX = cx + arcR * cos(midAngle);
  const labelY = cy + arcR * sin(midAngle);
  text(angle.toFixed(1) + '°', labelX, labelY);

  // Sun's rays on the right side (same as full view)
  stroke('#DAA520');
  strokeWeight(2);
  for (let y = 40; y < height - 40; y += 15) {
    line(width - 40, y, width - 10, y);
    // Arrow head
    push();
    fill('#DAA520');
    triangle(width - 10, y - 3, width - 10, y + 3, width - 15, y);
    pop();
  }

  // Post and shadow right-angled triangle at Alexandria
  // Right triangle with horizontal base and vertical side (approximation)
  const triSize = 20;
  const triA = createVector(alexX, alexY);
  const triB = createVector(alexX + 30, alexY);
  const triC = createVector(alexX, alexY - 20); // vertical upwards
  fill('#D2B48C'); // tan color
  noStroke();
  triangle(triA.x, triA.y, triB.x, triB.y, triC.x, triC.y);
}
</script>
</body>
</html>
