<div id="app-container">
  <div id="control-panel">
    <button id="btn-reset">+</button>
    <div class="control-row">
      <label for="slider-moon-pos">position of moon</label>
      <input type="range" id="slider-moon-pos" min="0" max="360" step="1" value="144">
    </div>
    <div class="control-row">
      <label for="slider-moon-pull">pull from moon</label>
      <input type="range" id="slider-moon-pull" min="0" max="100" step="1" value="40">
    </div>
    <div class="control-row">
      <label for="slider-sun-pos">position of sun</label>
      <input type="range" id="slider-sun-pos" min="0" max="360" step="1" value="144">
    </div>
    <div class="control-row">
      <label for="slider-sun-pull">pull from sun</label>
      <input type="range" id="slider-sun-pull" min="0" max="100" step="1" value="20">
    </div>
  </div>
  <div id="viz-container"></div>
</div>

<script>
(function(){
  // Default slider values
  const DEFAULTS = {
    moonPos: 144,
    moonPull: 40,
    sunPos: 144,
    sunPull: 20
  };

  // Grab slider elements
  const sliders = {
    moonPos: document.getElementById('slider-moon-pos'),
    moonPull: document.getElementById('slider-moon-pull'),
    sunPos: document.getElementById('slider-sun-pos'),
    sunPull: document.getElementId('slider-sun-pull')
  };

  // Helper to trigger redraw
  const triggerRedraw = () => { if (p5Instance) p5Instance.redraw(); };

  // Create p5 instance
  let p5Instance = new p5(p => {
    const center = {x: 250, y: 250};
    const earthRadius = 180;
    const baseWaterRadius = 200; // a bit bigger than Earth
    const scalingFactor = 2;     // controls bulge amplitude

    p.setup = () => {
      p.createCanvas(500, 500);
      p.noLoop();
      p.angleMode(p.RADIANS);
      p.redraw();  // initial draw
    };

    p.draw = () => {
      p.clear();

      const moonAngleRad = p.radians(parseFloat(sliders.moonPos.value));
      const sunAngleRad  = p.radians(parseFloat(sliders.sunPos.value));
      const moonPull = parseFloat(sliders.moonPull.value);
      const sunPull  = parseFloat(sliders.sunPull.value);

      // Draw water layer
      p.beginShape();
      p.fill('#aed6f1');
      p.stroke('#000');
      p.strokeWeight(1);
      for (let deg = 0; deg <= 360; deg += 1) {
        const theta = p.radians(deg);
        const moonEffect = moonPull * Math.pow(Math.cos(theta - moonAngleRad), 2);
        const sunEffect  = sunPull  * Math.pow(Math.cos(theta - sunAngleRad), 2);
        const totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);
        const x = center.x + totalRadius * Math.cos(theta);
        const y = center.y + totalRadius * Math.sin(theta);
        p.vertex(x, y);
      }
      p.endShape(p.CLOSE);

      // Draw Earth
      p.noStroke();
      p.fill('#966F33');
      p.ellipse(center.x, center.y, earthRadius*2, earthRadius*2);
    };
  }, 'viz-container');

  // Reset button
  document.getElementById('btn-reset').addEventListener('click', () => {
    sliders.moonPos.value = DEFAULTS.moonPos;
    sliders.moonPull.value = DEFAULTS.moonPull;
    sliders.sunPos.value = DEFAULTS.sunPos;
    sliders.sunPull.value = DEFAULTS.sunPull;
    triggerRedraw();
  });

  // Slider listeners
  Object.values(sliders).forEach(slider => {
    slider.addEventListener('input', triggerRedraw);
  });
})();
</script>
</body>
</html>
