<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Comet Holmes Simulation</title>
<link rel="preconnect" href="https://cdnjs.cloudflare.com/ajax/libs/three.js/r134/three.min.js">
<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r134/three.min.js"></script>
<style>
  html, body { height: 100%; margin: 0; font-family: Arial, sans-serif; }
  #app-container { display: flex; height: 100%; }
  #controls-panel {
    width: 220px; padding: 15px; background: #f0f0f0; box-sizing: border-box;
    overflow-y: auto;
  }
  .control-group { margin-bottom: 20px;}
  .control-group label { display: block; margin-bottom: 5px; font-weight: bold; }
  .control-group input[type=range] {
    width: 100%; background: #ddd; height: 4px; border-radius: 2px; outline: none;
  }
  .control-group input[type=range]::-webkit-slider-thumb {
    -webkit-appearance: none; appearance: none; width: 12px; height: 12px;
    border-radius: 50%; background: #4CAF50; cursor: pointer;
  }
  .control-group input[type=range]::-moz-range-thumb {
    width: 12px; height: 12px; border-radius: 50%; background: #4CAF50; cursor: pointer;
  }
  #visualization-panel { flex: 1; padding: 10px; box-sizing: border-box; }
  #telescopic-view-wrapper, #orbit-view-wrapper {
    margin-bottom: 20px; text-align: center; border: 1px solid #ccc;
  }
  canvas { width: 100%; height: 300px; display: block; }
  p { margin: 5px 0; font-weight: bold; }
</style>
</head>

<body>
<div id="app-container">
  <!-- Control Panel -->
  <div id="controls-panel">
    <div class="control-group">
      <label for="slider-earth-position">Earth position</label>
      <input type="range" id="slider-earth-position" min="0" max="360" step="1" value="180">
    </div>
    <div class="control-group">
      <label for="slider-comet-position">Comet position</label>
      <input type="range" id="slider-comet-position" min="0" max="360" step="1" value="180">
    </div>
    <div class="control-group">
      <label for="slider-coma">Coma</label>
      <input type="range" id="slider-coma" min="0" max="100" step="1" value="20">
    </div>
    <div class="control-group">
      <label for="slider-tail">Tail</label>
      <input type="range" id="slider-tail" min="0" max="100" step="1" value="50">
    </div>
    <div class="control-group">
      <label for="slider-tail-spread">Tail spread</label>
      <input type="range" id="slider-tail-spread" min="0" max="100" step="1" value="20">
    </div>
  </div>

  <!-- Visualization Panel -->
  <div id="visualization-panel">
    <div id="telescopic-view-wrapper">
      <canvas id="telescopic-canvas"></canvas>
      <p>telescopic view</p>
    </div>
    <div id="orbit-view-wrapper">
      <canvas id="orbit-canvas"></canvas>
      <p>orbit diagram</p>
    </div>
  </div>
</div>

<script>
/* ======== Globals ======== */
let orbitScene, orbitCamera, orbitRenderer,
    telescopicScene, telescopicCamera, telescopicRenderer,
    earthMesh, cometOrbitGroup, cometTelescopicGroup,
    cometNucleus, cometComa, cometTail;

// Orbital parameters
const orbitParams = {
  earth: { a: 4, e: 0.0167, color: 0x0000ff },
  mars:  { a: 6.1, e: 0.0934, color: 0xff0000 },
  jupiter:{ a: 20.8, e: 0.0489, color: 0xffa500 },
  comet:  { a: 14.4, e: 0.432,  color: 0xffff00 }
};

/* ======== Helper Functions ======== */
function ellipsePoint(a, e, theta) {
  const b = a * Math.sqrt(1 - e * e);
  return new THREE.Vector3(a * Math.cos(theta), 0, b * Math.sin(theta));
}

function createOrbitLine(a, e, segments = 128, color = 0xffffff) {
  const points = [];
  for (let i = 0; i <= segments; i++) {
    const t = (i / segments) * 2 * Math.PI;
    points.push(ellipsePoint(a, e, t));
  }
  const geometry = new THREE.BufferGeometry().setFromPoints(points);
  const material = new THREE.LineBasicMaterial({ color });
  return new THREE.Line(geometry, material);
}

function resizeRenderer(renderer, canvas) {
  const width = canvas.clientWidth;
  const height = canvas.clientHeight;
  if (renderer.domElement.width !== width || renderer.domElement.height !== height) {
    renderer.setSize(width, height);
  }
}

/* ======== Scene Setup ======== */
function initOrbitsScene() {
  orbitScene = new THREE.Scene();
  orbitScene.background = new THREE.Color(0xffffff);
  const aspect = window.innerWidth / window.innerHeight;
  orbitCamera = new THREE.PerspectiveCamera(60, aspect, 0.1, 1000);
  orbitCamera.position.set(0, 20, 20);
  orbitCamera.lookAt(0,0,0);

  // Lighting
  orbitScene.add(new THREE.AmbientLight(0x404040));
  const pointLight = new THREE.PointLight(0xffffff, 1.5);
  pointLight.position.set(0,0,0);
  orbitScene.add(pointLight);

  // Sun
  const sunGeom = new THREE.SphereGeometry(0.5, 32, 32);
  const sunMat = new THREE.MeshBasicMaterial({ color: 0xffff00, emissive: 0xffff00 });
  const sunMesh = new THREE.Mesh(sunGeom, sunMat);
  orbitScene.add(sunMesh);

  // Orbits
  orbitScene.add(createOrbitLine(orbitParams.earth.a, orbitParams.earth.e, 128, orbitParams.earth.color));
  orbitScene.add(createOrbitLine(orbitParams.mars.a,  orbitParams.mars.e, 128, orbitParams.mars.color));
  orbitScene.add(createOrbitLine(orbitParams.jupiter.a, orbitParams.jupiter.e, 128, orbitParams.jupiter.color));
  orbitScene.add(createOrbitLine(orbitParams.comet.a, orbitParams.comet.e, 128, orbitParams.comet.color));

  // Earth
  const earthGeom = new THREE.SphereGeometry(0.3, 32, 32);
  const earthMat = new THREE.MeshLambertMaterial({ color: 0x0000ff });
  earthMesh = new THREE.Mesh(earthGeom, earthMat);
  orbitScene.add(earthMesh);

  // Comet Group
  cometOrbitGroup = new THREE.Group();
  // Nucleus
  const nucleusGeom = new THREE.SphereGeometry(0.3, 32, 32);
  const nucleusMat = new THREE.MeshBasicMaterial({ color: 0xffffff, emissive: 0xffffff });
  cometNucleus = new THREE.Mesh(nucleusGeom, nucleusMat);
  cometOrbitGroup.add(cometNucleus);
  // Coma
  const comaGeom = new THREE.SphereGeometry(3, 32, 32);
  const comaMat = new THREE.MeshBasicMaterial({ color: 0x8FFF00, transparent: true, opacity: 0.5 });
  cometComa = new THREE.Mesh(comaGeom, comaMat);
  cometOrbitGroup.add(cometComa);
  // Tail
  const tailGeom = new THREE.ConeGeometry(0.5, 1, 32);
  const tailMat = new THREE.MeshBasicMaterial({ color: 0x8FFF00, transparent: true, opacity: 0.4 });
  cometTail = new THREE.Mesh(tailGeom, tailMat);
  cometTail.rotation.x = Math.PI / 2; // Align initially along x-axis
  cometOrbitGroup.add(cometTail);

  orbitScene.add(cometOrbitGroup);

  // Renderer
  orbitRenderer = new THREE.WebGLRenderer({ canvas: document.getElementById('orbit-canvas'), antialias: true });
  orbitRenderer.setClearColor(0xffffff);
  orbitRenderer.setPixelRatio(window.devicePixelRatio);
  orbitRenderer.setSize(orbitRenderer.domElement.clientWidth, orbitRenderer.domElement.clientHeight);
  orbitRenderer.domElement.style.display = 'block';
  orbitRenderer.domElement.style.width = '100%';
  orbitRenderer.domElement.style.height = '300px';
}

function initTelescopicScene() {
  telescopicScene = new THREE.Scene();
  telescopicScene.background = new THREE.Color(0x000000);
  const aspect = window.innerWidth / window.innerHeight;
  telescopicCamera = new THREE.PerspectiveCamera(15, aspect, 0.1, 1000);
  // Position will be set in update()

  // Lighting
  telescopicScene.add(new THREE.AmbientLight(0xffffff, 1));

  // Same Comet Group for telescopic view
  cometTelescopicGroup = new THREE.Group();
  const nucleusGeom = new THREE.SphereGeometry(0.3, 32, 32);
  const nucleusMat = new THREE.MeshBasicMaterial({ color: 0xffffff, emissive: 0xffffff });
  const nucleusClone = new THREE.Mesh(nucleusGeom, nucleusMat);
  cometTelescopicGroup.add(nucleusClone);

  const comaGeom = new THREE.SphereGeometry(3, 32, 32);
  const comaMat = new THREE.MeshBasicMaterial({ color: 0x8FFF00, transparent: true, opacity: 0.5 });
  const comaClone = new THREE.Mesh(comaGeom, comaMat);
  cometTelescopicGroup.add(comaClone);

  const tailGeom = new THREE.ConeGeometry(0.5, 1, 32);
  const tailMat = new THREE.MeshBasicMaterial({ color: 0x8FFF00, transparent: true, opacity: 0.4 });
  const tailClone = new THREE.Mesh(tailGeom, tailMat);
  tailClone.rotation.x = Math.PI / 2;
  cometTelescopicGroup.add(tailClone);

  telescopicScene.add(cometTelescopicGroup);

  // Renderer
  telescopicRenderer = new THREE.WebGLRenderer({ canvas: document.getElementById('telescopic-canvas'), antialias: true, alpha: true });
  telescopicRenderer.setClearColor(0x000000, 1);
  telescopicRenderer.setPixelRatio(window.devicePixelRatio);
  telescopicRenderer.setSize(telescopicRenderer.domElement.clientWidth, telescopicRenderer.domElement.clientHeight);
  telescopicRenderer.domElement.style.display = 'block';
  telescopicRenderer.domElement.style.width = '100%';
  telescopicRenderer.domElement.style.height = '300px';
}

/* ======== Update Logic ======== */
function update() {
  // Earth position
  const earthAngleRad = THREE.MathUtils.degToRad(document.getElementById('slider-earth-position').value);
  const earthPos = ellipsePoint(orbitParams.earth.a, orbitParams.earth.e, earthAngleRad);
  earthMesh.position.copy(earthPos);

  // Comet position
  const cometAngleRad = THREE.MathUtils.degToRad(document.getElementById('slider-comet-position').value);
  const cometPos = ellipsePoint(orbitParams.comet.a, orbitParams.comet.e, cometAngleRad);
  cometOrbitGroup.position.copy(cometPos);
  cometTelescopicGroup.position.copy(cometPos);

  // Coma size
  const comaScale = 0.1 + (document.getElementById('slider-coma').value / 50); // 0.1 - 2.1
  cometComa.scale.setScalar(comaScale);
  const comaScaleTel = cometComa.clone().scale.setScalar(comaScale);
  cometTelescopicGroup.children[1].scale.setScalar(comaScale);

  // Tail
  const tailLength = 0.01 + (document.getElementById('slider-tail').value / 10); // 0.01 - 10.01
  const tailSpread = 1 + (document.getElementById('slider-tail-spread').value / 20); // 1 - 6
  cometTail.scale.set(tailSpread, tailLength, tailSpread);
  cometTelescopicGroup.children[2].scale.set(tailSpread, tailLength, tailSpread);

  // Tail orientation: pointing away from Sun (origin)
  const dirFromSun = new THREE.Vector3().subVectors(cometPos, new THREE.Vector3(0,0,0)).normalize();
  const dirTail = new THREE.Vector3(-dirFromSun.x, -dirFromSun.y, -dirFromSun.z).normalize();
  const up = new THREE.Vector3(0,1,0);
  const quat = new THREE.Quaternion().setFromUnitVectors(up, dirTail);
  cometTail.setRotationFromQuaternion(quat);
  cometTelescopicGroup.children[2].setRotationFromQuaternion(quat);

  // Telescopic camera: position at Earth, look at comet
  telescopicCamera.position.copy(earthPos);
  telescopicCamera.lookAt(cometPos);
}

/* ======== Animation Loop ======== */
function animate() {
  requestAnimationFrame(animate);
  if (orbitRenderer && orbitCamera) {
    resizeRenderer(orbitRenderer, orbitRenderer.domElement);
    orbitRenderer.render(orbitScene, orbitCamera);
  }
  if (telescopicRenderer && telescopicCamera) {
    resizeRenderer(telescopicRenderer, telescopicRenderer.domElement);
    telescopicRenderer.render(telescopicScene, telescopicCamera);
  }
}

/* ======== Initialize ======== */
window.addEventListener('DOMContentLoaded', () => {
  initOrbitsScene();
  initTelescopicScene();

  // Attach listeners
  document.querySelectorAll('#controls-panel input[type=range]').forEach(slider => {
    slider.addEventListener('input', update);
  });

  update(); // initial positions
  animate();
});

window.addEventListener('resize', () => {
  if (orbitCamera) {
    orbitCamera.aspect = orbitRenderer.domElement.clientWidth / orbitRenderer.domElement.clientHeight;
    orbitCamera.updateProjectionMatrix();
  }
  if (telescopicCamera) {
    telescopicCamera.aspect = telescopicRenderer.domElement.clientWidth / telescopicRenderer.domElement.clientHeight;
    telescopicCamera.updateProjectionMatrix();
  }
});
</script>
</body>
</html>
