      <input
        type="range"
        id="slider-y"
        min="0.1"
        max="5"
        step="0.001"
        value="1.498"
      >
      <span id="label-y">1.498</span>
    </div>
  </  <!-- Visualization Area -->
  <div id="visualization"></div>
</div>

<script>
  // Global references to DOM elements
  let mSlider, xSlider, ySlider;
  let mLabel, xLabel, yLabel;

  const L = 10;          // Distance between posts
  const g = 9.81;        // Acceleration due to gravity

  // Coordinates of the two fixed posts (anchor points)
  const anchor1 = { x: 100, y: 150 };
  const anchor2 = { x: 700, y: 150 };

  // Scaling factors to convert logical units to pixel units
  const scaleX = (anchor2.x - anchor1.x) / L;   // 60 px per logical unit
  const scaleY = 50;                            // 50 px per logical unit

  function setup() {
    // Create the p5 canvas and attach it to the visualization container
    const canvas = createCanvas(800, 500);
    canvas.parent('visualization');

    // Grab slider and display elements
    mSlider = select('#slider-m');
    xSlider = select('#slider-x');
    ySlider = select('#slider-y');
    mLabel = select('#label-m');
    xLabel = select('#label-x');
    yLabel = select('#label-y');

    // Initialise display values
    mLabel.html(mSlider.value());
    xLabel.html(xSlider.value());
    yLabel.html(ySlider.value());

    // Attach input listeners to update labels and redraw the sketch
    mSlider.input(() => { mLabel.html(mSlider.value()); redraw(); });
    xSlider.input(() => { xLabel.html(xSlider.value()); redraw(); });
    ySlider.input(() => { yLabel.html(ySlider.value()); redraw(); });

    noLoop(); // Only redraw when sliders change
  }

  function draw() {
    background('#FFFFFF');

    const m = parseFloat(mSlider.value());
    const x = parseFloat(xSlider.value());
    const y = parseFloat(ySlider.value());

    // Geometry calculations
    const theta1 = atan2(y, x);
    const theta2 = atan2(y, L - x);
    const Fg = m * g;
    const sumAngles = theta1 + theta2;
    const sinSum = sin(sumAngles);

    const T1 = (Fg *(theta2)) / sinSum;
    const T2 = (Fg * cos(theta1)) / sinSum;

    // Position of the mass
    const massX = anchor1.x + x * scaleX;
    const massY = anchor1.y + y * scaleY;

    // Draw posts
    stroke(0);
    strokeWeight(6);
    line(anchor1.x, 0, anchor1.x, height);
    line(anchor2.x, 0, anchor2.x, height);

    // Horizontal dashed reference line
    stroke(150);
    strokeWeight(1);
    const dashSpacing = 10;
    const dashGap = 5;
    let dashX = anchor1.x;
    while (dashX < anchor2.x) {
      line(dashX, anchor1.y, dashX + dashSpacing, anchor1.y);
      dashX += dashSpacing + dashGap;
    }

    // Rope (two segments)
    strokeWeight(2);
    line(anchor1.x, anchor1.y, massX, massY);
    line(anchor2.x, anchor2.y, massX, massY);

    // Mass
    fill(0);
    noStroke();
    ellipse(massX, massY, 40, 40);

    // Angle arcs
    noFill();
    stroke(0);
    const arcRadius = 40;
    arc(anchor1.x, anchor1.y, arcRadius, arcRadius, 0, theta1);
    arc(anchor2.x, anchor2.y, arcRadius, arcRadius, PI, PI - theta2);

    // Display tensions
    noStroke();
    fill(0);
    textSize(16);
    textAlign(CENTER, TOP);
    text(`T₁ = ${T1.toFixed(4)} N | T₂ = ${T2.toFixed(4)} N`, width / 2, 10);
  }
</script>
</body>
</html>
