<script>
/* ----------------- Global State ------------------- */
let time = 0;
let isRunning = false;
let animationFrameReq;
let prevTimestamp = null;

// simulation parameters
let params = {
  x0: 0.1,
  v0: 1,
  m: 1,
  c: 0.1,
  k: 1,
  Kp: 0.1,
  Kd: 0.1
};

// sampled solution (for plotting and animation)
const SAMPLE_DT = 0.01;
const MAX_TIME = 10;
const steps = Math.floor(MAX_TIME / SAMPLE_DT) + 1;
let tArray = new Array(steps).fill(0).map((_,i)=>i*SAMPLE_DT);
let xArray = [];
let vArray = [];

// Three.js objects
let scene, camera, renderer, controls;
let massMesh, springMesh, damperGroup, baseMesh, enclosureMesh;

/* ----------------- Utility Functions ------------------- */
function clamp(val,min,max){return Math.min(Math.max(val,min),max);}
function fmt(val,dec){return val.toFixed(dec);}
function getSliderValue(id){return parseFloat(document.getElementById(id).value);}
function updateValueDisplay(id, spanId){ document.getElementById(spanId).textContent = fmt(getSliderValue(id),1); }

/* ----------------- Differential Equation Solver ------------------- */
function computeSolution() {
  const {m,c,k,Kp,Kd,x0,v0} = params;
  const k_eff = k + Kp;
  const c_eff = c + Kd;
  const omega0 = Math.sqrt(k_eff/m);
  const zeta = c_eff / (2*Math.sqrt(m*k_eff));
  xArray = new Array(steps).fill(0);
  vArray = new Array(steps).fill(0);
  if (zeta < 1) { // underdamped
    const omegaD = omega0*Math.sqrt(1-zeta*zeta);
    const C1 = x0;
    const C2 = (v0 + zeta*omega0*x0)/omegaD;
    for(let i=0;i<steps;i++){
      const t=tArray[i];
      const expTerm = Math.exp(-zeta*omega0*t);
      const cosTerm = Math.cos(omegaD*t);
      const sinTerm = Math.sin(omegaD*t);
      const x = expTerm*(C1*cosTerm + C2*sinTerm);
      const v = expTerm*(
              -zeta*omega0*(C1*cosTerm + C2*sinTerm)
              -omegaD*(C1*sinTerm - C2*cosTerm)); // derivative
      xArray[i]=x; vArray[i]=v;
    }
  } else if (Math.abs(zeta-1) < 1e-6) { // critically damped
    const r = -omega0*zeta;
    const C1 = x0;
    const C2 = v0 - r*x0;
    for(let i=0;i<steps;i++){
      const t=tArray[i];
      const expTerm = Math.exp(r*t);
      const x = (C1 + C2*t)*expTerm;
      const v = expTerm*(C2 + r*(C1 + C2*t));
      xArray[i]=x; vArray[i]=v;
    }
  } else { // overdamped
    const sqrtVal = Math.sqrt(zeta*zeta-1);
    const r1 = -omega0*(zeta + sqrtVal);
    const r2 = -omega0*(zeta - sqrtVal);
    const denom = r2 - r1;
    const C1 = (v0 - r2*x0)/denom;
    const C2 = (r1*x0 - v0)/denom;
    for(let i=0;i<steps;i++){
      const t=tArray[i];
      const x = C1*Math.exp(r1*t) + C2*Math.exp(r2*t);
      const v = C1*r1*Math.exp(r1*t) + C2*r2*Math.exp(r2*t);
      xArray[i]=x; vArray[i]=v;
    }
  }
}

/* ----------------- Plotting Functions ------------------- */
function initPlots() {
  const displacementData = [
    {x:tArray,y:xArray,mode:'lines',line:{color:'orange'},showlegend:false},
    {x:[0],y:[params.x0],mode:'markers',marker:{size:8,color:'blue'},showlegend:false}
  ];
  const velocityData = [
    {x:tArray,y:vArray,mode:'lines',line:{color:'orange'},showlegend:false},
    {x:[0],y:[params.v0],mode:'markers',marker:{size:8,color:'green'},showlegend:false}
