  Plotly.newPlot('plot-displacement',displacementData,layout);
  Plotly.newPlot('plot-velocity',velocityData,velocityLayout);
}

function updatePlots() {
  const idx = Math.round(time/SAMPLE_DT);
  const xCur = xArray[Math.max(0, Math.min(idx, steps-1))];
  const vCur = vArray[Math.max(0, Math.min(idx, steps-1))];
  Plotly.restyle('plot-displacement',['x','y'],[[time],[xCur]],[1]);
  Plotly.restyle('plot-velocity',['x','y'],[[time],[vCur]],[1]);
}

/* ----------------- Three.js Setup ------------------- */
function initScene() {
  const container = document.getElementById('scene-container');
  const width = container.clientWidth;
  const height = container.clientHeight;
  scene = new THREE.Scene();
  camera = new THREE.PerspectiveCamera(45, width/height, 0.1, 1000);
  camera.position.set(0,4,10);
  camera.lookAt(0,0,0);
  renderer = new THREE.WebGLRenderer({antialias:true});
  renderer.setSize(width, height);
  container.appendChild(renderer.domElement);

  controls = new THREE.OrbitControls(camera, renderer.domElement);
  controls.enableDamping=true;
  controls.dampingFactor=0.1;

  // Lights
  scene.add(new THREE.AmbientLight(0x404040));
  const dirLight = new THREE.DirectionalLight(0xffffff,0.8);
  dirLight.position.set(5,10,7.5);
  scene.add(dirLight);

  // Base
  const baseGeom = new THREE.BoxGeometry(4,0.1,4);
  const baseMat = new THREE.MeshStandardMaterial({color:0x008000});
  baseMesh = new THREE.Mesh(baseGeom,baseMat);
  baseMesh.position.y=-0.05;
  scene.add(baseMesh);

  // Mass
  const massGeom = new THREE.BoxGeometry(0.5,0.5,0.5);
  const massMat = new THREE.MeshStandardMaterial({color:0xffff00});
  massMesh = new THREE.Mesh(massGeom,massMat);
  massMesh.position.set(params.x0,0.25,0);
  scene.add(massMesh);

  // Spring (simple cylinder)
  const springGeom = new THREE.CylinderGeometry(0.1,0.1,2,32);
  const springMat = new THREE.MeshStandardMaterial({color:0xff0000});
  springMesh = new THREE.Mesh(springGeom, springMat);
  springMesh.position.set((params.x0)/2,0.4,0);
  springMesh.rotation.x = Math.PI/2;
  springMesh.scale.y = 1;
  scene.add(springMesh);

  // Damper
  damperGroup = new THREE.Group();
  const housingGeom = new THREE.CylinderGeometry(0.2,0.2,0.1,32);
  const housingMat = new THREE.MeshStandardMaterial({color:0x0000ff});
  const housing = new THREE.Mesh(housingGeom, housingMat);
  housing.position.set(0,-0.03,0);
  damperGroup.add(housing);

  const pistonGeom = new THREE.CylinderGeometry(0.1,0.1,0.3,32);
  const pistonMat = new THREE.MeshStandardMaterial({color:0x0000ff});
  const piston = new THREE.Mesh(pistonGeom, pistonMat);
  piston.position.set(0,0.1,0);
  damperGroup.add(piston);

  damperGroup.position.set(params.x0,0.25,0);
  scene.add(damperGroup);

  // Enclosure
  const enclosureGeom = new THREE.BoxGeometry(3,3,3);
  const enclosureMat = new THREE.MeshBasicMaterial({color:0x000000, wireframe:true, transparent:true, opacity:0.2});
  enclosureMesh = new THREE.Mesh(enclosureGeom,enclosureMat);
  enclosureMesh.position.set(params.x0/2,0.5,0);
  scene.add(enclosureMesh);

  window.addEventListener('resize', onWindowResize, false);
}

function onWindowResize(){
  const container = document.getElementById('scene-container');
  const width = container.clientWidth;
  const height = container.clientHeight;
  camera.aspect = width / height;
  camera.updateProjectionMatrix();
  renderer.setSize(width, height);
}

/* ----------------- Update 3D Scene ------------------- */
function updateScene() {
  const idx = Math.round(time/SAMPLE_DT);
  const xCur = xArray[Math.max(0, Math.min(idx, steps-1))];
  const vCur = vArray[Math.max(0, Math.min(idx, steps-1))];
  massMesh.position.x = xCur;
  damperGroup.position.x = xCur;
  enclosureMesh.position.x = xCur/2;

  // Spring scale (compression/extension)
  const baseLength = 2;       // original spring length
  let scaleY = 1 + (xCur)/(baseLength); // simple linear mapping
  if (scaleY < 0.2) scaleY = 0.2;
  springMesh.scale.y = Math.abs(scaleY);
  springMesh.position.x = xCur/2;
  springMesh.position.y = 0.4 + (scaleY-1)*0.5; // adjust vertical center

  // Light updates optional
}

/* ----------------- Animation Loop ------------------- */
function animate(timestamp){
  if (!prevTimestamp) prevTimestamp = timestamp;
  const dt = timestamp - prevTimestamp;
  prevTimestamp = timestamp;

  if (isRunning){
    time += dt/1000;
    if (time > MAX_TIME){
      time = MAX_TIME;
      pauseAnimation();
    }
    updateScene();
    updatePlots();
  }
  renderer.render(scene, camera);
  controls.update();
  animationFrameReq = requestAnimationFrame(animate);
}

/* ----------------- Control Handlers ------------------- */
function pauseAnimation(){
  isRunning = false;
}
function playAnimation(){
  if (!isRunning){
    isRunning = true;
    pauseAnimation(); // ensures correct integration
  }
}
function resetAnimation(){
  time = 0;
  pauseAnimation();
  updateScene();
  updatePlots();
}

/* ----------------- Slider Setup ------------------- */
function bindSlider(id, spanId){
  const slider = document.getElementById(id);
  updateValueDisplay(id, spanId);
  slider.addEventListener('input', ()=>{
    const val = parseFloat(slider.value);
    document.getElementById(spanId).textContent = fmt(val,1);
    // update params
    switch(id){
      case 'slider-displacement': params.x0 = val; break;
      case 'slider-velocity':     params.v0 = val; break;
      case 'slider-stiffness':    params.k = val; break;
      case 'slider-mass':         params.m = val; break;
      case 'slider-damping':      params.c = val; break;
      case 'slider-proportional-gain': params.Kp = val; break;
      case 'slider-derivative-gain':   params.Kd = val; break;
    }
    pauseAnimation();
    time = 0;
    computeSolution();
    initPlots();
    updateScene();
    updatePlots();
  });
}
function initSliders(){
  bindSlider('slider-displacement','value-displacement');
  bindSlider('slider-velocity','value-velocity');
  bindSlider('slider-stiffness','value-stiffness');
  bindSlider('slider-mass','value-mass');
  bindSlider('slider-damping','value-damping');
  bindSlider('slider-proportional-gain','value-proportional-gain');
  bindSlider('slider-derivative-gain','value-derivative-gain');
}

/* ----------------- Button Handlers ------------------- */
document.getElementById('btn-play').addEventListener('click', playAnimation);
document.getElementById('btn-pause').addEventListener('click', pauseAnimation);
document.getElementById('btn-reset').addEventListener('click', resetAnimation);

/* ----------------- Initialize ------------------- */
computeSolution();
initPlots();
initScene();
initSliders();
animate();

</script>
</body>
</html>
