<script>
/*=== Global State ===*/
let T = 50, m = [1,1,1], x0 = [0.1,0.1,0];
let omega = [], A = [], c = [];
let dispData = [[],[],[]], velData = [[],[],[]], ts = [];
let simulationTime = 0, dt = 0.02; // integration step for plots
let isAnimating = false;
let plotShapes = { disp: null, vel: null };
let displacementPlot, velocityPlot;

//=== Utility Functions ===
function updateSliderText(id, val) {
  document.getElementById(`val-${id}`).textContent = val.toFixed(2);
}

function getSliderVals() {
  T = parseFloat(document.getElementById('slider-tension').value);
  m = [
    parseFloat(document.getElementById('slider-m1').value),
    parseFloat(document.getElementById('slider-m2').value),
    parseFloat(document.getElementById('slider-m3').value)
  ];
  x0 = [
    parseFloat(document.getElementById('slider-x1').value),
    parseFloat(document.getElementById('slider-x2').value),
    parseFloat(document.getElementById('slider-x3').value)
  ];
}

//=== Physics Setup ===
function setupPhysics() {
  // Mass and Stiffness matrices
  const M = numeric.diag(m);
  const K = numeric.mul(T, [
    [2, -1, 0],
    [-1, 2, -1],
    [0, -1, 2]
  ]);

  // Pre- and post- multiply to convert to standard eigenproblem
  const sqrtM = numeric.diag(m.map(Math.sqrt));
  const invRootM = numeric.diag(m.map(x => 1 / Math.sqrt(x)));
  const C = numeric.dot(numeric.dot(invRootM, K), invRootM);
  const eig = numeric.eig(C);
  const eigVals = eig.lambda.x;
  const eigVecs = eig.E.x; // columns are eigenvectors of C

  // Transform back to original coordinates
  omega = eigVals.map(v => Math.sqrt(v));
  // Eigenvectors (columns)
  A = [];
  for (let i = 0; i < 3; i++) {
    // w_i is eigVecs column i
    const w = eigVecs.map(row => row[i]);
    // A_i = sqrtM * w
    const Ai = numeric.dot(sqrtM, w);
    A.push(Ai);
  }

  // Participation factors c_i
  c = [];
  for (let i = 0; i < 3; i++) {
    const Ai = A[i];
    const denom = numeric.dot(Ai, numeric.dot(M, Ai)); // Ai^T M Ai
    const numer = numeric.dot(Ai, numeric.dot(M, x0));  // Ai^T M x0
    c.push(numer / denom);
  }
}

//=== Precompute Data for Plots ===
function precomputePlotData() {
  const maxTime = 5.0;
  ts = [];
  dispData = [[], [], []];
  velData = [[], [], []];
  for (let t = 0; t <= maxTime; t += dt) {
    ts.push(t);
    const y = [];
    const v = [];
    for (let i = 0; i < 3; i++) {
      const Ai = A[i];
      const wi = omega[i];
      const ci = c[i];
      const cosTerm = Math.cos(wi * t);
      const sinTerm = Math.sin(wi * t);
      // displacement component for mass i
      y[i] = y[i] || 0;
      v[i] = v[i] || 0;
      for (let j = 0; j < 3; j++) {
        y[j] += ci * Ai[j] * cosTerm;
        v[j] += -ci * Ai[j] * wi * sinTerm;
      }
    }
    for (let j = 0; j < 3; j++) {
      dispData[j].push(y[j]);
      velData[j].push(v[j]);
    }
  }
}

//=== Plot Initialization and Update ===
function initPlots() {
  precomputePlotData();
  const colors = ['#1f77b4', '#ff7f0e', '#2ca02c']; // blue, orange, green
  const dispTraces = [];
  const velTraces = [];
  for (let i = 0; i < 3; i++) {
    dispTraces.push({
      x: ts,
      y: dispData[i],
      mode: 'lines',
      name: `m${i+1}`,
      line:{color: colors[i]}
    });
    velTraces.push({
      x: ts,
      y: velData[i],
      mode: 'lines',
      name: `m${i+1}`,
      line:{color: colors[i]}
    });
  }
  const dispLayout = {
    title: 'displacement',
    xaxis:{title:'time t', range:[0,5]},
    yaxis:{title:'displacement'},
    shapes: [{ type: 'line', x0:0, x1:0, y0:-1, y1:1, line:{color:'red', width:2} }]
  };
  const velLayout = {
    title: 'velocity',
    xaxis:{title:'time t', range:[0,5]},
    yaxis:{title:'velocity'},
    shapes: [{ type: 'line', x0:0, x1:0, y0:-1, y1:1, line:{color:'red', width:2} }]
  };
  displacementPlot = document.getElementById('displacement-plot-container');
  velocityPlot = document.getElementById('velocity-plot-container');
  Plotly.newPlot(displacementPlot, dispTraces, dispLayout);
  Plotly.newPlot(velocityPlot, velTraces, velLayout);
  plotShapes.disp = displacementPlot.layout.shapes[0];
  plotShapes.vel  = velocityPlot.layout.shapes[0];
}

function updatePlotLines(currentTime) {
  // Update vertical line for both plots
  Plotly.relayout(displacementPlot, {shapes:[{type:'line', x0:currentTime, x1:currentTime, y0:-1, y1:1,
                                        line:{color:'red', width:2}}]});
  Plotly.relayout(velocityPlot, {shapes:[{type:'line', x0:currentTime, x1:currentTime, y0:-1, y1:1,
                                        line:{color:'red', width:2}}]});
}

//=== Reset Logic ===
function resetSimulation() {
  // Pause animation if running
  isAnimating = false;
  document.getElementById('btn-play-pause').textContent = '▶';
  noLoop(); // p5 method
  simulationTime = 0;
  getSliderVals();
  setupPhysics();
  initPlots();
}

//=== Animation Controls ===
document.getElementById('btn-reset').addEventListener('click', resetSimulation);
document.getElementById('btn-play-pause').addEventListener('click', () => {
  isAnimating = !isAnimating;
  document.getElementById('btn-play-pause').textContent = isAnimating ? '❚❚' : '▶';
  if (isAnimating) loop();
  else noLoop();
});

// Add listeners to all sliders to update values and reset
['slider-tension','slider-x1','slider-x2','slider-x3','slider-m1','slider-m2','slider-m3'].forEach(id=>{
  const el = document.getElementById(id);
  el.addEventListener('input', () => {
    updateSliderText(id.split('-')[1], parseFloat(el.value));
    resetSimulation();
  });
});

//=== P5.js Sketch ===
const sketch = (p) => {
  let canvas;
  p.setup = () => {
    canvas = p.createCanvas(500, 250);
    canvas.parent('main-animation-container');
    p.frameRate(30);
  };
  p.draw = () => {
    simulationTime += dt;
    if (simulationTime > 5) simulationTime = 5; // stop at 5s
    // Compute current displacement
    const y = [0,0,0];
    for (let i = 0; i < 3; i++) {
      const Ai = A[i];
      const wi = omega[i];
      const ci = c[i];
      const cosTerm = Math.cos(wi * simulationTime);
      for (let j = 0; j < 3; j++) {
        y[j] += ci * Ai[j] * cosTerm;
      }
    }
    // Map coords to canvas
    const mapX = (x)=> 50 + (x/4)*400; // 0->50, 4->450
    const mapY = (y)=> 125 - y*110; // y in [-1,1], center 125
    p.background(255);
    // grid
    p.stroke(200);
    for (let i=0;i<=4;i++){
      const x= mapX(i);
      p.line(x,0,x,250);
    }
    for (let i=-1;i<=1;i+=0.5){
      const y=mapY(i);
      p.line(0,y,500,y);
    }
    // string
    p.stroke(0);
    p.strokeWeight(2);
    p.noFill();
    p.beginShape();
    p.vertex(mapX(0), mapY(0));
    p.vertex(mapX(1), mapY(y[0]));
    p.vertex(mapX(2), mapY(y[1]));
    p.vertex(mapX(3), mapY(y[2]));
    p.vertex(mapX(4), mapY(0));
    p.endShape();
    // masses
    const rs = m.map(mi=> 10 * Math.sqrt(mi)); // radius scaling
    const colors = ['red','red','red'];
    for (let i=0;i<3;i++){
      p.fill(colors[i]);
      p.noStroke();
      p.ellipse(mapX(i+1), mapY(y[i]), rs[i]*2, rs[i]*2);
    }
    // update plot line
    updatePlotLines(simulationTime);
  };
  p.windowResized = () => {
    p.resizeCanvas(500, 250);
  };
};
new p5(sketch);

//=== Initial Setup ===
document.addEventListener('DOMContentLoaded', () => {
  // Set initial slider texts
  updateSliderText('tension', T);
  updateSliderText('x1', x0[0]);
  updateSliderText('x2', x0[1]);
  updateSliderText('x3', x0[2]);
  updateSliderText('m1', m[0]);
  updateSliderText('m2', m[1]);
  updateSliderText('m3', m[2]);
  // Perform initial reset to initialize everything
  resetSimulation();
});

</script>
</body>
