<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Jarvis March Visualization</title>
<style>
  body, html {margin:0; padding:0; font-family:Arial, sans-serif; background:#fafafa;}
  #app-container {display:flex; flex-direction:column; align-items:center; padding:20px;}
  #header-controls, #step-controls {margin:10px;}
  button {background:#f0f0f0; border:1px solid #ccc; padding:5px 10px; margin:5px; cursor:pointer;}
  button:disabled {opacity:0.5; cursor:not-allowed;}
  #explanation-panel {width:700px; min-height:50px; margin-top:10px; padding:10px; background:#fff; border:1px solid #ddd;}
  #step-controls > div {display:inline-block; margin-right:20px;}
  #step3-actions button {margin-left:5px;}
</style>
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
<div id="app-container">
  <div id="header-controls">
    <button id="btn-move">move the points</button>
    <button id="btn-start-algo">start the algorithm</button>
  </div>
  <div id="step-controls" style="display:none;">
    <div id="step1-controls">
      <button disabled>step 1</button>
      <button id="btn-step1-explain">explain</button>
      <button id="btn-step1-do" disabled>do it</button>
    </div>
    <div id="step2-controls">
      <button disabled>step 2</button>
      <button id="btn-step2-explain">explain</button>
      <button id="btn-step2-do" disabled>do it</button>
    </div>
    <div id="step3-controls-container">
      <button disabled>step 3</button>
      <button id="btn-step3-explain">explain</button>
      <div id="step3-actions" style="display:inline-block;"></div>
    </div>
  </div>
  <div id="canvas-container"></div>
  <div id="explanation-panel">
    <p id="explanation-text"></p>
  </div>
</div>

<script>
/* Global state */
let mode = 'move';
let points = [];
let draggedPoint = null;

/* Algorithm state */
let algorithmState = {
  step: 0,
  hullPointsIndices: [],
  fullHull: [],
  leftmostPointIndex: null,
  currentPointIndex: null,
  candidateLines: [],
  explanationSteps: []
};

/* Utility functions */
function resetAlgorithmState() {
  algorithmState.step = 0;
  algorithmState.hullPointsIndices = [];
  algorithmState.fullHull = [];
  algorithmState.leftmostPointIndex = null;
  algorithmState.currentPointIndex = null;
  algorithmState.candidateLines = [];
  algorithmState.explanationSteps = [];
}

function updateExplanationPanel() {
  const el = document.getElementById('explanation-text');
  el.innerHTML = algorithmState.explanationSteps.map(s=>s.replace(/\\n/g, '<br>')).join('<br>');
}

/* Convert initial point coordinates to p5.Vector objects */
function initPoints() {
  const data = [
    {x:150, y:350}, {x:250, y:150}, {x:350, y:250},
    {x:550, y:180}, {x:480, y:320}, {x:420, y:400}
  ];
  points = data.map(p=>createVector(p.x, p.y));
}

/* Precompute full hull using Jarvis March */
function computeFullHull() {
  const h = [];
  let start = 0;
  for (let i=1;i<points.length;i++){
    if (points[i].x < points[start].x) start = i;
  }
  h.push(start);
  let curr = start;
  while (true) {
    let next = (curr+1)%points.length;
    for (let i=0;i<points.length;i++){
      if (i===curr) continue;
      const orient = (points[next].x-points[curr].x)*(points[i].y-points[curr].y) -
                     (points[next].y-points[curr].y)*(points[i].x-points[curr].x);
      if (orient < 0) next = i; // right turn (clockwise)
    }
    if (next===start) break;
    h.push(next);
    curr = next;
  }
  return h;
}

/* P5.js setup and draw */
let myp5;
myp5 = new p5(function(s) {
  s.setup = function() {
    initPoints();
    const c = s.createCanvas(700, 500);
    c.parent('canvas-container');
  };
  s.draw = function() {
    s.background(255);
    // Draw white border inside canvas
    s.stroke(220);
    s.noFill();
    s.rect(0, 0, s.width, s.height);
    // Draw candidate lines
    s.strokeWeight(2);
    s.stroke(60, 150, 140); // teal
    algorithmState.candidateLines.forEach(pair=>{
      const [f,t] = pair;
      s.line(points[f].x, points[f].y, points[t].x, points[t].y);
    });
    // Draw hull edges if more than 1 hull point
    if (algorithmState.hullPointsIndices.length > 1) {
      s.strokeWeight(3);
      s.stroke(45, 60, 110); // dark blue
      const h = algorithmState.hullPointsIndices;
      for (let i=0;i<h.length-1;i++){
        const a=h[i], b=h[i+1];
        s.line(points[a].x, points[a].y, points[b].x, points[b].y);
      }
      // Final edge back to leftmost if hull complete
      if (algorithmState.fullHull.length &&
          algorithmState.hullPointsIndices.length === algorithmState.fullHull.length) {
        const a=algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length-1];
        const b=algorithmState.hullPointsIndices[0];
        s.line(points[a].x, points[a].y, points[b].x, points[b].y);
      }
    }
    // Draw points
    algorithmState.hullPointsIndices.forEach(idx=>{
      s.push();
      s.fill(45, 60, 110); // dark blue hull points
      s.stroke(0);
      s.ellipse(points[idx].x, points[idx].y, 12, 12);
      // crosshair
      s.strokeWeight(1);
      s.line(points[idx].x-3, points[idx].y, points[idx].x+3, points[idx].y);
      s.line(points[idx].x, points[idx].y-3, points[idx].x, points[idx].y+3);
      s.pop();
    });
    // Default points
    for (let i=0;i<points.length;i++){
      if (!algorithmState.hullPointsIndices.includes(i)) {
        s.push();
        s.fill(200, 220, 100); // light green
        s.stroke(0);
        s.ellipse(points[i].x, points[i].y, 12, 12);
        // crosshair
        s.strokeWeight(1);
        s.line(points[i].x-3, points[i].y, points[i].x+3, points[i].y);
        s.line(points[i].x, points[i].y-3, points[i].x, points[i].y+3);
        s.pop();
      }
    }
  };
  s.mousePressed = function() {
    if (mode !== 'move') return;
    const mx = s.mouseX;
    const my = s.mouseY;
    for (let i=0;i<points.length;i++){
      const d = p5.Vector.dist(createVector(mx,my), points[i]);
      if (d <= 6){
        draggedPoint = i;
        break;
      }
    }
  };
  s.mouseDragged = function() {
    if (mode !== 'move' || draggedPoint === null) return;
    points[draggedPoint].set(s.mouseX, s.mouseY);
  };
  s.mouseReleased = function() {
    draggedPoint = null;
  };
});

/* UI event handlers */
document.getElementById('btn-move').addEventListener('click', () => {
  mode = 'move';
  resetAlgorithmState();
  document.getElementById('step-controls').style.display = 'none';
  document.getElementById('explanation-text').innerText = '';
});

document.getElementById('btn-start-algo').addEventListener('click', () => {
  mode = 'algorithm';
  resetAlgorithmState();
  // Recompute full hull quietly
  algorithmState.fullHull = computeFullHull();
  // Show step controls
  document.getElementById('step-controls').style.display = 'inline-block';
  // Enable step 1 buttons
  document.getElementById('btn-step1-do').disabled = false;
  document.getElementById('btn-step1-explain').disabled = false;
  // Disable other step buttons
  document.getElementById('btn-step2-do').disabled = true;
  document.getElementById('btn-step2-explain').disabled = true;
  // Populate step 3 actions dynamically
  const step3Act = document.getElementById('step3-actions');
  step3Act.innerHTML = '';
  // Determine the sequence of hull points to be added after step2
  const remaining = algorithmState.fullHull.slice(2); // exclude first two
  // do it button
  if (remaining.length > 0) {
    const btnDo = document.createElement('button');
    btnDo.id = 'btn-step3-do';
    btnDo.textContent = 'do it';
    btnDo.disabled = true;
    step3Act.appendChild(btnDo);
    btnDo.addEventListener('click', () => performStep3(remaining[0], btnDo, remaining.slice(1)));
  }
});

document.getElementById('btn-step1-explain').addEventListener('click', () => {
  algorithmState.explanationSteps.push('Step 1. Find the leftmost point.');
  updateExplanationPanel();
});

document.getElementById('btn-step1-do').addEventListener('click', () => {
  // Find leftmost
  let minX = points[0].x, idx = 0;
  for (let i=1;i<points.length;i++){
    if (points[i].x < minX){
      minX = points[i].x; idx = i;
    }
  }
  algorithmState.leftmostPointIndex = idx;
  algorithmState.currentPointIndex = idx;
  algorithmState.hullPointsIndices = [idx];
  // Update UI
  document.getElementById('btn-step1-do').disabled = true;
  document.getElementById('btn-step1-explain').disabled = true;
  document.getElementById('btn-step2-do').disabled = false;
  document.getElementById('btn-step2-explain').disabled = false;
});

document.getElementById('btn-step2-explain').addEventListener('click', () => {
  algorithmState.explanationSteps.push(
    'Step 2. Find the point clockwise furthest to the right relative to the leftmost point.'
  );
  updateExplanationPanel();
});

document.getElementById('btn-step2-do').addEventListener('click', () => {
  const secondIdx = algorithmState.fullHull[1];
  // Candidate lines for step 2
  algorithmState.candidateLines = [];
  for (let i=0;i<points.length;i++){
    if (!algorithmState.hullPointsIndices.includes(i) || i===algorithmState.currentPointIndex){
      algorithmState.candidateLines.push([algorithmState.currentPointIndex,i]);
    }
  }
  // Update hull
  algorithmState.currentPointIndex = secondIdx;
  algorithmState.hullPointsIndices.push(secondIdx);
  // Clear candidate lines after a short delay
  setTimeout(() => {
    algorithmState.candidateLines = [];
    // Enable step 3 actions
    const step3Act = document.getElementById('step3-actions');
    const btnDo = step3Act.querySelector('#btn-step3-do');
    if (btnDo) btnDo.disabled = false;
  }, 500);
  // Disable step 2 buttons
  document.getElementById('btn-step2-do').disabled = true;
  document.getElementById('btn-step2-explain').disabled = true;
});

/* Helper to perform step 3 actions dynamically */
function performStep3(nextIdx, button, remaining) {
  const fromIdx = algorithmState.currentPointIndex;
  // Draw candidate lines
  algorithmState.candidateLines = [];
  for (let i=0;i<points.length;i++){
    if (!algorithmState.hullPointsIndices.includes(i) || i===fromIdx){
      algorithmState.candidateLines.push([fromIdx,i]);
    }
  }
  // Disable button immediately
  button.disabled = true;
  // After delay, finalize hull addition
  setTimeout(() => {
    algorithmState.currentPointIndex = nextIdx;
    algorithmState.hullPointsIndices.push(nextIdx);
    algorithmState.candidateLines = [];
    // If more remaining, enable next button
    if (remaining.length > 0) {
      const nextBtn = document.createElement('button');
      nextBtn.textContent = 'repeat';
      step3Actions.appendChild(nextBtn);
      nextBtn.addEventListener('click', () => performStep3(remaining[0], nextBtn, remaining.slice(1)));
    } else {
      // Close hull by connecting back to leftmost
      // After adding final point, it will be drawn in draw(), no explicit action needed
    }
  }, 500);
}

/* Ensure step3-actions container reference */
const step3Actions = document.getElementById('step3-actions');

/* Allow dynamic enabling of repeat buttons (will be added in performStep3) */
/* Attach a quick method to add repeat button if not existing */
function addRepeatButton(idx, nextRemaining) {
  const btn = document.createElement('button');
  btn.textContent = 'repeat';
  step3Actions.appendChild(btn);
  btn.addEventListener('click', () => performStep3(algorithmState.fullHull[idx], btn, nextRemaining));
}

/* Adjust button enabling for step3 dynamically */
document.getElementById('btn-step3-explain').addEventListener('click', () => {
  algorithmState.explanationSteps.push(
    'Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.'
  );
  updateExplanationPanel();
});

</script>
</body>
</html>
