<script>
'use strict';
let simulationData = null;

// ==== ODE SOLVER (RK4) ====
function runSimulation(params) {
  const {
    alphaMax,
    tMax,
    enzymeCtrl,
    feedRate,
    feedConc,
    feedTime
  } = params;

  // Default constants
  const muMax = 0.4,      // 1/h
        K_G   = 20,       // mol/m³
        Y_X_Glu = 0.5,   // mol_X / mol_Glu
        epsilon = 0.3;   // mol_Eta / mol_X
  const k_synth0 = 0.1, k_deg0 = 0.01;
  const k_synth1 = 0.1, k_deg1 = 0.01;
  const k_synth2 = 0.1, k_deg2 = 0.01;
  const nHill = 4;

  // Helper: compute alpha
  function alphaFunc(Glu) {
    const KI = enzymeCtrl;
    const num = Math.pow(KI, nHill);
    const den = num + Math.pow(Glu, nHill);
    return alphaMax * num / den;
  }

  // Helper: derivative array
  function deriv(state, t) {
    let [X, Glu, Eta, E0, E1, E2, V] = state;
    const fed = (t <= feedTime) ? feedRate : 0;
    const G_in = (t <= feedTime) ? feedConc : 0;
    const D = fed / V;
    const alpha = alphaFunc(Glu);

    const mu = muMax * (Glu / (K_G + ???));
    const mu = muMax * (Glu / (K_G + Glu));
    const growth = mu * X;

    const dX = growth - D * X;
    const dGlu = -growth / Y_X_Glu + D * (G_in - Glu);
    const dEta = epsilon * (1 - alpha) * growth - D * Eta;
    const dE0 = k_synth0 * (1 - alpha) * X - k_deg0 * E0 - D * E0;
    const dE1 = k_synth1 * (1 - alpha) * X - k_deg1 * E1 - D * E1;
    const dE2 = k_synth2 * alpha * X - k_deg2 * E2 - D * E2;
    const dV = fed;

    return [dX, dGlu, dEta, dE0, dE1, dE2, dV];
  }

  // Initial states
  const X0 = 0.1, Glu0 = 60, Eta0 = 0, E0_0 = 1.0, E1_0 = 0, E2_0 = 0, V0 = 1.0;
  let t = 0;
  let dt = Math.max(0.001, tMax / 2000); // ~2000 steps max
  const state = [X0, Glu0, Eta0, E0_0, E1_0, E2_0, V0];

  const tArray = [], XArray = [], GluArray = [], EtaArray = [], E0Array = [], E1Array = [], E2Array = [], VArray = [], alphaArray = [];

  while (t <= tMax + 1e-9) {
    tArray.push(t);
    XArray.push(state[0]); GluArray.push(state[1]); EtaArray.push(state[2]);
    E0Array.push(state[3]); E1Array.push(state[4]); E2Array.push(state[5]); VArray.push(state[6]);
    alphaArray.push(alphaFunc(state[1]));

    // RK4
    const k1 = deriv(state, t);
    const s2 = state.map((s, i) => s + k1[i] * dt / 2);
    const k2 = deriv(s2, t + dt/2);
    const s3 = state.map((s, i) => s + k2[i] * dt / 2);
    const k3 = deriv(s3, t + dt/2);
    const s4 = state.map((s, i) => s + k3[i] * dt);
    const k4 = deriv(s4, t + dt);

    for (let i = 0; i < state.length; i++) {
      state[i] += dt / 6 * (k1[i] + 2*k2[i] + 2*k3[i] + k4[i]);
    }

    t += dt;
  }

  return { t: tArray, X: XArray, Glu: GluArray, Eta: EtaArray, E0: E0Array, E1: E1Array, E2: E2Array, V: VArray, alpha: alphaArray };
}

// ==== Plotting ====
function drawPlots() {
  if (!simulationData) return;
  const plotType = document.getElementById('select-plot').value;
  const layoutBase = {margin:{l:50,r:20,t:40,b:50},xaxis:{title:"time (h)"},yaxis:{title:"concentration [mol/m³]"}};

  let dataTop = [], dataBottom = [], layoutTop = {}, layoutBottom = {};

  if (plotType === 'fed batch') {
    // Top: X, Glu, Eta
    dataTop = [
      {x: simulationData.t, y: simulationData.X, mode:'lines', name:'X(t)', line:{color:'blue'}},
      {x: simulationData.t, y: simulationData.Glu, mode:'lines', name:'Glu(t)', line:{color:'red'}},
      {x: simulationData.t, y: simulationData.Eta, mode:'lines', name:'Eta(t)', line:{color:'green'}}
    ];
    layoutTop = {...layoutBase, title:"diauxic behavior"};

    // Bottom: E2, E1, E0
    dataBottom = [
      {x: simulationData.t, y: simulationData.E2, mode:'lines', name:'E₂(t)', line:{color:'black'}},
      {x: simulationData.t, y: simulationData.E1, mode:'lines', name:'E₁(t)', line:{color:'magenta'}},
      {x: simulationData.t, y: simulationData.E0, mode:'lines', name:'E₀(t)', line:{color:'orange'}}
    ];
    layoutBottom = {...layoutBase, title:"enzyme concentration"};
  } else { // α plot
    // Top: Glu
    dataTop = [
      {x: simulationData.t, y: simulationData.Glu, mode:'lines', name:'Glu(t)', line:{color:'red'}}
    ];
    layoutTop = {...layoutBase, title:"glucose concentration"};

    // Bottom: α
    dataBottom = [
      {x: simulationData.t, y: simulationData.alpha, mode:'lines', name:'α', line:{color:'blue'}}
    ];
    layoutBottom = {...layoutBase, title:"fraction of respiring biomass", yaxis:{title:'α'}};
  }

  Plotly.react('plot-top', dataTop, layoutTop);
  Plotly.react('plot-bottom', dataBottom, layoutBottom);
}

// ==== Event Handlers ====
function updateSliderSpan(slider) {
  const span = slider.nextElementSibling;
  span.textContent = Number(slider.value).toFixed(2);
}

function onSliderInput(e) {
  const slider = e.target;
  updateSliderSpan(slider);
  if (slider.id === 'slider-t-max') {
    const newMax = slider.value;
    const feedTimeSlider = document.getElementById('slider-feed-time');
    feedTimeSlider.max = newMax;
    if (feedTimeSlider.value > newMax) feedTimeSlider.value = newMax;
    updateSliderSpan(feedTimeSlider);
  }
  runSimulationAndDraw();
}

function onPlotSelectChange(e) {
  drawPlots();
}

// ==== Main Driver ====
function runSimulationAndDraw() {
  const params = {
    alphaMax: parseFloat(document.getElementById('slider-alpha-max').value),
    tMax: parseFloat(document.getElementById('slider-t-max').value),
    enzymeCtrl: parseFloat(document.getElementById('slider-enzyme-control').value),
