    feedConc: parseFloat(document.getElementById('slider-substrate-feed-conc').value),
    feedTime: parseFloat(document.getElementById('slider-feed-time').value)
  };
  simulationData = runSimulation(params);
  drawPlots();
}

document.addEventListener('DOMContentLoaded', () => {
