    updateReadouts();
    updateSunPath();
    updateSunPosition();
    updateEarthRotation();
    updateTerminator();
    updateLatitudeParallel();
    updateDayNightParallel();

    setViewMode('ground');

    // Light
    const ambient = new THREE.AmbientLight(0x222222);
