<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Unsteady-State Diffusion in a Slab</title>
<style>
  body {font-family: Arial, sans-serif; margin: 0; padding: 0;}
  #main-container {display: flex; flex-direction: column; align-items: center; max-width: 1200px; margin: 0 auto; padding: 20px;}
  h1 {margin-bottom: 10px;}
  p {margin-bottom: 20px; line-height: 1.4;}
  #control-panel {background:#f0f0f0; border:1px solid #ccc; padding: 15px; width: 100%; box-sizing: border-box; display:grid; grid-template-columns: 1fr 1fr; gap:15px; margin-bottom: 20px;}
  .control-row {display:flex; flex-direction:column;}
  label {margin-bottom:5px;}
  input[type=range] {width: 100%;}
  #plot-div {width: 100%; height: 500px;}
  @media (max-width: 600px) {
    #control-panel {grid-template-columns: 1fr;}
  }
</style>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
<div id="main-container">
  <h1>Unsteady-State Diffusion in a Slab</h1>
  <p>
    This demo visualizes the diffusion of a solute within a slab of thickness 0.004 m, where the surface concentration is fixed at 4.0×10<sup>-3</sup> kg/m³. The concentration profile evolves according to the analytic solution of the diffusion equation. Use the controls below to adjust the elapsed time and the diffusion coefficient, and observe how the profile changes in real time.
  </p>
  <div id="control-panel">
    <div class="control-row">
      <label for="time-slider">time (s)</label>
      <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
      <span id="time-value">100.0</span>
    </div>
    <div class="control-row">
      <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
      <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
      <span id="diffusivity-value">4.0 × 10<sup>-9</sup></span>
    </div>
  </div>
  <div id="plot-div"></div>
</div>

<script>
  // Constants
  const L = 0.004;           // slab thickness [m]
  const cs = 4.0e-3;         // surface concentration [kg/m³]
  const N = 100;             // number of terms in series
  const xPoints = 200;       // sampling points in [0, L]

  // Precompute wave numbers k_n and coefficients B_n (independent of t and D)
  const k = [];
  const B = [];
  for (let n = 0; n < N; n++) {
    const kn = ((n + 0.5) * Math.PI) / L;
    k.push(kn);
    const term = ((250 * Math.pow(-1, n)) / (kn * kn)) - (3 / kn);
    const Bn = (2 / L) * 1e-3 * term;
    B.push(Bn);
  }

  // Utility functions
  function formatTime(t) {
    return parseFloat(t).toFixed(1);
  }

  function formatDiffusivity(D) {
    const exponent = Math.floor(Math.log10(D));
    const mantissa = D / Math.pow(10, exponent);
    return `${mantissa.toFixed(1)} × 10<sup>${exponent}</sup>`;
  }

  function createArray(start, end, points) {
    const arr = [];
    const step = (end - start) / (points - 1);
    for (let i = 0; i < points; i++) {
      arr.push(start + step * i);
    }
    return arr;
  }

  // Plotting
  const xArray = createArray(0, L, xPoints);
  let layout = {
    title: '',
    xaxis: {
      title: 'position (m)',
      range: [0, L],
      tickformat: '.3f',
      dtick: 0.001,
      showgrid: true,
      minor: { showgrid: true }
    },
    yaxis: {
      title: 'concentration (kg/m³)',
      range: [0.001, 0.0042],
      tickformat: '.4f',
      dtick: 0.0005,
      showgrid: true,
      minor: { showgrid: true }
    },
    margin: { l: 80, r: 20, t: 20, b: 50 },
    datarevision: 0
  };

  function updatePlot() {
    const t = parseFloat(document.getElementById('time-slider').value);
    const D = parseFloat(document.getElementById('diffusivity-slider').value);
    const yArray = new Array(xPoints);

    if (t === 0) {
      for (let i = 0; i < xPoints; i++) {
        const x = xArray[i];
        yArray[i] = (1.0 + 250 * x) * 1e-3;
      }
    } else {
      for (let i = 0; i < xPoints; i++) {
        const x = xArray[i];
        let u = 0.0;
        for (let n = 0; n < N; n++) {
          u += B[n] * Math.sin(k[n] * x) * Math.exp(-k[n] * k[n] * D * t);
        }
        yArray[i] = u + cs;
      }
    }

    const trace = { x: xArray, y: yArray, mode: 'lines', name: 'c(x)' };
    layout.datarevision = (layout.datarevision + 1) % Number.MAX_SAFE_INTEGER;
    Plotly.react('plot-div', [trace], layout);
  }

  // Initial setup
  document.addEventListener('DOMContentLoaded', () => {
    const timeSlider = document.getElementById('time-slider');
    const diffusivitySlider = document.getElementById('diffusivity-slider');
    const timeVal = document.getElementById('time-value');
    const diffusivityVal = document.getElementById('diffusivity-value');

    // Initialize display values
    timeVal.textContent = formatTime(timeSlider.value);
    diffusivityVal.innerHTML = formatDiffusivity(diffusivitySlider.value);

    // Event listeners
    timeSlider.addEventListener('input', () => {
      timeVal.textContent = formatTime(timeSlider.value);
      updatePlot();
    });

    diffusivitySlider.addEventListener('input', () => {
      diffusivityVal.innerHTML = formatDiffusivity(diffusivitySlider.value);
      updatePlot();
    });

    // Initial plot
    updatePlot();
  });
</script>
</body>
</html>
