<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Integration Estimation Demo</title>
<style>
  body, html { margin:0; padding:0; font-family:Arial, sans-serif; background:#f0f0f0; }
  #app-container { display:flex; flex-direction:column; align-items:center; max-width:1000px; margin:20px auto; }
  #controls-container { display:flex; flex-wrap:wrap; gap:15px; justify-content:center; margin-bottom:10px; }
  .control-group { display:flex; align-items:center; gap:5px; }
  .control-group span { font-weight:bold; }
  button { padding:5px 10px; border:1px solid #888; background:white; cursor:pointer; }
  button.active { background:#1e90ff; color:white; border-color:#1e90ff; }
  input[type="range"] { width:120px; }
  #value-a, #value-n { font-weight:bold; margin-left:5px; }
  #canvas-container { width:100%; max-width:800px; height:500px; background:#fafafa; border:1px solid #ccc; }
</style>
</head>
<body>
<div id="app-container">
  <div id="controls-container">
    <div id="function-controls" class="control-group">
      <span>function</span>
      <button class="function-btn active" data-function="x">x</button>
      <button class="function-btn" data-function="sqr_x_minus_3"> (x‑3)<sup>2</sup> </button>
      <button class="function-btn" data-function="cube_x_minus_3_plus_27"> (x‑3)<sup>3</sup>+27 </button>
      <button class="function-btn" data-function="sqrt_x"> √x </button>
      <button class="function-btn" data-function="sin_x_plus_1"> sin(x)+1 </button>
      <button class="function-btn" data-function="exp_x_div_3"> e<sup>x</sup>/3 </button>
    </div>
    <div id="type-controls" class="control-group">
      <span>type</span>
      <button class="type-btn active" data-type="left">left</button>
      <button class="type-btn" data-type="right">right</button>
      <button class="type-btn" data-type="midpoint">midpoint</button>
      <button class="type-btn" data-type="overestimate">overestimate</button>
      <button class="type-btn" data-type="underestimate">underestimate</button>
      <button class="type-btn" data-type="trapezoidal">trapezoidal</button>
    </div>
    <div id="slider-a-controls" class="control-group">
      <label for="slider-a">upper limit a</label>
      <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5">
      <span id="value-a">5.00</span>
    </div>
    <div id="slider-n-controls" class="control-group">
      <label for="slider-n">num of quadrilaterals</label>
      <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
      <span id="value-n">3</span>
    </div>
  </div>
  <div id="canvas-container"></div>
</div>

<script src="https://cdn.jsdelivr.net/npm/p5@1.9.4/lib/p5.min.js"></script>
<script>
/* ------ State & Function Map ------ */
let state = { funcKey: 'x', type: 'right', a: 5, n: 3 };

const functionMap = {
  'x': { func: x => x, integral: x => x*x/2 },
  'sqr_x_minus_3': { func: x => Math.pow(x-3,2), integral: x => Math.pow(x-3,3)/3 },
  'cube_x_minus_3_plus_27': { func: x => Math.pow(x-3,3)+27, integral: x => Math.pow(x-3,4)/4 + 27*x },
  'sqrt_x': { func: x => x>=0 ? Math.sqrt(x) : NaN, integral: x => (2/3)*Math.pow(x,1.5) },
  'sin_x_plus_1': { func: x => Math.sin(x)+1, integral: x => -Math.cos(x)+x },
  'exp_x_div_3': { func: x => Math.exp(x)/3, integral: x => Math.exp(x)/3 }
};

/* ------ Event Listeners ------ */
function setupListeners() {
  // Function buttons
  document.querySelectorAll('.function-btn').forEach(btn => {
    btn.addEventListener('click', () => {
      document.querySelectorAll('.function-btn').forEach(b=>b.classList.remove('active'));
      btn.classList.add('active');
      state.funcKey = btn.dataset.function;
      sketch.redraw();
    });
  });

  // Type buttons
  document.querySelectorAll('.type-btn').forEach(btn => {
    btn.addEventListener('click', () => {
      document.querySelectorAll('.type-btn').forEach(b=>b.classList.remove('active'));
      btn.classList.add('active');
      state.type = btn.dataset.type;
      sketch.redraw();
    });
  });

  // Slider a
  const sliderA = document.getElementById('slider-a');
  const valueA = document.getElementById('value-a');
  sliderA.addEventListener('input', () => {
    state.a = parseFloat(sliderA.value);
    valueA.textContent = state.a.toFixed(2);
    sketch.redraw();
  });

  // Slider n
  const sliderN = document.getElementById('slider-n');
  const valueN = document.getElementById('value-n');
  sliderN.addEventListener('input', () => {
    state.n = parseInt(sliderN.value,10);
    valueN.textContent = state.n;
    sketch.redraw();
  });
}

/* ------ p5 Sketch ------ */
let sketch = new p5(s => {
  s.setup = () => {
    s.createCanvas(800, 500);
    s.noLoop(); // redraw only when state changes
    s.textFont('Arial');
  };

  s.draw = () => {
    s.background(248,248,248);

    const fObj = functionMap[state.funcKey];
    if (!fObj) return;

    // ----- Determine y-axis bounds -----
    let yVals = [];
    for (let x=0; x<=state.a; x+=0.01) {
      yVals.push(fObj.func(x));
    }
    const yMin = Math.min(...yVals);
    const yMax = Math.max(...yVals);
    const yPadding = (yMax - yMin) * 0.1 + 0.1;
    const yLow = yMin - yPadding;
    const yHigh = yMax + yPadding;

    // Map logical to pixel
    const mapX = x => s.map(x, -1, state.a+1, 50, s.width-50);
    const mapY = y => s.map(y, yLow, yHigh, s.height-50, 50);

    // ----- Draw Axes -----
    s.stroke(204);
    s.line(mapX(0), mapY(yLow), mapX(0), mapY(yHigh)); // y-axis
    s.line(mapX(0), mapY(0), mapX(state.a), mapY(0));   // x-axis

    // Tick marks
    s.noFill();
    s.stroke(200);
    for (let tickX=0; tickX<=state.a; tickX+=1) {
      s.line(mapX(tickX), mapY(0)-5, mapX(tickX), mapY(0)+5);
      s.fill(0); s.text(tickX, mapX(tickX), mapY(0)-15);
    }
    for (let tickY=Math.ceil(yLow); tickY<=Math.ceil(yHigh); tickY+=1) {
      if (tickY===0) continue;
      s.line(mapX(0)-5, mapY(tickY), mapX(0)+5, mapY(tickY));
      s.fill(0); s.text(tickY, mapX(0)-10, mapY(tickY));
    }

    // ----- Draw Function -----
    s.stroke(0);
    s.strokeWeight(2);
    s.noFill();
    s.beginShape();
    for (let x=0; x<=state.a; x+=0.005) {
      const y = fObj.func(x);
      s.vertex(mapX(x), mapY(y));
    }
    s.endShape();

    // ----- Draw Estimation Shapes -----
    const deltaX = state.a / state.n;
    let estimatedArea = 0;
    for (let i=0; i<state.n; i++) {
      const x0 = i * deltaX;
      const x1 = x0 + deltaX;

      // Determine height based on type
      let height, area;
      const f0 = fObj.func(x0);
      const f1 = fObj.func(x1);
      const mid = (x0 + x1)/2;
      const fm = fObj.func(mid);

      if (state.type === 'left') {
        height = f0; area = height * deltaX;
      } else if (state.type === 'right') {
        height = f1; area = height * deltaX;
      } else if (state.type === 'midpoint') {
        height = fm; area = height * deltaX;
      } else if (state.type === 'overestimate') {
        // Sample points to find max
        const samples = 20;
        let maxY = -Infinity;
        for (let s=0; s<=samples; s++) {
          const xp = x0 + (s/samples)*deltaX;
          const yp = fObj.func(xp);
          if (yp > maxY) maxY = yp;
        }
        height = maxY; area = height * deltaX;
      } else if (state.type === 'underestimate') {
        const samples = 20;
        let minY = Infinity;
        for (let s=0; s<=samples; s++) {
          const xp = x0 + (s/samples)*deltaX;
          const yp = fObj.func(xp);
          if (yp < minY) minY = yp;
        }
        height = minY; area = height * deltaX;
      } else { // trapezoidal
        height = Math.max(f0, f1); // for shape drawing we don't need full trapezoid
        // area computed differently below
      }

      // Compute exact integral over [x0,x1]
      const trueInt = fObj.integral(x1) - fObj.integral(x0);

      // Color logic
      let fillColor;
      if (state.type === 'trapezoidal') {
        const avgHeight = (f0+f1)/2;
        area = avgHeight * deltaX;
        height = avgHeight; // for drawing shape
        fillColor = (area > trueInt) ? 'rgba(173,216,230,0.7)' : 'rgba(144,238,144,0.7)';
      } else {
        fillColor = (area > trueInt) ? 'rgba(173,216,230,0.7)' : 'rgba(144,238,144,0.7)';
      }

      s.noStroke();
      s.fill(fillColor);
      s.stroke('#555');

      if (state.type === 'trapezoidal') {
        // Draw trapezoid
        s.beginShape();
        s.vertex(mapX(x0), mapY(0));
        s.vertex(mapX(x1), mapY(0));
        s.vertex(mapX(x1), mapY(f1));
        s.vertex(mapX(x0), mapY(f0));
        s.endShape(CLOSE);
      } else {
        // Draw rectangle
        s.rect(mapX(x0), mapY(0), deltaX* (s.width-100)/ (state.a+2), mapY(height)-mapY(0));
        // Rectangle height drawn from x axis
      }

      estimatedArea += area;
    }

    // ----- Draw Upper Limit Marker -----
    s.stroke(255,0,0);
    s.strokeWeight(2);
    s.line(mapX(state.a), mapY(yLow), mapX(state.a), mapY(yHigh));

    // ----- Display Calculations -----
    const integralTotal = fObj.integral(state.a) - fObj.integral(0);
    s.noStroke();
    s.fill(0);
    s.textSize(16);
    const textY = 30;
    s.textAlign(CENTER, CENTER);
    s.text(`estimated area: ${estimatedArea.toFixed(5)}`, s.width/2, textY);
    s.text(`integral:       ${integralTotal.toFixed(5)}`, s.width/2, textY + 20);
  };
});

/* ------ Initialize ------ */
window.addEventListener('load', () => {
  setupListeners();
});
</script>
</body>
</html>
