        <span id="value-current">0.431</span>
      </div>
      <div class="slider-group">
        <label for="slider-earth-field">horizontal Earth field (T)</label>
        <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
        <span id="value-earth-field">1.94e−5</span>
      </div>
      <div class="slider-group">
        <label for="slider-radius">radius (m)</label>
        <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
        <span id="value-radius">0.5</span>
      </div>
      <div class="slider-group">
        <label for="slider-turns">number of turns</label>
        <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
        <span id="value-turns">25</span>
      </div>
      <button id="btn-reset" title="Reset">&#x21BB;</button>
    </div>
    <div id="viz-container">
      <canvas id="p5-canvas"></canvas>
    </div>
  </div>

  <!-- p5.js CDN -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    /* ----- Parameter storage ----- */
    const params = {
      I: 0.431,
      BzEarth: 0.0000194,
      R: 0.5,
      N: 25
    };
    const INIT = { I:0.431, BzEarth:0.0000194, R:0.5, N:25 };

    /* ----- UI helper functions ----- */
    function formatValue(val) {
      return Math.abs(val) < 0.0005 ? val.toExponential(2) : val.toFixed(3);
    }

    function updateValueDisplay(id, val) {
      document.getElementById(id).innerText = formatValue(val);
    }

    /* ----- Event listeners ----- */
    document.getElementById('slider-current').addEventListener('input', e=>{
      params.I = parseFloat(e.target.value);
      updateValueDisplay('value-current', params.I);
      redraw();
    });

    document.getElementById('slider-earth-field').addEventListener('input', e=>{
      params.BzEarth = parseFloat(e.target.value);
      updateValueDisplay('value-earth-field', params.BzEarth);
      redraw();
    });

    document.getElementById('slider-radius').addEventListener('input', e=>{
      params.R = parseFloat(e.target.value);
      updateValueDisplay('value-radius', params.R);
      redraw();
    });

    document.getElementById('slider-turns').addEventListener('input', e=>{
      params.N = parseInt(e.target.value);
      updateValueDisplay('value-turns', params.N);
      redraw();
    });

    document.getElementById('btn-reset').addEventListener('click', ()=>{
      params.I = INIT.I;
      params.BzEarth = INIT.BzEarth;
      params.R = INIT.R;
      params.N = INIT.N;

      document.getElementById('slider-current').value = params.I;
      document.getElementById('slider-earth-field').value = params.BzEarth;
      document.getElementById('slider-radius').value = params.R;
      document.getElementById('slider-turns').value = params.N;

      updateValueDisplay('value-current', params.I);
      updateValueDisplay('value-earth-field', params.BzEarth);
      updateValueDisplay('value-radius', params.R);
      updateValueDisplay('value-turns', params.N);
      redraw();
    });

    /* ----- Elliptic integrals ----- */
    function ellipticK(m) {
      // AGM method
      let a = 1, b = Math.sqrt(1 - m);
      while (Math.abs(a - b) > 1e-10) {
        const an = (a + b) / 2;
        b = Math.sqrt(a * b);
        a = an;
      }
      return Math.PI / (2 * a);
    }

    function ellipticE(m) {
      // Numerical integration (Simpson's rule)
      const steps = 50; // reasonable accuracy
      const h = Math.PI / (2 * steps);
      let sum = 0;
      for (let i = 0; i <= steps; i++) {
        const theta = i * h;
        sum += Math.sqrt(1 - m * Math.sin(theta) * Math.sin(theta));
      }
      return h * sum;
    }

    /* ----- Magnetic field calculation ----- */
    const MU0 = 4 * Math.PI * 1e-7; // H/m
    function BFieldAt(y, z) {
      const { I, R, N, BzEarth } = params;
      // Earth field
      let Bx = 0; // x-component irrelevant (x=0 plane)
      let By = 0;
      let Bz = BzEarth;

      // Helmholtz coils (two symmetrically placed along z)
      const Bcoil1 = fieldFromCoil(y, z, -R/2);
      const Bcoil2 = fieldFromCoil(y, z, +R/2);

      By += Bcoil1.By + Bcoil2.By;
      Bz += Bcoil1.Bz + Bcoil2.Bz;
      return { By, Bz };
    }

    function fieldFromCoil(y, z, zOffset) {
      const { I, R, N } = params;
      const zp = z - zOffset;
      const denom = Math.sqrt((R + y) ** 2 + zp ** 2);
      const m = 4 * R * y / (((R + y) ** 2 + zp ** 2));
      const K = ellipticK(Math.max(0, Math.min(m, 1)));
      const E = ellipticE(Math.max(0, Math.min(m, 1)));
      const factor = (MU0 * I * N) / (2 * Math.PI);
      let By = 0;
      let Bz = 0;
      if (Math.abs(y) > 1e-8) {
        By = factor * (zp / y) * (1 / denom) *
          (((R ** 2 + y ** 2 + zp ** 2) / ((R - y) ** 2 + zp ** 2)) * E - K);
      }
      if (m >= 0 && m <= 1) { // valid m
        Bz = factor * (1 / denom) *
          (K - ((R ** 2 - y ** 2 - zp ** 2) / ((R - y) ** 2 + zp ** 2)) * E);
      }
      return { By, Bz };
    }

    /* ----- P5.js sketch ----- */
    const sketch = (p) => {
      let canvas;
      let width = 400, height = 400; // default, resizable later

      p.setup = () => {
        canvas = p.createCanvas(width, height);
        canvas.parent('p5-canvas');
        p.pixelDensity(1);
        p.colorMode(p.HSB, 360, 100, 100);
        resizeCanvas();
      };

      function resizeCanvas() {
        const container = document.getElementById('viz-container');
        const rect = container.getBoundingClientRect();
        p.resizeCanvas(rect.width, rect.height);
        width = rect.width;
        height = rect.height;
      }

      p.windowResized = () => {
        resizeCanvas();
      };

      // Map world (-0.5..0.5) to canvas coordinates
      function worldToCanvas(y, z) {
        const cx = p.map(y, -0.5, 0.5, 0, width);
        const cy = p.map(z, -0.5, 0.5, height, 0); // invert y for canvas
        return { cx, cy };
      }

      // Grid size for streamlines
      const gridCount = 25;
      const stepSize = 0.01; // world units
      const maxSteps = 200;

      p.draw = () => {
        p.clear();
        drawAxes();
        drawStreamlines();
      };

      function drawAxes() {
        p.stroke(0); // black for axis lines
        p.strokeWeight(1);
        // Y-axis (horizontal)
        p.line(0, height/2, width, height/2);
        // Z-axis (vertical)
        p.line(width/2, 0, width/2, height);
        // Tick marks
        p.stroke(255, 0, 0); // red
        p.strokeWeight(1);
        const ticks = [-0.4, -0.2, 0, 0.2, 0.4];
        const px = width/2;
        const py = height/2;
        const tickLen = 5;
        p.textAlign(p.CENTER, p.CENTER);
        p.fill(255, 0, 0);
        ticks.forEach(v=>{
          const {cx,cy}=worldToCanvas(v,0);
          p.line(cx, cy - tickLen, cx, cy + tickLen);
          p.text(v.toFixed(1), cx, tens=cy > py ? cy + 15 : cy - 10);
          // for Z
          const {cx:cxz,cy:cyz}=worldToCanvas(0,v);
          p.line(cxz - tickLen, cyz, cxz + tickLen, cyz);
          p.text(v.toFixed(1), cxz + 15, cyz);
        });
      }

      // Pre-compute min/max magnitude for color scale
      let minMag = Infinity, maxMag = -Infinity;
      function computeMagRange() {
        minMag = Infinity; maxMag = -Infinity;
        for (let i=0; i<gridCount; i++){
          const y = p.map(i,0,gridCount-1,-0.5,0.5);
          for (let j=0;j<gridCount;j++){
            const z = p.map(j,0,gridCount-1,-0.5,0.5);
            const B = BFieldAt(y,z);
            const mag = Math.hypot(B.By, B.Bz);
            if (mag<minMag) minMag=mag;
            if (mag>maxMag) maxMag=mag;
          }
        }
        // avoid zero range
        if (maxMag === minMag) maxMag = minMag+1e-6;
      }

      function drawStreamlines() {
        computeMagRange();
        const startPoints = [];
        for (let i=0;i<gridCount;i++){
          const y = p.map(i,0,gridCount-1,-0.5,0.5);
          for (let j=0;j<gridCount;j++){
            const z = p.map(j,0,gridCount-1,-0.5,0.5);
            startPoints.push({y,z});
          }
        }
        startPoints.forEach(p0=>{
          // forward
          drawStreamline(p0.y, p0.z, 1);
          // backward
          drawStreamline(p0.y, p0.z, -1);
        });
      }

      function drawStreamline(y, z, dir) {
        let currY = y, currZ = z;
        const points = [];
        for (let s=0; s<maxSteps; s++){
          const B = BFieldAt(currY, currZ);
          const mag = Math.hypot(B.By, B.Bz);
          if (mag < 1e-10) break;
          const dY = B.By / mag * dir;
          const dZ = B.Bz / mag * dir;
          points.push({y:currY, z:currZ});
          currY += dY * stepSize;
          currZ += dZ * stepSize;
          if (Math.abs(currY) > 0.5 || Math.abs(currZ) > 0.5) break;
        }
        if (points.length < 2) return;
        // color based on magnitude of first segment
        const firstB = BFieldAt(points[0].y, points[0].z);
        const firstMag = Math.hypot(firstB.By, firstB.Bz);
        const hue = p.map(firstMag, minMag, maxMag, 270, 60);
        p.stroke(hue, 100, 100);
        p.strokeWeight(2);
        for (let i=1;i<points.length;i++){
          const pStart = worldToCanvas(points[i-1].y, points[i-1].z);
          const pEnd = worldToCanvas(points[i].y, points[i].z);
          p.line(pStart.cx, pStart.cy, pEnd.cx, pEnd.cy);
        }
        // arrows
        for (let i=10;i<points.length-1;i+=10){
          const pPrev = worldToCanvas(points[i-1].y, points[i-1].z);
          const pCurr = worldToCanvas(points[i].y, points[i].z);
          const pNext = worldToCanvas(points[i+1].y, points[i+1].z);
          drawArrow(pPrev, pCurr, pNext);
        }
      }

      function drawArrow(pA, pB, pC) {
        const cx = (pA.cx + pC.cx) / 2;
        const cy = (pA.cy + pC.cy) / 2;
        const angle = Math.atan2(pB.cy - cy, pB.cx - cx);
        const arrowLen = 8;
        const arrowAngle = Math.PI / 6;
        p.fill(hue, 100, 100);
        p.noStroke();
        p.push();
        p.translate(cx, cy);
        p.rotate(angle);
        p.beginShape();
        p.vertex(0, 0);
        p.vertex(-arrowLen, -arrowLen * Math.tan(arrowAngle));
        p.vertex(-arrowLen, arrowLen * Math.tan(arrowAngle));
        p.endShape(p.CLOSE);
        p.pop();
      }
    };

    new p5(sketch);
  </script>
</body>
</html>
