<script>
const WORD_LIST = [
  'THE','IDES','OF','MARCH','ARE','COME','EVEN','YOU','BRUTUS',
  'CAESAR','CIPHER','HELLO','WORLD','SECRET','MESSAGE','ATTACK',
  'DAWN','I','A'
].sort((a,b)=>b.length-a.length);

let currentMessage = '';
let currentCiphertext = '';
let interceptedCiphertext = '';

const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
const alphabetArray = alphabet.split('');

// ------------------------------------------------------------------
// Utility functions
function shiftAlphabet(offset) {
  // offset may be negative or positive 0..25
  const res = [];
  for (let i = 0; i < 26; i++) {
    const idx = (i + offset + 26) % 26;
    res.push(alphabetArray[idx]);
  }
  return res.join(' ');
}

function encryptText(text, offset) {
  let res = '';
  for (let ch of text) {
    if (ch >= 'A' && ch <= 'Z') {
      const code = (ch.charCodeAt(0) - 65 + offset + 26) % 26 + 65;
      res += String.fromCharCode(code);
    }
  }
  return res;
}

function decryptText(text, offset) {
  let res = '';
  for (let ch of text) {
    if (ch >= 'A' && ch <= 'Z') {
      const code = (ch.charCodeAt(0) - 65 - offset + 26) % 26 + 65;
      res += String.fromCharCode(code);
    }
  }
  return res;
}

function highlightWords(str) {
  let i = 0;
  let html = '';
  while (i < str.length) {
    let matched = false;
    for (let w of WORD_LIST) {
      if (str.startsWith(w, i)) {
        html += `<span class="highlighted">${w}</span>`;
        i += w.length;
        matched = true;
        break;
      }
    }
    if (!matched) {
      html += str[i];
      i++;
    }
  }
  return html;
}

// ------------------------------------------------------------------
// Generate virtual keyboards
function generateKeyboard(container, withSpace, withBackspace) {
  const frag = document.createDocumentFragment();
  for (let ch of alphabetArray) {
    const btn = document.createElement('button');
    btn.textContent = ch;
    btn.dataset.key = ch;
    frag.appendChild(btn);
  }
  if (withSpace) {
    const btn = document.createElement('button');
    btn.textContent = 'SPACE';
    btn.dataset.key = ' ';
    frag.appendChild(btn);
  }
  if (withBackspace) {
    const btn = document.createElement('button');
    btn.textContent = 'BACKSPACE';
    btn.dataset.key = 'BACKSPACE';
    frag.appendChild(btn);
  }
  container.appendChild(frag);
}

// ------------------------------------------------------------------
// Encoding panel logic (updateEncoding)
function updateEncoding() {
  const offset = parseInt(document.getElementById('slider-encoding-offset').value,10);
  const plaintext = currentMessage.toUpperCase().replace(/ /g,'X');
  const ciphertext = encryptText(plaintext, offset);
  currentCiphertext = ciphertext;

  document.getElementById('display-message').textContent = currentMessage;
  document.getElementById('display-plaintext').textContent = plaintext;
  document.getElementById('display-ciphertext').textContent = ciphertext;

  document.getElementById('display-encoding-alphabet-to').textContent = shiftAlphabet(offset);
}

// ------------------------------------------------------------------
// Decoding panel logic (updateDecoding)
function updateDecoding() {
  const offset = parseInt(document.getElementById('slider-decoding-offset').value,10);
  document.getElementById('display-decoding-alphabet-from').textContent = shiftAlphabet(-offset);
  document.getElementById('display-decoding-alphabet-to').textContent = shiftAlphabet(0);

  const tbody = document.querySelector('#table-decoding-output tbody');
  tbody.innerHTML = '';

  const offsets = [offset-2, offset-1, offset, offset+1, offset+2];
  for (let d of offsets) {
    const tr = document.createElement('tr');
    const tdOffset = document.createElement('td');
    tdOffset.textContent = d;
    tr.appendChild(tdOffset);

    const decoded = decryptText(interceptedCiphertext, d);
    const highlighted = highlightWords(decoded);
    const tdDecoded = document.createElement('td');
    tdDecoded.innerHTML = highlighted;
    tr.appendChild(tdDecoded);

    tbody.appendChild(tr);
  }

  document.getElementById('display-intercept').textContent = interceptedCiphertext;
}

// ------------------------------------------------------------------
// Tab controls
document.getElementById('btn-toggle-encoding').addEventListener('click', ()=>{
  document.getElementById('panel-encoding').style.display = '';
  document.getElementById('panel-decoding').style.display = 'none';
  document.getElementById('btn-toggle-encoding').classList.add('active');
  document.getElementById('btn-toggle-decoding').classList.remove('active');
});

document.getElementById('btn-toggle-decoding').addEventListener('click', ()=>{
  document.getElementById('panel-encoding').style.display = 'none';
  document.getElementById('panel-decoding').style.display = '';
  document.getElementById('btn-toggle-decoding').classList.add('active');
  document.getElementById('btn-toggle-encoding').classList.remove('active');
});

// ------------------------------------------------------------------
// Encoding panel event handlers
document.getElementById('select-message').addEventListener('change', (e)=>{
  currentMessage = e.target.value;
  updateEncoding();
});

document.getElementById('slider-encoding-offset').addEventListener('input', (e)=>{
  document.getElementById('span-encoding-offset-value').textContent = e.target.value;
  updateEncoding();
});

document.getElementById('keyboard-encoding').addEventListener('click', (e)=>{
  const key = e.target.dataset.key;
  if (!key) return;
  if (key === 'BACKSPACE') {
    currentMessage = currentMessage.slice(0, -1);
  } else {
    if (currentMessage.length < 32) currentMessage += key;
  }
  updateEncoding();
});

document.getElementById('btn-clear-encoding').addEventListener('click', ()=>{
  currentMessage = '';
  currentCiphertext = '';
  document.getElementById('slider-encoding-offset').value = 0;
  document.getElementById('span-encoding-offset-value').textContent = '0';
  updateEncoding();
});

// ------------------------------------------------------------------
// Decoding panel event handlers
document.getElementById('btn-intercept').addEventListener('click', ()=>{
  interceptedCiphertext = currentCiphertext;
  updateDecoding();
});

document.getElementById('slider-decoding-offset').addEventListener('input', (e)=>{
  document.getElementById('span-decoding-offset-value').textContent = e.target.value;
  updateDecoding();
});

document.getElementById('keyboard-decoding').addEventListener('click', (e)=>{
  const key = e.target.dataset.key;
  if (!key) return;
  if (key === 'BACKSPACE') {
    interceptedCiphertext = interceptedCiphertext.slice(0, -1);
  } else {
    if (interceptedCiphertext.length < 32) interceptedCiphertext += key;
  }
  updateDecoding();
});

document.getElementById('btn-clear-decoding').addEventListener('click', ()=>{
  interceptedCiphertext = '';
  document.getElementById('slider-decoding-offset').value = 0;
  document.getElementById('span-decoding-offset-value').textContent = '0';
  updateDecoding();
});

// ------------------------------------------------------------------
// Initialization
window.addEventListener('DOMContentLoaded', ()=>{
  generateKeyboard(document.getElementById('keyboard-encoding'), true, true);
  generateKeyboard(document.getElementById('keyboard-decoding'), false, true);
  updateEncoding();  // set initial state
  updateDecoding();
});
</script>
</body>
</html>
