<script>
let beforeData = [];
let afterData = [];
let gridSize = 24;
let convolver = 2;
let letter = 'a';
let filter = 'shadow';
let canvas;

// ---------- p5 Sketch ----------
new p5((p) => {
  p.setup = () => {
    canvas = p.createCanvas(400, 200);
    canvas.parent('canvas-before-container');
    p.noLoop();
    updateVisualization();
  };
  p.draw = () => {
    p.background(255);
    const rectSize = 200 / gridSize;
    // Left: before image
    for (let i = 0; i < gridSize; i++) {
      for (let j = 0; j < gridSize; j++) {
        p.noStroke();
        p.fill(beforeData[i][j] ? 0 : 255);
        p.rect(j * rectSize, i * rectSize, rectSize, rectSize);
      }
    }
    // Right: after image
    for (let i = 0; i < gridSize; i++) {
      for (let j = 0; j < gridSize; j++) {
        p.noStroke();
        const val = afterData[i][j];
        const gray = p.map(val, 0, 1, 255, 0);
        p.fill(gray);
        p.rect(200 + j * rectSize, i * rectSize, rectSize, rectSize);
      }
