}, 'visualization-area');

// ---------- Utility Functions ----------
function getKernel(k, type) {
  const D = 2 * k + 1;
  const kernel = Array.from({ length: D }, () => Array(D).fill(0));
  if (type === 'blur') {
    const v = 1 / (D * D);
    for (let r = 0; r < D; r++) for (let c = 0; c < D; c++) kernel[r][c] = v;
  } else { // shadow
    const offset = 1;
    for (let r = 0; r < k; r++) {
      for (let c = 0; c < k; c++) {
        kernel[r + offset][c + offset] = 1 / (k * k);
      }
    }
  }
  return kernel;
}

function convolve(src, kernel, k) {
  const N = src.length;
  const out = Array.from({ length: N }, () => Array(N).fill(0));
  for (let i = 0; i < N; i++) {
    for (let j = 0; j < N; j++) {
      let sum = 0;
      for (let dr = -k; dr <= k; dr++) {
        for (let dc = -k; dc <= k; dc++) {
          const ii = i + dr;
          const jj = j + dc;
          const val = (ii >= 0 && ii < N && jj >= 0 && jj < N) ? src[ii][jj] : 0;
          sum += val * kernel[dr + k][dc + k];
        }
      }
      out[i][j] = sum;
    }
  }
  return out;
}

function renderLetterGrid(size, char) {
  const gfx = createGraphics(size, size);
  gfx.pixelDensity(1);
  gfx.background(255);
  gfx.textAlign(CENTER, CENTER);
  gfx.textSize(size * 0.8);
  gfx.fill(0);
  gfx.text(char, size / 2, size / 2);
  gfx.loadPixels();
  const data = [];
  let idx = 0;
  for (let i = 0; i < size; i++) {
    const row = [];
    for (let j = 0; j < size; j++) {
      const r = gfx.pixels[idx];
      const v = r < 128 ? 1 : 0;
      row.push(v);
      idx += 4; // RGBA
    }
    data.push(row);
  }
  return data;
}

// ---------- Main Update ----------
function updateVisualization() {
  convolver = parseInt(document.getElementById('slider-convolver').value, 10);
  gridSize = parseInt(document.getElementById('slider-image-size').value, 10);
  letter = document.getElementById('input-letter').value.toLowerCase() || 'a';
  filter = document.querySelector('input[name="filter"]:checked').value;

  beforeData = renderLetterGrid(gridSize, letter);
  const kernel = getKernel(convolver, filter);
  afterData = convolve(beforeData, kernel, convolver);
  p5.redraw();
}

// ---------- Event Handlers ----------
const inputs = [
  'slider-convolver',
  'slider-image-size',
  'input-letter',
  'radio-shadow',
  'radio-blur'
];

inputs.forEach(id => {
  const el = document.getElementById(id);
  if (el) el.addEventListener('input', updateVisualization);
});

document.getElementById('convolver-plus-btn').addEventListener('click', () => {
  const slider = document.getElementById('slider-convolver');
  slider.value = Math.min(parseInt(slider.value) + parseInt(slider.step), parseInt(slider.max));
  slider.dispatchEvent(new Event('input'));
});
document.getElementById('convolver-minus-btn').addEventListener('click', () => {
  const slider = document.getElementById('slider-convolver');
  slider.value = Math.max(parseInt(slider.value) - parseInt(slider.step), parseInt(slider.min));
  slider.dispatchEvent(new Event('input'));
});
document.getElementById('image-size-plus-btn').addEventListener('click', () => {
  const slider = document.getElementById('slider-image-size');
  slider.value = Math.min(parseInt(slider.value) + parseInt(slider.step), parseInt(slider.max));
  slider.dispatchEvent(new Event('input'));
});
document.getElementById('image-size-minus-btn').addEventListener('click', () => {
  const slider = document.getElementById('slider-image-size');
  slider.value = Math.max(parseInt(slider.value) - parseInt(slider.step), parseInt(slider.min));
  slider.dispatchEvent(new Event('input'));
});

// Arrow key cycling for letter input
document.getElementById('input-letter').addEventListener('keydown', ev => {
  if (ev.key === 'ArrowUp' || ev.key === 'ArrowDown') {
    ev.preventDefault();
    let code = letter.charCodeAt(0);
    if (ev.key === 'ArrowUp') {
      code = code >= 122 ? 97 : code + 1; // wrap z -> a
    } else {
      code = code <= 97 ? 122 : code - 1; // wrap a -> z
    }
    letter = String.fromCharCode(code);
    document.getElementById('input-letter').value = letter;
    document.getElementById('input-letter').dispatchEvent(new Event('input'));
  }
});

// Trigger initial draw
updateVisualization();
</script>
</body>
</html>
