</div>

<!-- Libraries -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r152/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/examples/js/controls/OrbitControls.js"></script>
<script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>

<script>
/*============= Data Generation ===================*/
const aminoAcids = ['A','C','D','E','F','G','H','I','K','L','M','N','P','Q','R','S','T','V','W','Y'];
function generateProteinData(num) {
  const residues=[];
  let x=0,y=0,z=0;
  for(let i=1;i<=num;i++){
    // Random walk for CA
    const dx=(Math.random()-0.5)*1.5;
    const dy=(Math.random()-0.5)*1.5;
    const dz=(Math.random()-0.5)*1.5;
    x+=dx; y+=dy; z+=dz;
    const base = {x,y,z};
    const atoms = [
      {atomName:'CA',x:base.x,y:base.y,z:base.z},
      {atomName:'N',  x:base.x,y:base.y+1,z:base.z},
      {atomName:'C',  x:base.x,y:base.y-1,z:base.z},
      {atomName:'O',  x:base.x,y:base.y+2,z:base.z},
      {atomName:'CB', x:base.x+1,y:base.y,z:base.z}
    ];
    const resName = aminoAcids[Math.floor(Math.random()*aminoAcids.length)]+aminoAcids[Math.floor(Math.random()*aminoAcids.length)]+aminoAcids[Math.floor(Math.random()*aminoAcids.length)];
    residues.push({resSeq:i,resName,atoms});
  }
  return residues;
}
const protein = generateProteinData(400);
const numRes = protein.length;

/*============= DOM References and defaults ===============*/
const btnCryst = document.getElementById('btn-crystallography');
const btnContact = document.getElementById('btn-contact-map');
const chkLateral = document.getElementById('checkbox-lateral-chains');
const sliderGray = document.getElementById('slider-gray-level');
const spanGray = document.getElementById('gray-level-value');
const sliderBrowse = document.getElementById('slider-browse-sequence');
const spanBrowse = document.getElementById('browse-sequence-value');
const selectResidue = document.getElementById('select-residue');
const sliderX = document.getElementById('slider-residue-x');
const spanX = document.getElementById('residue-x-value');
const sliderY = document.getElementById('slider-residue-y');
const spanY = document.getElementById('residue-y-value');
const sliderRMSD = document.getElementById('slider-rmsd');
const spanRMSD = document.getElementById('rmsd-value');
const sliderPlot = document.getElementById('slider-plot-size');
const spanPlot = document.getElementById('plot-size-value');
const btnReset = document.getElementById('btn-reset');
const infoDisplay = document.getElementById('info-display');
const canvasContainer = document.getElementById('canvas-container');

const plotSizeDefault = 400;

/* Populate residue selection dropdown with 20 letter codes */
aminoAcids.forEach(a => {
  const opt = document.createElement('option');
  opt.value = a;
  opt.textContent = a;
  selectResidue.appendChild(opt);
});

/*============= 3D Setup ===================*/
let renderer, scene, camera, controls, atomGroup, boxHelper, atomsByResidue=[];
function initThree(){
  renderer = new THREE.WebGLRenderer({antialias:true});
  renderer.setSize(plotSizeDefault,plotSizeDefault);
  canvasContainer.appendChild(renderer.domElement);
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0xffffff);
  camera = new THREE.PerspectiveCamera(50,plotSizeDefault/plotSizeDefault,0.1,1000);
  camera.position.set(0,0,200);
  controls = new THREE.OrbitControls(camera,renderer.domElement);
  const amb = new THREE.AmbientLight(0xcccccc,0.8);
  const dir = new THREE.DirectionalLight(0xffffff,0.8);
  dir.position.set(1,1,1);
  scene.add(amb,dir);
  atomGroup = new THREE.Group();
  scene.add(atomGroup);
  // Build atoms
  const sphereGeom = new THREE.SphereGeometry(0.5,16,16);
  protein.forEach((res,idx)=>{
    const meshes={};
    res.atoms.forEach(atom=>{
      const mat = new THREE.MeshLambertMaterial({color:0x888888});
      const mesh = new THREE.Mesh(sphereGeom,mat);
      mesh.position.set(atom.x,atom.y,atom.z);
      atomGroup.add(mesh);
      meshes[atom.atomName] = mesh;
    });
    atomsByResidue.push({resIndex:idx,meshes});
  });
  // Build bounding box
  boxHelper = new THREE.BoxHelper(atomGroup,null);
  scene.add(boxHelper);
}
initThree();

/*============= 2D Setup (p5) ===================*/
let p5Instance=null;
let distanceMatrix = []; // 2D array
function computeDistanceMatrix(){
  distanceMatrix = Array.from({length:numRes},()=>Array(numRes).fill(0));
  for(let i=0;i<numRes;i++){
    const ca1=protein[i].atoms.find(a=>a.atomName==='CA');
    for(let j=i+1;j<numRes;j++){
      const ca2=protein[j].atoms.find(a=>a.atomName==='CA');
      const dx=ca1.x-ca2.x, dy=ca1.y-ca2.y, dz=ca1.z-ca2.z;
      const dist=Math.sqrt(dx*dx+dy*dy+dz*dz);
      distanceMatrix[i][j]=distanceMatrix[j][i]=dist;
    }
  }
}
computeDistanceMatrix();

/* initializer for p5 sketch */
function sketch(p){
  let w=plotSizeDefault, h=plotSizeDefault;
  p.setup = function(){
    const canvas = p.createCanvas(w,h);
    canvas.parent('canvas-container');
    canvas.id('p5canvas');
    w = canvas.width;
    h = canvas.height;
    p.pixelDensity(1);
  };
  p.windowResized = function(){
    if(p5Instance){
      p.resizeCanvas(w,h);
    }
  };
  p.draw = function(){
    p.background(255);
    // Axes
    p.stroke(200);
    p.line(0,0,w-1,0);
    p.line(0,0,0,h-1);
    // Draw contact map points
    const thresh = parseFloat(sliderRMSD.value);
    for(let i=0;i<numRes;i++){
      for(let j=0;j<numRes;j++){
        if(distanceMatrix[i][j]<=thresh){
          const x = p.map(i,0,numRes-1,0,w-1);
          const y = p.map(j,0,numRes-1,0,h-1);
          p.stroke(0);
          p.point(x,y);
        }
      }
    }
    // Vertical line X
    const xVal = parseInt(sliderX.value)-1;
    if(xVal>=0 && xVal<numRes){
      const x = p.map(xVal,0,numRes-1,0,w-1);
      p.stroke('red');
      p.line(x,0,x,h);
    }
    // Horizontal line Y
    const yVal = parseInt(sliderY.value)-1;
    if(yVal>=0 && yVal<numRes){
      const y = p.map(yVal,0,numRes-1,0,h-1);
      p.stroke('red');
      p.line(0,y,w,y);
    }
  };
}

/*============= View Switch ===================*/
let currentView = 'crystallography';
function switchView(view){
  if(view===currentView) return;
  currentView = view;
  if(view==='crystallography'){
    // Remove p5
    if(p5Instance){
      p5Instance.remove();
      p5Instance=null;
    }
    // Attach renderer
    if(!renderer.domElement.parentElement){
      renderer.setSize(parseInt(sliderPlot.value),parseInt(sliderPlot.value));
      canvasContainer.appendChild(renderer.domElement);
    }
    controls.update();
    render3D(); // one render
  }else{
    // Detach renderer
    if(renderer.domElement.parentElement){
      renderer.domElement.remove();
    }
    // Start p5 sketch if not started
    if(!p5Instance){
      p5Instance = new p5(sketch);
    }
    p5Instance.canvas.resizeCanvas(parseInt(sliderPlot.value),parseInt(sliderPlot.value));
    p5Instance.redraw();
  }
}
switchView('crystallography');

/*============= Rendering 3D ===================*/
function render3D(){
  if(currentView!=='crystallography') return;
  update3DColors();
  renderer.render(scene,camera);
}
function update3DColors(){
  const grayVal = parseFloat(sliderGray.value);
  const baseColor = new THREE.Color(0x000000).lerp(new THREE.Color(0x0000ff),grayVal);
  const resAIndex = parseInt(sliderBrowse.value)-1;
  const resXIndex = parseInt(sliderX.value)-1;
  const resYIndex = parseInt(sliderY.value)-1;
  let caRed = new THREE.Color(0xff0000);
  let yellow = new THREE.Color(0xffff00);
  // iterate residues
  atomsByResidue.forEach((resObj,i)=>{
    const isCAHidden = !chkLateral.checked;
    Object.keys(resObj.meshes).forEach(atomName=>{
      const mesh = resObj.meshes[atomName];
      // visibility
      mesh.visible = !(isCAHidden && atomName!=='CA');
      // color rules
      if(i===resAIndex){
        mesh.material.color.copy(yellow);
      }else if((i===resXIndex || i===resYIndex) && atomName==='CA'){
        mesh.material.color.copy(caRed);
      }else{
        mesh.material.color.copy(baseColor);
      }
    });
  });
  // update bounding box
  boxHelper.update();
}

/*============= Info update ===================*/
function updateInfo(){
  const resA = protein[parseInt(sliderBrowse.value)-1];
  const resX = protein[parseInt(sliderX.value)-1];
  const resY = protein[parseInt(sliderY.value)-1];
  infoDisplay.textContent = `Res A: ${resA.resName}${resA.resSeq} | Res X: ${resX.resName}${resX.resSeq} | Res Y: ${resY.resName}${resY.resSeq}`;
}
updateInfo();

/*============= Size update ===================*/
function resizeCanvas(){
  const size = parseInt(sliderPlot.value);
  canvasContainer.style.width = size+'px';
  canvasContainer.style.height = size+'px';
  if(currentView==='crystallography'){
    camera.aspect = size/size;
    camera.updateProjectionMatrix();
    renderer.setSize(size,size);
  }else{
    if(p5Instance){
      p5Instance.resizeCanvas(size,size);
    }
  }
}

/*============= Interaction Handlers ===================*/
btnCryst.addEventListener('click',()=>{
  btnCryst.classList.add('active');
  btnContact.classList.remove('active');
  switchView('crystallography');
});
btnContact.addEventListener('click',()=>{
  btnContact.classList.add('active');
  btnCryst.classList.remove('active');
  switchView('contact map');
});

chkLateral.addEventListener('change',()=>{
  update3DColors();
  render3D();
});

sliderGray.addEventListener('input',()=>{
  spanGray.textContent = parseFloat(sliderGray.value).toFixed(2);
  if(currentView==='crystallography') update3DColors();
});
sliderBrowse.addEventListener('input',()=>{
  spanBrowse.textContent = sliderBrowse.value;
  if(currentView==='crystallography') update3DColors();
  updateInfo();
});
selectResidue.addEventListener('change', ()=>{
  // Move slider-browse-sequence to next/prev residue with same type
  const targetType = selectResidue.value;
  const currentIdx = parseInt(sliderBrowse.value)-1;
  // Search forward
  let found = null;
  for(let i=currentIdx+1;i<numRes;i++){
    if(protein[i].resName[0]===targetType){ found=i; break; }
  }
  // If not found, search backward
  if(found===null){
    for(let i=currentIdx-1;i>=0;i--){
      if(protein[i].resName[0]===targetType){ found=i; break; }
    }
  }
  if(found!==null){
    sliderBrowse.value = found+1;
    sliderBrowse.dispatchEvent(new Event('input'));
  }
});

sliderX.addEventListener('input',()=>{
  spanX.textContent = sliderX.value;
  if(currentView==='crystallography') update3DColors();
  updateInfo();
});
sliderY.addEventListener('input',()=>{
  spanY.textContent = sliderY.value;
  if(currentView==='crystallography') update3DColors();
  updateInfo();
});
sliderRMSD.addEventListener('input',()=>{
  spanRMSD.textContent = parseFloat(sliderRMSD.value).toFixed(1);
  if(currentView==='contact map'){
    p5Instance.redraw();
  }
});
sliderPlot.addEventListener('input',()=>{
  spanPlot.textContent = sliderPlot.value;
  resizeCanvas();
});

/* Reset button */
btnReset.addEventListener('click',()=>{
  camera.position.set(0,0,200);
  camera.lookAt(0,0,0);
  controls.update();
  if(currentView==='crystallography'){
    render3D();
  }
});

/* initial render */
render3D();
</script>
</body>
</html>
