<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Sylvester's Postage Stamp Problem Demo</title>
<style>
  body{font-family:Arial,Helvetica,sans-serif;margin:0;padding:0;background:#f9f9f9;}
  #info-content{display:none;padding:10px;background:#fff;border-bottom:1px solid #ccc;}
  #controls-panel{padding:10px;background:#fff;display:flex;flex-wrap:wrap;align-items:center;gap:10px;border-bottom:1px solid #ccc;}
  #inputs-container{display:flex;align-items:center;gap:5px;}
  label{margin-right:5px;}
  #canvas-container{padding:10px;background:#fff;}
  #equation-text{margin:10px;color:#333;font-weight:bold;}
</style>
<script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>
<script>
document.addEventListener('DOMContentLoaded', () => {
  const infoToggle = document.getElementById('info-toggle');
  const infoContent = document.getElementById('info-content');
  const checkboxMode = document.getElementById('checkbox-mode');
  const checkboxDisplay = document.getElementById('checkbox-display');
  const inputA = document.getElementById('input-a');
  const inputB = document.getElementById('input-b');
  const sliderC = document.getElementById('slider-c');
  const displayC = document.getElementById('display-c');
  const equationText = document.getElementById('equation-text');

  // Utility functions
  function gcd(a,b){while(b){[a,b]=[b,a%b];}return a;}
  function updateSliderMax(){const a = Number(inputA.value); const b = Number(inputB.value); const max = a*b*2; sliderC.max = max; if(sliderC.value > max) sliderC.value = max;}
  function updateDisplayC(){displayC.textContent = sliderC.value;}
  function disableCInput(disable){sliderC.disabled = disable; displayC.style.opacity = disable?0.5:1;}
  function updateEquationText(){let a=Number(inputA.value),b=Number(inputB.value); if(gcd(a,b)!==1){equationText.textContent="Numbers a and b should be coprime!";} else if(checkboxDisplay.checked){const F = a*b - a - b; equationText.textContent=`Frobenius number: ${a}*${b} - ${a} - ${b} = ${F}`;} else {equationText.textContent=`Current equation: ${a}x + ${b}y = ${sliderC.value}`;}
  }
  // Event listeners
  infoToggle.addEventListener('click',()=>{infoContent.style.display=infoContent.style.display==='none'?'block':'none';infoToggle.textContent=infoToggle.textContent==='+'?'-':'+';});
  inputA.addEventListener('change',()=>{inputA.value=parseInt(inputA.value)||7; updateSliderMax(); updateEquationText(); if(new p5Sketch) p5Sketch.redraw();});
  inputB.addEventListener('change',()=>{inputB.value=parseInt(inputB.value)||8; updateSliderMax(); updateEquationText(); if(new p5Sketch) p5Sketch.redraw();});
  sliderC.addEventListener('input',()=>{updateDisplayC(); if(new p5Sketch) p5Sketch.redraw();});
  checkboxMode.addEventListener('change',()=>{if(new p5Sketch) p5Sketch.redraw();});
  checkboxDisplay.addEventListener('change',()=>{if(checkboxDisplay.checked){disableCInput(true);}else{disableCInput(false);} updateEquationText(); if(new p5Sketch) p5Sketch.redraw();});
  // Initialize
  updateSliderMax(); updateDisplayC(); updateEquationText();
});

let new p5Sketch;

(function(){
  const canvasSize = 550;
  const margin = 50;
  class p5Sketch extends p5{
    constructor(){super();}
    setup(){
      const cnv = createCanvas(canvasSize, canvasSize);
      cnv.parent('canvas-container');
      this.noLoop();
      // Listen to changes
      const redrawAll = () => this.redraw();
      document.querySelectorAll('#input-a,#input-b,#slider-c,#checkbox-mode,#checkbox-display')
        .forEach(el=>el.addEventListener('change',redrawAll));
      document.querySelectorAll('#input-a,#input-b,#slider-c')
        .forEach(el=>el.addEventListener('input',redrawAll));
    }
    draw(){
      const inputA = document.getElementById('input-a');
      const inputB = document.getElementById('input-b');
      const sliderC = document.getElementById('slider-c');
      const checkboxMode = document.getElementById('checkbox-mode');
      const checkboxDisplay = document.getElementById('checkbox-display');
      const equationText = document.getElementById('equation-text');
      const a = Number(inputA.value);
      const b = Number(inputB.value);
      const ctx = this;
      ctx.clearCanvas();
      ctx.push();
      ctx.translate(margin, canvasSize - margin);
      ctx.scale(1, -1);
      ctx.strokeWeight(1);
      ctx.stroke(color('#000'));
      // Grid and axes
      const xScale = (canvasSize - 2*margin)/b;
      const yScale = (canvasSize - 2*margin)/a;
      ctx.textSize(12);
      // Axes
      ctx.line(0,0,b*xScale,0);
      ctx.line(0,0,0,a*yScale);
      // Axis labels
      ctx.fill(color('#00f'));
      for(let i=0;i<=b;i++){ctx.text(i, i*xScale-5, -5);}
      for(let i=0;i<=a;i++){ctx.text(i, -10, i*yScale-3);}
      // Coprime check
      const isCoprime = (function(a,b){while(b){[a,b]=[b,a%b];}return a===1;})(a,b);
      if(!isCoprime){
        ctx.pop();return;
      }
      // Compute c value
      let c;
      if(checkboxDisplay.checked){
        c = a*b - a - b;
      } else {
        c = Number(sliderC.value);
      }
      // Dot or numbers mode
      if(checkboxMode.checked){
        // Dots mode
        ctx.fill(color('#000'));
        const dotSize=4;
        for(let x=0;x<=b;x++){
          for(let y=0;y<=a;y++){
            ctx.ellipse(x*xScale,y*yScale,dotSize,dotSize);
            if( a*x + b*y === c ){
              ctx.fill(color('#0f0')); ctx.ellipse(x*xScale,y*yScale,dotSize*2,dotSize*2);
              ctx.fill(color('#000'));
            }
          }
        }
        // Draw line
        const x0 = (c - 0)/a; const y0 = (c - 0)/b;
        let p1 = null,p2=null;
        if(x0>=0 && x0<=b){p1=[x0,y0];}
        if(y0>=0 && y0<=a){p2=[x0,y0];}
        // Intersections at y=0 or x=0
        const yAtX0 = c/b;
        if(yAtX0>=0 && yAtX0<=a) p1=[0,yAtX0];
        const xAtY0 = c/a;
        if(xAtY0>=0 && xAtY0<=b) p2=[xAtY0,0];
        if(p1 && p2){
          ctx.strokeWeight(2);
          ctx.stroke(color('#f00'));
          ctx.line(p1[0]*xScale,p1[1]*yScale,p2[0]*xScale,p2[1]*yScale);
          ctx.strokeWeight(1);
        }
      } else {
        // Numbers mode
        const maxVal = a*b; // maximum value reachable in grid
        for(let x=0;x<=b;x++){
          for(let y=0;y<=a;y++){
            const v = a*x + b*y;
            ctx.textSize(10);
            ctx.fill(color('#000'));
            ctx.text(v, x*xScale-5, y*yScale-3);
            if(v===c){ctx.fill(color('#0f0')); ctx.text(v, x*xScale-5, y*yScale-3);}
            else if(checkboxDisplay.checked){
              // Highlight unsolvable numbers
              const solvable = (function(v,a,b){
                for(let vx=0;vx<=Math.floor(v/a);vx++){
                  if((v - a*vx)%b===0) return true;
                }
                return false;
              })(v,a,b);
              if(!solvable){ctx.fill(color('#f00')); ctx.text(v, x*xScale-5, y*yScale-3);}
            }
          }
        }
      }
      ctx.pop();
    }
  }
  new p5Sketch();
})();
</script>
</head>
