<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.6.0/p5.min.js"></script>
<script>
  // ----- Tree Data -----
  const nodeData = [
    { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
    { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
    { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
    { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
    { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
    { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
    { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
    { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
    { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
    { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
    { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
    { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
    { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
    { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
    { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
  ];
  const nodeMap = {};
  nodeData.forEach(n => { nodeMap[n.id] = n; });

  // ----- State Variables -----
  let currentTier = 5;
  let showBinary = false;
  let showNodeChars = true;

  let p5Sketch = null;

  // ----- P5.js Sketch -----
  function setupCanvas() {
    p5Sketch = new p5(function(p){
      p.setup = function() {
        const canvas = p.createCanvas(800,500);
        canvas.parent('canvas-area');
        p.textFont('Arial');
        p.textSize(12);
        p.textAlign(p.CENTER, p.CENTER);
        p.noLoop();
      };
      p.draw = function() {
        p.clear();
        // Render edges first
        nodeData.forEach(node => {
          if (node.tier < currentTier && node.children.length > 0) {
            node.children.forEach((childId, idx) => {
              const child = nodeMap[childId];
              if (child.tier <= currentTier) {
                p.stroke('#C70000');
                p.strokeWeight(2);
                p.line(node.pos.x, node.pos.y, child.pos.x, child.pos.y);
                if (showBinary) {
                  const bit = idx === 0 ? '0' : '1';
                  const midx = (node.pos.x + child.pos.x) / 2;
                  const midy = (node.pos.y + child.pos.y) / 2;
                  p.noStroke();
                  p.fill('#000');
                  p.text(bit, midx, midy - 5);
                }
              }
            });
          }
        });

        // Render nodes
        nodeData.forEach(node => {
          if (node.tier <= currentTier) {
            const isLeaf = node.tier === 1;
            let label = '';
            if (isLeaf) {
              label = `${node.label}(${node.freq})`;
            } else {
              if (showNodeChars) {
                label = `${node.label}(${node.freq})`;
              } else {
                label = `(${node.freq})`;
              }
            }
            const w = p.textWidth(label) + 20;
            const h = 24;
            p.fill('#FEFDD5');
            p.stroke('#C70000');
            p.rect(node.pos.x - w / 2, node.pos.y - h / 2, w, h, 8);
            p.fill('#000');
            p.noStroke();
            p.text(label, node.pos.x, node.pos.y);
          }
        });
      };
    }, 'canvas-area');
  }

  // ----- UI Updates -----
  function highlightTierButton() {
    for (let i = 1; i <= 5; i++) {
      const btn = document.getElementById(`btn-tier-${i}`);
      if (currentTier === i) btn.classList.add('active');
      else btn.classList.remove('active');
    }
  }

  function updateExplanation() {
    const ex = document.getElementById('explanation-text');
    if (currentTier === 1) {
      ex.innerText = 'After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.';
    } else if (currentTier >= 2 && currentTier <= 4) {
      ex.innerText = 'We continue grouping until we get to the highest tier.';
    } else if (currentTier === 5) {
      ex.innerText = 'Once at the top, the total frequency becomes 17, which is the total number of characters.';
    }
  }

  function updateEncodingTable() {
    const tableDiv = document.getElementById('encoding-table');
    const mapping = [
      { char: 'I', code: '00' },
      { char: 'S', code: '01' },
      { char: 'P', code: '100' },
      { char: 'R', code: '101' },
      { char: 'M', code: '1100' },
      { char: 'V', code: '1101' },
      { char: 'E', code: '1110' },
      { char: '_', code: '1111' }
    ];
    if (currentTier >= 2) {
      const html = mapping.map(m => `<p>${m.char} = ${m.code}</p>`).join('');
      tableDiv.innerHTML = html;
      tableDiv.style.display = 'block';
    } else {
      tableDiv.style.display = 'none';
    }
  }

  function updateEncodedText() {
    const encDiv = document.getElementById('encoded-text');
    const encodedStr = '1100000101000101001001000011111010011011110101';
    if (currentTier === 5) {
      encDiv.innerText = encodedStr;
      encDiv.style.display = 'block';
    } else {
      encDiv.style.display = 'none';
    }
  }

  function updateUI() {
    highlightTierButton();
    updateExplanation();
    updateEncodingTable();
    updateEncodedText();
    if (p5Sketch) p5Sketch.redraw();
  }

  // ----- Event Listeners -----
  document.addEventListener('DOMContentLoaded', () => {
    setupCanvas();

    // Tier buttons
    for (let i = 1; i <= 5; i++) {
      const btn = document.getElementById(`btn-tier-${i}`);
      btn.addEventListener('click', () => {
        currentTier = i;
        updateUI();
      });
    }

    // Binary visibility
    const chk = document.getElementById('checkbox-binary');
    chk.checked = showBinary;
    chk.addEventListener('change', () => {
      showBinary = chk.checked;
      updateUI();
    });

    // Characters radio
    const radioYes = document.getElementById('radio-chars-yes');
    radioYes.checked = showNodeChars;
    radioYes.addEventListener('change', () => {
      showNodeChars = radioYes.checked;
      updateUI();
    });

    updateUI();
  });
</script>
</body>
</html>
