<!-- MathJax -->
<script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

<!-- p5.js -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

<script>
(() => {
  // Global n
  let n = 1;

  // Color array
  const colors = ['#ffff80','#98fb98','#7fffd4','#00ffff','#87cefa','#6495ed','#9370db','#8a2be2','#ffb6c1','#ff69b4','#ff1493','#c71585','#ba55d3','#9370db','#8a2be2','#4b0082','#6a5acd','#7b68ee','#1e90ff','#00bfff','#add8e6','#87ceeb'];

  // Helper: coordinate conversion
  const mapX = (x, xMin, xMax, w) => ( (x - xMin)/(xMax - xMin) ) * w;
  const mapY = (y, yMin, yMax, h) => h - ( (y - yMin)/(yMax - yMin) ) * h; // invert y

  // Get canvas containers
  const funcDiv = document.getElementById('function-plot-container');
  const sumDiv = document.getElementById('sum-plot-container');

  // Create Function Plot sketch
  const functionSketch = new p5((p) => {
    let canvas;
    p.setup = () => {
      const w = funcDiv.clientWidth;
      const h = funcDiv.clientHeight;
      canvas = p.createCanvas(w, h);
      canvas.parent(funcDiv);
      canvas.id('canvas-function');
      p.noLoop();
    };

    p.draw = () => {
      p.background(255);
      const w = p.width;
      const h = p.height;
      const xMin=0, xMax=11, yMin=0, yMax=2.2;
      // Axes
      p.stroke(0);
      p.line(mapX(xMin,xMin,xMax,w), mapY(yMin,yMin,yMax,h), mapX(xMax,xMin,xMax,w), mapY(yMin,yMin,yMax,h)); // X
      p.line(mapX(xMin,xMin,xMax,w), mapY(yMin,yMin,yMax,h), mapX(xMin,xMin,xMax,w), mapY(yMax,yMin,yMax,h)); // Y
      // Ticks and labels
      p.textAlign(p.RIGHT, p.CENTER);
      for(let ix=0; ix<=10; ix++){
        let px=mapX(ix,xMin,xMax,w);
        p.line(px, mapY(yMin-0.05,yMin,yMax,h), px, mapY(yMin+0.05,yMin,yMax,h));
        if(ix!==0) p.text(ix, px-5, mapY(yMin-0.2,yMin,yMax,h));
      }
      p.textAlign(p.CENTER, p.BOTTOM);
      for(let iy=0; iy<=4; iy++){
        let py=mapY(iy*0.5,yMin,yMax,h);
        p.line(mapX(xMin-0.05,xMin,xMax,w), py, mapX(xMin+0.05,xMin,xMax,w), py);
        if(iy>0) p.text(iy*0.5, mapX(xMin-0.2,xMin,xMax,w), py+5);
      }
      // Plot function y=2/x for x>0.5
      p.stroke(0,0,255);
      p.strokeWeight(2);
      p.noFill();
      p.beginShape();
      for(let tx=0.5; tx<=10; tx+=0.01){
        const y=2/tx;
        p.vertex(mapX(tx,xMin,xMax,w), mapY(y,yMin,yMax,h));
      }
      p.endShape();
      // Rectangles
      for(let k=1; k<=n; k++){
        const color = colors[(k-1)%colors.length];
        const x1=k, y1=2/(k+1);
        const x2=k+1, y2=2/k;
        const xPx1=mapX(x1,xMin,xMax,w), yPy1=mapY(y1,yMin,yMax,h);
        const xPx2=mapX(x2,xMin,xMax,w), yPy2=mapY(y2,yMin,yMax,h);
        p.noStroke();
        p.fill(color);
        p.rect(xPx1, yPy1, xPx2-xPx1, yPy2-yPy1);
        // For k<=3: dashed line from (k,0) to (k,2/k)
        if(k<=3){
          p.stroke(128);
          p.strokeWeight(1);
          p.drawingContext.setLineDash([5,5]);
          p.line(mapX(k,xMin,xMax,w), mapY(0,yMin,yMax,h), mapX(k,xMin,xMax,w), mapY(2/k,yMin,yMax,h));
          p.drawingContext.setLineDash([]);
          p.noStroke();
        }
      }
    };
  }, funcDiv);

  // Create Sum Plot sketch
  const sumSketch = new p5((p) => {
    let canvas;
    p.setup = () => {
      const w = sumDiv.clientWidth;
      const h = sumDiv.clientHeight;
      canvas = p.createCanvas(w, h);
      canvas.parent(sumDiv);
      canvas.id('canvas-sum');
      p.noLoop();
    };

    p.draw = () => {
      p.background(255);
      const w = p.width;
      const h = p.height;
      const xMin=0, xMax=1.5, yMin=0, yMax=2.2;
      // Axes
      p.stroke(0);
      p.line(mapX(xMin,xMin,xMax,w), mapY(yMin,yMin,yMax,h), mapX(xMax,xMin,xMax,w), mapY(yMin,yMin,yMax,h)); // X
      p.line(mapX(xMin,xMin,xMax,w), mapY(yMin,yMin,yMax,h), mapX(xMin,xMin,xMax,w), mapY(yMax,yMin,yMax,h)); // Y
      // Ticks and labels
      p.textAlign(p.RIGHT, p.CENTER);
      for(let ix=0; ix<=1; ix++){
        let px=mapX(ix,xMin,xMax,w);
        p.line(px, mapY(yMin-0.05,yMin,yMax,h), px, mapY(yMin+0.05,yMin,yMax,h));
        if(ix!==0) p.text(ix, px-5, mapY(yMin-0.2,yMin,yMax,h));
      }
      p.textAlign(p.CENTER, p.BOTTOM);
      for(let iy=0; iy<=4; iy++){
        let py=mapY(iy*0.5,yMin,yMax,h);
        p.line(mapX(xMin-0.05,xMin,xMax,w), py, mapX(xMin+0.05,xMin,xMax,w), py);
        if(iy>0) p.text(iy*0.5, mapX(xMin-0.2,xMin,xMax,w), py+5);
      }
      // Stacked rectangles
      let y_stack=0;
      for(let k=n; k>=1; k--){
        const h_rect=2/k - 2/(k+1);
        const color=colors[(k-1)%colors.length];
        const y0=mapY(y_stack,yMin,yMax,h);
        const hPx=mapY(y_stack+h_rect,yMin,yMax,h)-y0;
        p.noStroke();
        p.fill(color);
        p.rect(mapX(0,xMin,xMax,w), y0, mapX(1,xMin,xMax,w)-mapX(0,xMin,xMax,w), hPx);
        y_stack+=h_rect;
      }
    };
  }, sumDiv);

  // Sync controls
  const slider = document.getElementById('slider-n');
  const input = document.getElementById('input-n');

  const updateN = val => {
    n = val;
    slider.value = val;
    input.value = val;
    functionSketch.redraw();
    sumSketch.redraw();
    MathJax.typesetPromise(); // Re-typeset in case of dynamic change
  };

  slider.addEventListener('input', () => updateN(parseInt(slider.value)));
  input.addEventListener('input', () => {
    let val = parseInt(input.value);
    if(isNaN(val) || val<1) val=1;
    if(val>20) val=20;
    updateN(val);
  });

  // Initial render
  window.addEventListener('load', () => {
    functionSketch.resizeCanvas();
    sumSketch.resizeCanvas();
    updateN(n);
  });

  // Adjust canvases when window resizes
  window.addEventListener('resize', () => {
    functionSketch.resizeCanvas();
    sumSketch.resizeCanvas();
    functionSketch.redraw();
    sumSketch.redraw();
  });
})();
</script>
</body>
</html>
