<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Biochemical Reactor Demo</title>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<style>
  body {margin:0;font-family:sans-serif;display:flex;height:100vh;overflow:hidden}
  #main-container {display:flex;flex:1}
  #control-panel {width:250px;background:#f8f9fa;padding:10px;box-shadow:2px 0 5px rgba(0,0,0,.1);overflow-y:auto}
  #visualization-area {flex:1;padding:10px}
  .button-group {margin-bottom:15px}
  .button-group button{width:48%;margin-right:2%;padding:5px 0;background:#e9ecef;border:none;border-radius:4px;cursor:pointer}
  .button-group button.active{background:#007bff;color:#fff}
  .slider-container {margin-bottom:12px}
  .slider-container label{display:block;margin-bottom:3px}
  .slider-container input{width:100%}
  .slider-container span{display:block;text-align:right;font-weight:bold}
  #plot-container{width:100%;height:100%}
</style>
</head>
<body>

<div id="main-container">
  <div id="control-panel">
    <div class="button-group">
      <button id="btn-monod" class="active">Monod</button>
      <button id="btn-si">substrate inhibition</button>
    </div>
    <div class="button-group">
      <button id="btn-bifurcation" class="active">bifurcation</button>
      <button id="btn-nontrivial">nontrivial steady state</button>
    </div>
    <div class="slider-container" id="container-x2f">
      <label for="slider-x2f">x<sub>2f</sub></label>
      <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4">
      <span id="value-x2f">4.0</span>
    </div>
    <div class="slider-container" id="container-umax">
      <label for="slider-umax">μ<sub>max</sub></label>
      <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
      <span id="value-umax">0.60</span>
    </div>
    <div class="slider-container" id="container-km">
      <label for="slider-km">k<sub>m</sub></label>
      <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
      <span id="value-km">0.12</span>
    </div>
    <div class="slider-container" id="container-k1" style="display:none">
      <label for="slider-k1">k<sub>1</sub></label>
      <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.45">
      <span id="value-k1">0.45</span>
    </div>
    <div class="slider-container" id="container-ds">
      <label for="slider-ds">D<sub>s</sub></label>
      <input type="range" id="slider-ds" min="0.01" max="1" step="0.01" value="0.6">
      <span id="value-ds">0.60</span>
    </div>
  </div>

  <div id="visualization-area">
    <div id="plot-container"></div>
  </div>
</div>

<script>
/* Global State */
let currentModel = 'monod';
let currentView = 'bifurcation';

/* Parameter Values */
const params = {
  x2f: 4.0,
  umax: 0.6,
  km: 0.12,
  k1: 0.45,
  ds: 0.6
};

/* Helper Functions */
function updateSliderDisplays() {
  document.getElementById('value-x2f').textContent = params.x2f.toFixed(1);
  document.getElementById('value-umax').textContent = params.umax.toFixed(2);
  document.getElementById('value-km').textContent = params.km.toFixed(2);
  document.getElementById('value-k1').textContent = params.k1.toFixed(4);
  document.getElementById('value-ds').textContent = params.ds.toFixed(2);
}

function calcD(x2) {
  if (currentModel === 'monod') {
    return params.umax * x2 / (params.km + x2);
  } else {
    return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
  }
}

function derivativeD(x2) {
  // Analytical derivative
  if (currentModel === 'monod') {
    return params.umax * params.km / Math.pow(params.km + x2, 2);
  } else {
    const denom = params.km + x2 + params.k1 * x2 * x2;
    const num = params.km - params.k1 * x2 * x2; // km - k1*x^2
    return params.umax * num / Math.pow(denom, 2);
  }
}

function findRoots(func, a, b, step=0.01) {
  const roots = [];
  let prevX = a;
  let prevY = func(a);
  for (let x = a + step; x <= b; x += step) {
    const y = func(x);
    if (prevY === 0) roots.push(prevX);
    if (prevY * y < 0) {
      // Linear interpolation
      const root = prevX - prevY * (x - prevX) / (y - prevY);
      roots.push(root);
    }
    prevX = x; prevY = y;
  }
  return roots;
}

/* Plotting Functions */
function drawBifurcationDiagram() {
  const xVals = [];
  const dVals = [];
  const x2Crit = Math.sqrt(params.km / params.k1);
  const stableX2 = [];
  const stableD = [];
  const unstableX2 = [];
  const unstableD = [];
  for (let x2 = 0; x2 <= params.x2f; x2 += 0.01) {
    const d = calcD(x2);
    xVals.push(x2);
    dVals.push(d);
    if (currentModel === 'monod' || x2 <= x2Crit) {
      stableX2.push(x2);
      stableD.push(d);
    } else {
      unstableX2.push(x2);
      unstableD.push(d);
    }
  }

  /* Nontrivial curve traces */
  const traces = [];
  traces.push({
    x: stableX2,
    y: stableD,
    mode: 'lines',
    name: 'Nontrivial (stable)',
    line: {color: 'blue', width: 2}
  });
  if (unstableX2.length) {
    traces.push({
      x: unstableX2,
      y: unstableD,
      mode: 'lines',
      name: 'Nontrivial (unstable)',
      line: {color: 'red', width: 2, dash: 'dash'}
    });
  }

  /* Trivial line y = x2f */
  const D_bif = calcD(params.x2f);
  const trivialX = [0, D_bif, 1.0];
  const trivialY = [params.x2f, params.x2f, params.x2f];
  traces.push({
    x: trivialX.slice(0,2),
    y: trivialY.slice(0,2),
    mode: 'lines',
    name: 'Trivial (unstable)',
    line: {color: 'red', width: 2, dash: 'dash'}
  });
  traces.push({
    x: trivialX.slice(1),
    y: trivialY.slice(1),
    mode: 'lines',
    name: 'Trivial (stable)',
    line: {color: 'blue', width: 2}
  });

  /* Intersection points D = D_s */
  const intersectionX2 = findRoots(x2 => calcD(x2) - params.ds, 0.001, params.x2f);
  const intersectionPoints = {x: [], y: [], mode: 'markers', marker: {size: 8}};
  const colors = [];
  intersectionX2.forEach(x2 => {
    const d = calcD(x2);
    intersectionPoints.x.push(params.ds);
    intersectionPoints.y.push(x2);
    const stable = derivativeD(x2) > 0;
    colors.push(stable ? 'magenta' : 'cyan');
  });
  // Add trivial intersection
  intersectionPoints.x.push(params.ds);
  intersectionPoints.y.push(params.x2f);
  colors.push('green');
  intersectionPoints.marker.color = colors;

  traces.push(intersectionPoints);

  /* Layout */
  const layout = {
    title: currentModel === 'monod' ? 'Monod Bifurcation Diagram' : 'Substrate Inhibition Bifurcation Diagram',
    xaxis: {title: 'Dilution rate', range: [0,1]},
    yaxis: {title: 'X₂', range: [0,6]},
    shapes: [{
      type: 'line',
      x0: params.ds, y0: 0,
      x1: params.ds, y1: 6,
      line: {color: 'grey', width: 1, dash: 'dot'}
    }]
  };

  Plotly.newPlot('plot-container', traces, layout);
}

function drawNontrivialStatePlot() {
  const x2s = [];
  const muVals = [];
  for (let x2 = 0; x2 <= 7; x2 += 0.01) {
    const mu = currentModel === 'monod'
      ? params.umax * x2 / (params.km + x2)
      : params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
    x2s.push(x2);
    muVals.push(mu);
  }

  const traces = [];
  traces.push({
    x: x2s,
    y: muVals,
    mode: 'lines',
    name: 'μ(x₂)',
    line: {color: 'blue', width: 2}
  });

  /* Horizontal lines */
  traces.push({
    x: [0,7],
    y: [params.ds, params.ds],
    mode: 'lines',
    name: 'Dₛ',
    line: {color: 'grey', width: 2}
  });

  /* Bistability region */
  const muPeak = params.umax / (1 + 2 * Math.sqrt(params.k1 * params.km));
  const D_upper = muPeak;
  const D_lower = currentModel === 'monod'
    ? params.umax * params.x2f / (params.km + params.x2f) // For monod this is trivial
    : params.umax * params.x2f / (params.km + params.x2f + params.k1 * params.x2f * params.x2f);
  // Draw shaded rectangle
  const shapes = [{
    type: 'rect',
    x0: 0, y0: D_lower,
    x1: 7, y1: D_upper,
    fillcolor: 'rgba(255,165,0,0.3)',
    line: {width: 0}
  }];

  /* Intersection points */
  const interX2 = findRoots(x2 => {
    const mu = currentModel === 'monod'
      ? params.umax * x2 / (params.km + x2)
      : params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
    return mu - params.ds;
  }, 0.001, 7);
  const interPoints = {x: [], y: [], mode: 'markers', marker: {size: 8}};
  const colors = [];
  interX2.forEach(x2 => {
    const mu = currentModel === 'monod'
      ? params.umax * x2 / (params.km + x2)
      : params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
    interPoints.x.push(x2);
    interPoints.y.push(mu);
    // Determine stable (ascending part vs descending)
    const muPrime = evaluationDerivative(x2);
    colors.push(muPrime > 0 ? 'magenta' : 'cyan');
  });
  interPoints.marker.color = colors;
  traces.push(interPoints);

  const layout = {
    title: currentModel === 'monod' ? 'Monod Nontrivial Steady State' : 'Substrate Inhibition Nontrivial Steady State',
    xaxis: {title: 'Substrate concentration', range: [0,7]},
    yaxis: {title: 'Specific growth rate', range: [0,1]},
    shapes: shapes
  };

  Plotly.newPlot('plot-container', traces, layout);
}

function evaluationDerivative(x2) {
  // derivative of μ wrt x2 same as derivative of D
  if (currentModel === 'monod') {
    return params.umax * params.km / Math.pow(params.km + x2, 2);
  } else {
    const denom = params.km + x2 + params.k1 * x2 * x2;
    const num = params.km - params.k1 * x2 * x2;
    return params.umax * num / Math.pow(denom, 2);
  }
}

function updatePlot() {
  if (currentView === 'bifurcation') {
    drawBifurcationDiagram();
  } else {
    if (currentModel !== 'si') {
      // For consistency, show a message or fallback
      Plotly.newPlot('plot-container', [], {
        title: 'Nontrivial steady state plot is defined for Substrate Inhibition model only.',
