<!-- Libraries -->
<script src="https://cdn.jsdelivr.net/npm/three@0.158.0/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.158.0/examples/js/controls/OrbitControls.js"></script>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

<script>
(() => {
  // Element references
  const sliderSeason = document.getElementById('slider-season');
  const sliderLatitude = document.getElementById('slider-latitude');
  const sliderTime = document.getElementById('slider-time');
  const spanSeason = document.getElementById('value-season');
  const spanLatitude = document.getElementById('value-latitude');
  const spanTime = document.getElementById('value-time');
  const globeContainer = document.getElementById('globe-container');
  const plotInstant = document.getElementById('plot-instant');
  const plotCumulative = document.getElementById('plot-cumulative');

  // Three.js objects
  let scene, camera, renderer, controls;
  let earth, surfaceNormalArrow, sunArrow, polarAxisArrow, selectedLatitudeLine, equatorLine;
  let directionalLight, ambientLight;
  let frameId;

  // Init
  init();
  function init() {
    // Three.js scene
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera( 45, globeContainer.clientWidth / globeContainer.clientHeight, 0.1, 100 );
    camera.position.set(0, 0, 3);
    renderer = new THREE.WebGLRenderer( { antialias: true } );
    renderer.setSize( globeContainer.clientWidth, globeContainer.clientHeight );
    globeContainer.appendChild( renderer.domElement );

    // Controls
    controls = new THREE.OrbitControls( camera, renderer.domElement );
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;

    // Lights
    directionalLight = new THREE.DirectionalLight( 0xffffff, 1 );
    directionalLight.position.set(0, 1, 0);
    directionalLight.target.position.set(0,0,0);
    scene.add(directionalLight);
    scene.add(directionalLight.target);
    ambientLight = new THREE.AmbientLight( 0x222222 );
    scene.add(ambientLight);

    // Earth sphere with procedural texture
    const radius = 1;
    const widthSegments = 64;
    const heightSegments = 64;
    const geometry = new THREE.SphereGeometry( radius, widthSegments, heightSegments );
    const texture = generateEarthTexture();
    const material = new THREE.MeshPhongMaterial( { map: texture } );
    earth = new THREE.Mesh( geometry, material );
    scene.add( earth );

    // Graticule (wireframe)
    const wireframeGeo = new THREE.WireframeGeometry( geometry );
    const wireframeMat = new THREE.LineBasicMaterial( { color: 0xffffff, opacity: 0.2, transparent: true } );
    const wireframe = new THREE.LineSegments( wireframeGeo, wireframeMat );
    scene.add( wireframe );

    // Equator line
    equatorLine = createLatitudeLine( 0, 0xffff00 ); // yellow
    scene.add( equatorLine );

    // Arrows (polar axis)
    polarAxisArrow = new THREE.ArrowHelper(
      new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0), 0.6, 0x00ff00, 0.1, 0.05 );
    scene.add( polarAxisArrow );

    // Setup plots
    setupPlots();

    // Add event listeners
    sliderSeason.addEventListener('input', update);
    sliderLatitude.addEventListener('input', update);
    sliderTime.addEventListener('input', update);

    // Window resize
    window.addEventListener('resize', onWindowResize, false);

    // Initial render
    update();
    animate();
  }

  function animate() {
    frameId = requestAnimationFrame( animate );
    controls.update();
    renderer.render( scene, camera );
  }

  function onWindowResize() {
    camera.aspect = globeContainer.clientWidth / globeContainer.clientHeight;
    camera.updateProjectionMatrix();
    renderer.setSize( globeContainer.clientWidth, globeContainer.clientHeight );
  }

  function generateEarthTexture() {
    const size = 512;
    const canvas = document.createElement('canvas');
    canvas.width = size;
    canvas.height = size;
    const ctx = canvas.getContext('2d');

    // Base blue
    ctx.fillStyle = '#1e90ff';
    ctx.fillRect(0,0,size,size);

    // Very simple continents (green rectangles)
    ctx.fillStyle = '#228B22';
    ctx.beginPath();
    ctx.moveTo(100,200);
    ctx.lineTo(120,300);
    ctx.lineTo(140,280);
    ctx.lineTo(120,220);
    ctx.closePath();
    ctx.fill();

    const texture = new THREE.CanvasTexture(canvas);
    texture.minFilter = THREE.LinearFilter;
    return texture;
  }

  // Plot setup
  function setupPlots() {
    const hours = Array.from({length: 25}, (_,i)=>i);
    const layoutInstant = {
      title: 'Instantaneous Solar Energy',
      xaxis: {title:'time (hr)', range:[0,24]},
      yaxis: {title:'Watts/m²', range:[0,1400]},
      margin:{t:50}
    };
    const dataInstant = [
      {x:hours, y:Array(25).fill(0), mode:'lines', name:'Equator', line:{color:'red'}},
      {x:hours, y:Array(25).fill(0), mode:'lines', name:'Selected Latitude', line:{color:'black'}},
      {x:hours, y:Array(25).fill(0), mode:'lines', name:'North Pole', line:{color:'blue'}},
      {x:[12], y:[0], mode:'markers', marker:{color:'black', size:8}, showlegend:false}
    ];
    Plotly.newPlot(plotInstant, dataInstant, layoutInstant);

    const layoutCumulative = {
      title: 'Cumulative Solar Energy',
      xaxis: {title:'time (hr)', range:[0,24]},
      yaxis: {title:'Watts/m²', range:[0,14000]},
      margin:{t:50}
    };
    const dataCumulative = [
      {x:hours, y:Array(25).fill(0), mode:'lines', name:'Equator', line:{color:'red'}},
      {x:hours, y:Array(25).fill(0), mode:'lines', name:'Selected Latitude', line:{color:'black'}},
      {x:hours, y:Array(25).fill(0), mode:'lines', name:'North Pole', line:{color:'blue'}}
    ];
    Plotly.newPlot(plotCumulative, dataCumulative, layoutCumulative);
  }

  // Create a latitude circle line
  function createLatitudeLine(latDeg, colorHex) {
    const radius = 1;
    const segments = 64;
    const latRad = THREE.MathUtils.degToRad(latDeg);
    const circle = new THREE.CircleGeometry( radius, segments );
    circle.applyMatrix4( new THREE.Matrix4().makeTranslation(0,0,0) ); // keep centered
    const material = new THREE.LineBasicMaterial( { color: colorHex, linewidth:2 } );
    const line = new THREE.Line( circle, material );
    line.rotation.x = latRad;
    return line;
  }

  // Main update function
  function update() {
    // Read slider values
    const season = parseFloat(sliderSeason.value);
    const latitude = parseFloat(sliderLatitude.value);
    const time = parseFloat(sliderTime.value);

    // Update spans
    spanSeason.textContent = season.toFixed(3);
    spanLatitude.textContent = latitude.toFixed(2);
    spanTime.textContent = time.toFixed(2);

    // Solar Declination
    const deltaDeg = 23.5 * Math.cos( 2 * Math.PI * season );
    const deltaRad = THREE.MathUtils.degToRad(deltaDeg);

    // Rotate Earth
    earth.rotation.y = (time / 24) * 2 * Math.PI;

    // Update directional light (sun)
    const sunDir = new THREE.Vector3(0, Math.sin(deltaRad), -Math.cos(deltaRad));
    directionalLight.position.copy( sunDir.clone().negate() ); // point from light to origin
    directionalLight.position.multiplyScalar(10); // distance from Earth
    directionalLight.target.updateMatrixWorld();

    // Update selected latitude line
    if (selectedLatitudeLine) scene.remove(selectedLatitudeLine);
    selectedLatitudeLine = createLatitudeLine( latitude, 0x0000ff ); // blue dashed
    selectedLatitudeLine.material = new THREE.LineDashedMaterial({
      color: 0x0000ff,
      linewidth:2,
      dashSize:0.1,
      gapSize:0.1
    });
    selectedLatitudeLine.computeLineDistances();
    scene.add( selectedLatitudeLine );

    // Update surface normal arrow
    if (surfaceNormalArrow) scene.remove(surfaceNormalArrow);
    const latRad = THREE.MathUtils.degToRad(latitude);
    const normalPoint = new THREE.Vector3( Math.cos(latRad), Math.sin(latRad), 0 );
    const normalDir = normalPoint.clone().normalize();
    surfaceNormalArrow = new THREE.ArrowHelper( normalDir, normalPoint, 0.3, 0x000000, 0.1, 0.05 );
    scene.add( surfaceNormalArrow );

    // Update sun direction arrow
    if (sunArrow) scene.remove(sunArrow);
    sunArrow = new THREE.ArrowHelper( sunDir.clone().normalize(), normalPoint, 0.3, 0xff0000, 0.1, 0.05 );
    scene.add( sunArrow );

    // Compute plot data
    const hours = Array.from({length:25},(_,i)=>i);
    const energyEquator = computeEnergyArray(0, deltaDeg, deltaRad, timesToHours(hours));
    const energyLat = computeEnergyArray(latitude, deltaDeg, deltaRad, timesToHours(hours));
    const energyNorth = computeEnergyArray(90, deltaDeg, deltaRad, timesToHours(hours));

    const cumulativeEquator = cumulativeArray(energyEquator);
    const cumulativeLat = cumulativeArray(energyLat);
    const cumulativeNorth = cumulativeArray(energyNorth);

    // Update instantaneous plot
    const instantData = [
      {x:hours, y:energyEquator, mode:'lines', name:'Equator', line:{color:'red'}},
      {x:hours, y:energyLat, mode:'lines', name:'Selected Latitude', line:{color:'black'}},
      {x:hours, y:energyNorth, mode:'lines', name:'North Pole', line:{color:'blue'}},
      {
        x:[time],
        y:[computeInstantEnergy(latitude, deltaDeg, time)],
        mode:'markers',
        marker:{color:'black', size:8},
        showlegend:false
      }
    ];
    Plotly.react(plotInstant, instantData, {layout:{xaxis:{range:[0,24]},yaxis:{range:[0,1400]}}});

    // Update cumulative plot
    const cumulativeData = [
      {x:hours, y:cumulativeEquator, mode:'lines', name:'Equator', line:{color:'red'}},
      {x:hours, y:cumulativeLat, mode:'lines', name:'Selected Latitude', line:{color:'black'}},
      {x:hours, y:cumulativeNorth, mode:'lines', name:'North Pole', line:{color:'blue'}},
    ];
    Plotly.react(plotCumulative, cumulativeData, {layout:{xaxis:{range:[0,24]},yaxis:{range:[0,14000]}}});
  }

  // Helper to compute array of hours times (in hours)
  function timesToHours(hoursArray) {
    return hoursArray.map(h=>h);
  }

  // Compute instantaneous energy array for given latitude
  function computeEnergyArray(latDeg, deltaDeg, deltaRad, hours) {
    const latRad = THREE.MathUtils.degToRad(latDeg);
    return hours.map(h=>{
      const HRAdeg = 15*(h-12);
      const HRArad = THREE.MathUtils.degToRad(HRAdeg);
      const val = Math.sin(latRad)*Math.sin(deltaRad) + Math.cos(latRad)*Math.cos(deltaRad)*Math.cos(HRArad);
      const energy = 1361 * Math.max(0, val);
      return energy;
    });
  }

  // Compute instantaneous energy at arbitrary time
  function computeInstantEnergy(latDeg, deltaDeg, time) {
    const latRad = THREE.MathUtils.degToRad(latDeg);
    const deltaRad = THREE.MathUtils.degToRad(deltaDeg);
    const HRAdeg = 15*(time-12);
    const HRArad = THREE.MathUtils.degToRad(HRAdeg);
    const val = Math.sin(latRad)*Math.sin(deltaRad) + Math.cos(latRad)*Math.cos(deltaRad)*Math.cos(HRArad);
    const energy = 1361 * Math.max(0, val);
    return energy;
  }

  // Cumulative energy via trapezoidal integration
  function cumulativeArray(instArray) {
    const cum = [0];
    for(let i=1;i<instArray.length;i++){
      const prev = instArray[i-1];
      const curr = instArray[i];
      const delta = (prev+curr)/2; // hour step =1
      cum.push( cum[i-1] + delta );
    }
    return cum;
  }
})();
</script>
</body>
</html>
