<!-- p5.js library -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<script>
/* Congruent data for selected numbers (some of them). Add more as needed. */
const congruentData = {
 5:  { a:{n:3, d:2},   b:{n:20, d:3},   c:{n:41, d:6} },
 6:  { a:{n:3, d:1},   b:{n:4, d:1},   c:{n:5, d:1} },
 7:  { a:{n:35, d:12}, b:{n:24, d:5},   c:{n:337, d:60} },
 13: { a:{n:780, d:323}, b:{n:323, d:30}, c:{n:106921, d:9690} },
 79: { a:{n:233126551, d:167973000}, b:{n:335946000, d:2950969}, c:{n:56434050774922081, d:495683115837000} }
};

/* Formatting num with spaces as thousand separator */
function formatNumber(num) {
  return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
}

/* Helper to draw a fraction at (x,y) with optional rotation */
function drawFraction(frac, x, y, rotation=0, fontSize=18) {
  push();
  translate(x, y);
  rotate(rotation);
  noStroke();
  fill('#8B5A2B');
  textAlign(CENTER);
  textSize(fontSize);
  const numStr = formatNumber(frac.n);
  const denStr = formatNumber(frac.d);
  const w1 = textWidth(numStr);
  const w2 = textWidth(denStr);
  const w = Math.max(w1, w2);
  const h = fontSize * 1.2; // approximate line height

  // Numerator
  text(numStr, 0, -h/3);
  // Divider line
  line(-w/2, 0, w/2, 0);
  // Denominator
  text(denStr, 0, h/3);
  pop();
}

/* P5.js sketch */
function setup() {
  const cnv = createCanvas(500,400);
  cnv.parent('canvas-container');
  noLoop();
  // Add event listeners for controls
  const slider = document.getElementById('slider-area');
  const input  = document.getElementById('input-area');

  slider.addEventListener('input', () => {
    input.value = slider.value;
    redrawing();
  });
  input.addEventListener('input', () => {
    let val = parseInt(input.value);
    if(val < 1) val = 1;
    if(val > 101) val = 101;
    slider.value = val;
    input.value = val;
    redrawing();
  });

  // Initial draw
  redrawing();
}

function redrawing() {
  const n = parseInt(document.getElementById('input-area').value);
  clear();
  if(congruentData[n]) {
    const {a,b,c} = congruentData[n];
    const a_val = a.n / a.d;
    const b_val = b.n / b.d;
    const c_val = c.n / c.d;
    // Determine scaling
    const padding = 40;
    const scale = Math.min((width - padding)/a_val, (height - padding)/b_val);
    const a_scaled = a_val * scale;
    const b_scaled = b_val * scale;
    // Centering offsets
    const x0 = (width - a_scaled)/2;
    const y0 = (height + b_scaled)/2; // bottom-left corner (y increases down)
    // Triangle vertices
    const p1 = {x: x0, y: y0 - b_scaled}; // top-left
    const p2 = {x: x0 + a_scaled, y: y0};   // bottom-right
    const p3 = {x: x0, y: y0};              // bottom-left

    // Draw triangle
    fill('#FFF2E5');
    stroke('#000');
    strokeWeight(2);
    beginShape();
      vertex(p1.x, p1.y);
      vertex(p2.x, p2.y);
      vertex(p3.x, p3.y);
    endShape(CLOSE);

    // Draw side labels
    const fontSize = 18;
    // a (base) label below base
    const aX = (p2.x + p3.x)/2;
    const aY = (p2.y + p3.y)/2 + 25;
    drawFraction(a, aX, aY, 0, fontSize);

    // b (height) label to the left of vertical leg
    const bX = p3.x - 25;
    const bY = (p1.y + p3.y)/2;
    drawFraction(b, bX, bY, 0, fontSize);

    // c (hypotenuse) label near midpoint of hypotenuse, rotated
    const midH = {x: (p1.x + p2.x)/2, y: (p1.y + p2.y)/2};
    const angle = atan2(p2.y - p1.y, p2.x - p1.x);
    // Offset outwards
    const normalX = -(p2.y - p1.y);
    const normalY = p2.x - p1.x;
    const len = sqrt(normalX*normalX + normalY*normalY);
    const offX = (normalX/len) * 20;
    const offY = (normalY/len) * 20;
    const cX = midH.x + offX;
    const cY = midH.y + offY;
    drawFraction(c, cX, cY, angle, fontSize);

  } else {
    // Non-congruent case
    noStroke();
    fill('#FF0000');
    textAlign(CENTER, CENTER);
    textSize(20);
    text(`${n} is not a congruent number`, width/2, height/2);
  }
}
</script>
</body>
</html>
