<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/examples/js/controls/OrbitControls.js"></script>

<script>
(() => {
  // ---------- Defaults ----------
  const DEFAULTS = {
    alpha: 0.5, beta: 0.5, w: 1.0, r: 1.0,
    Q: 3.0, C: 4.0
  };

  // ---------- DOM Elements ----------
  const sliders = {
    alpha: document.getElementById('slider-alpha'),
    beta: document.getElementById('slider-beta'),
    w: document.getElementById('slider-w'),
    r: document.getElementById('slider-r'),
    Q: document.getElementById('slider-Q'),
    C: document.getElementById('slider-C')
  };
  const labels = {
    alpha: document.getElementById('label-alpha'),
    beta: document.getElementById('label-beta'),
    w: document.getElementById('label-w'),
    r: document.getElementById('label-r'),
    Q: document.getElementById('label-Q'),
    C: document.getElementById('label-C')
  };
  const outputs = {
    primal: document.getElementById('output-primal'),
    dual: document.getElementById('output-dual')
  };
  const btnReset = document.getElementById('btn-reset');
  const btnQFromDual = document.getElementById('btn-Q-from-dual');
  const btnCFromPrimal = document.getElementById('btn-C-from-primal');

  // ---------- Three.js Containers ----------
  let primal = {}, dual = {};

  // ---------- Utility ----------
  function numFormat(n, p=2) { return Number(n).toFixed(p); }

  // ---------- Update Function ----------
  function update() {
    const alpha = parseFloat(sliders.alpha.value);
    const beta = parseFloat(sliders.beta.value);
    const w = parseFloat(sliders.w.value);
    const r = parseFloat(sliders.r.value);
    const Q_in = parseFloat(sliders.Q.value);
    const C_in = parseFloat(sliders.C.value);

    // Update labels
    labels.alpha.textContent = numFormat(alpha);
    labels.beta.textContent = numFormat(beta);
    labels.w.textContent = numFormat(w);
    labels.r.textContent = numFormat(r);
    labels.Q.textContent = numFormat(Q_in);
    labels.C.textContent = numFormat(C_in);

    // ---------- Primal Solver ----------
    const k = (w * beta) / (r * alpha);
    const x_p = Math.pow(Q_in / Math.pow(k, beta), 1/(alpha+beta));
    const y_p = x_p * k;
    const C_out = w*x_p + r*y_p;

    outputs.primal.textContent = `x = ${numFormat(x_p)}, y = ${numFormat(y_p)}, C = ${numFormat(C_out)}`;

    // ---------- Dual Solver ----------
    const x_d = (C_in * alpha) / (w * (alpha + beta));
    const y_d = (C_in * beta) / (r * (alpha + beta));
    const Q_out = Math.pow(x_d, alpha) * Math.pow(y_d, beta);

    outputs.dual.textContent = `x = ${numFormat(x_d)}, y = ${numFormat(y_d)}, Q = ${numFormat(Q_out)}`;

    // ---------- Render Plots ----------
    drawPrimalPlot(alpha, beta, w, r, Q_in, x_p, y_p, C_out);
    drawDualPlot(alpha, beta, w, r, C_in, x_d, y_d, Q_out);
  }

  // ---------- Buttons ----------
  btnReset.addEventListener('click', () => {
      sliders.alpha.value = DEFAULTS.alpha;
      sliders.beta.value  = DEFAULTS.beta;
      sliders.w.value    = DEFAULTS.w;
      sliders.r.value    = DEFAULTS.r;
      sliders.Q.value    = DEFAULTS.Q;
      sliders.C.value    = DEFAULTS.C;
      update();
  });

  btnCFromPrimal.addEventListener('click', () => {
      const primalOutput = outputs.primal.textContent;
      const C_out = parseFloat(primalOutput.match(/C = ([\d.]+)/)[1]);
      sliders.C.value = C_out.toFixed(2);
      update();
  });

  btnQFromDual.addEventListener('click', () => {
      const dualOutput = outputs.dual.textContent;
      const Q_out = parseFloat(dualOutput.match(/Q = ([\d.]+)/)[1]);
      sliders.Q.value = Q_out.toFixed(2);
      update();
  });

  // ---------- Slider Event ----------
  Object.values(sliders).forEach(slider => {
      slider.addEventListener('input', update);
  });

  // ---------- Three.js Setup ----------
  function initScene(containerId) {
    const canvasWidth = 500, canvasHeight = 500;
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
    camera.position.set(15, 15, 15);
    camera.lookAt(0,0,0);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(canvasWidth, canvasHeight);
    const container = document.getElementById(containerId);
    container.innerHTML = ''; // clear
    container.appendChild(renderer.domElement);
    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    const animate = () => { requestAnimationFrame(animate); renderer.render(scene, camera); };
    animate();
    return { scene, camera, renderer, controls, objects: {} };
  }

  // ---------- Primal Plot ----------
  function drawPrimalPlot(alpha, beta, w, r, Q_val, x_p, y_p, C_out) {
    if (!primal.scene) {
      primal = initScene('plot-primal');
    }
    const s = primal.scene;
    // Remove old objects
    ['cylinder','isocost','line','cube'].forEach(id => {
        if (primal.objects[id]) { s.remove(primal.objects[id]); }
        delete primal.objects[id];
    });

    // Axes helper (simple lines)
    const axisLength=10;
    const axes = new THREE.Group();
    axes.add(new THREE.Line(new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,0,0), new THREE.Vector3(axisLength,0,0)]), new THREE.LineBasicMaterial({color:0xff0000})));
    axes.add(new THREE.Line(new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,0,0), new THREE.Vector3(0,0,axisLength)]), new THREE.LineBasicMaterial({color:0x00ff00})));
    axes.add(new THREE.Line(new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,0,0), new THREE.Vector3(0,axisLength,0)]), new THREE.LineBasicMaterial({color:0x0000ff})));
    s.add(axes);
    primitives = axes;

    // Isoquant curve (orange)
    const isoPoints = [];
    const steps = 200;
    for (let i=0.01;i<10;i+=0.05){
        const yVal = Math.pow(Q_val / Math.pow(i, alpha), 1/beta);
        if (yVal < 10) isoPoints.push(new THREE.Vector3(i, yVal, 0));
    }
    const isoGeom = new THREE.BufferGeometry().setFromPoints(isoPoints);
    const isoMat = new THREE.LineBasicMaterial({color:0xffa500, linewidth:2});
    const isoLine = new THREE.Line(isoGeom, isoMat);
    primal.objects.cylinder = isoLine;
    s.add(isoLine);

    // Isocost plane (grey, semi‐transparent)
    const planeSize = 15;
    const plane = new THREE.PlaneGeometry(planeSize, planeSize);
    const planeMat = new THREE.MeshBasicMaterial({color:0x808080, side:THREE.DoubleSide, opacity:0.3, transparent:true});
    const isocost = new THREE.Mesh(plane, planeMat);
    // Plane equation: w*x + r*z = C_out => normal (w,0,r)
    const normal = new THREE.Vector3(w,0,r).normalize();
    isocost.lookAt(normal);
    isocost.position.set(0, C_out / 2, 0);
    primal.objects.isocost = isocost;
    s.add(isocost);

    // Solution vector (blue line)
    const vecPoints = [new THREE.Vector3(0,0,0), new THREE.Vector3(x_p, y_p, C_out)];
    const vecGeom = new THREE.BufferGeometry().setFromPoints(vecPoints);
    const vecMat = new THREE.LineBasicMaterial({color:0x0000ff, linewidth:3});
    const vecLine = new THREE.Line(vecGeom, vecMat);
    primal.objects.line = vecLine;
    s.add(vecLine);

    // Marker sphere for optimal point
    const sphereGeo = new THREE.SphereGeometry(0.2, 32, 32);
    const sphereMat = new THREE.MeshBasicMaterial({color:0x0000ff});
    const sphere = new THREE.Mesh(sphereGeo, sphereMat);
    sphere.position.set(x_p, y_p, C_out);
    primal.objects.cube = sphere;
    s.add(sphere);
  }

  // ---------- Dual Plot ----------
  function drawDualPlot(alpha, beta, w, r, C_val, x_d, y_d, Q_out) {
    if (!dual.scene) {
      dual = initScene('plot-dual');
    }
    const s = dual.scene;
    // Remove old objects
    ['surface','budget','curve','sphere'].forEach(id => {
        if (dual.objects[id]) { s.remove(dual.objects[id]); }
        delete dual.objects[id];
    });

    // Axes helper
    const axisLength = 10;
    const axes = new THREE.Group();
    axes.add(new THREE.Line(new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,0,0), new THREE.Vector3(axisLength,0,0)]), new THREE.LineBasicMaterial({color:0xff0000})));
    axes.add(new THREE.Line(new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,0,0), new THREE.Vector3(0,0,axisLength)]), new THREE.LineBasicMaterial({color:0x00ff00})));
    axes.add(new THREE.Line(new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,0,0), new THREE.Vector3(0,axisLength,0)]), new THREE.LineBasicMaterial({color:0x0000ff})));
    s.add(axes);

    // Production surface (orange)
    const surfFunc = (u,v, target) => {
      const x = u*10;           // range x
      const z = v*10;           // range y
      const y = Math.pow(x, alpha) * Math.pow(z, beta);
      target.set(x, y, z);
    };
    const paramGeom = new THREE.ParametricGeometry(surfFunc, 50, 50);
    const surfMat = new THREE.MeshLambertMaterial({color:0xffa500, side:THREE.DoubleSide, flatShading:true});
    const surface = new THREE.Mesh(paramGeom, surfMat);
    dual.objects.surface = surface;
    s.add(surface);

    // Budget curtain plane (grey)
    const budgetSize = 15;
    const plane = new THREE.PlaneGeometry(budgetSize, budgetSize);
    const planeMat = new THREE.MeshBasicMaterial({color:0x808080, side:THREE.DoubleSide, opacity:0.3, transparent:true});
    const budget = new THREE.Mesh(plane, planeMat);
    const normal = new THREE.Vector3(w,0,r).normalize();
    budget.lookAt(normal);
    budget.position.set(0, C_val/2, 0);
    dual.objects.budget = budget;
    s.add(budget);

    // Intersection curve (red)
    const curvePoints = [];
    const step = 0.05;
    for (let x = 0.01; x < 10; x += step){
        const yVal = (C_val - w * x) / r;
        if (yVal > 0 && yVal < 10) {
            const q = Math.pow(x, alpha) * Math.pow(yVal, beta);
            curvePoints.push(new THREE.Vector3(x, q, yVal));
        }
    }
    if (curvePoints.length>1){
        const curveGeom = new THREE.BufferGeometry().setFromPoints(curvePoints);
        const curveMat = new THREE.LineBasicMaterial({color:0xff0000, linewidth:2});
        const curveLine = new THREE.Line(curveGeom, curveMat);
        dual.objects.curve = curveLine;
        s.add(curveLine);
    }

    // Optimal point sphere (gray)
    const sphereGeo = new THREE.SphereGeometry(0.2, 32, 32);
    const sphereMat = new THREE.MeshBasicMaterial({color:0x808080});
    const sphere = new THREE.Mesh(sphereGeo, sphereMat);
    sphere.position.set(x_d, Q_out, y_d);
    dual.objects.sphere = sphere;
    s.add(sphere);
  }

  // ---------- Initial Render ----------
  update();
})();
</script>
