<body>
<div id="demo-container">
  <div id="control-panel">
    <div class="control-row">
      <label for="slider-pressure">flash pressure (kPa)</label>
      <input type="range" id="slider-pressure" min="100" max="1000" step="1" value="180">
      <span id="pressure-value">180 kPa</span>
    </div>
    <div class="control-row">
      <label for="slider-heat">heat load (kJ/hr)</label>
      <input type="range" id="slider-heat" min="0" max="10000" step="100" value="0">
      <span id="heat-value">0 kJ/hr</span>
    </div>
  </div>

  <div id="visualization-area"></div>
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
/* Globally accessible data */
let flashParams = {
  pressure: 180,           // kPa
  heat: 0,                 // kJ/hr
  T: 323.2,                // K (initial guess)
  V: 0.5,                  // vapor fraction
  x: [0.25,0.25,0.25,0.25],
  y: [0.25,0.25,0.25,0.25]
};

/* Feed constants */
const F = 10; // kmol/hr
const T_F = 323.2; // K
const P_F = 1000; // kPa
const z = [0.25,0.25,0.25,0.25];
const component = ["n-butane","n-pentane","n-heptane","n-octane"];

const Kconst = {
  "n-butane":   {A:6.80165,   B:1005.157, C:-27.159},
  "n-pentane":  {A:6.86177,   B:1184.249, C:-35.223},
  "n-heptane":  {A:6.90237,   B:1358.59,  C:-51.303},
  "n-octane":   {A:6.92383,   B:1431.149, C:-59.501}
};

const Cp_L = [132,161,225,255];      // kJ/kmol·K
const DeltaH_vap = [22400,25800,31800,34400]; // kJ/kmol

/* Utility: arrowhead */
function drawArrow(x1,y1,x2,y2, color=null){
  const len = 10;
  const angle = Math.atan2(y2-y1, x2-x1);
  if (color) fill(color);
  else fill(0);
  strokeWeight(1);
  line(x1,y1,x2,y2);
  push();
  translate(x2,y2);
  rotate(angle);
  triangle(0,0,-len/2,-len/2,-len/2,len/2);
  pop();
}

/* Root finding helpers */
function bisect(func, a, b, tol=1e-6, maxIter=50){
  let f_a = func(a), f_b = func(b);
  if (f_a * f_b > 0) return (a+b)/2; // no sign change
  for (let i=0; i<maxIter; i++){
    const mid = (a+b)/2;
    const f_mid = func(mid);
    if (Math.abs(f_mid) < tol) return mid;
    if (f_a * f_mid < 0){
      b = mid; f_b = f_mid;
    }else{
      a = mid; f_a = f_mid;
    }
  }
  return (a+b)/2;
}

/* Calculation routine */
function calculateFlash(P_flash, Q_hr){
  const T_min = 200, T_max = 400; // K reasonable range
  const target_T = bisect((T)=>{
    // compute K_i
    const K = KconstList(T,P_flash);
    // solve Rachford-Rice for V
    const V = solVEnergy(T, Q_hr, P_flash);
    const x = z.map( zi => zi/(1+V*(K[component.indexOf(zi)??0]-1)) ); // placeholder
    return null; // placeholder
  }, T_min, T_max);

  // For simplicity, perform only 10 iteration bisection to find T
  const T = bisect((Tguess)=>{
    const K = Klist(Tguess,P_flash);
    // Rachford-Rice for V
    const V = rachfordV(K);
    // compute x and y
    const x = [];
    const y = [];
    for (let i=0;i<4;i++){
      x[i] = z[i]/(1+V*(K[i]-1));
      y[i] = K[i]*x[i];
    }
    // Energy balance residual
    const lhs = Q_hr/F + z.reduce((s,zi,i)=>s+zi*Cp_L[i]*(T_F - Tguess),0);
    const rhs = V * y.reduce((s,yi,i)=>s+yi*DeltaH_vap[i],0);
    return lhs - rhs;
  }, T_min, T_max, 1e-4, 40);

  /* Final inner solve for V at found T */
  const Kfinal = Klist(T,P_flash);
  const Vfinal = rachfordV(Kfinal);

  /* Compute mole fractions */
  const xfinal = [];
  const yfinal = [];
  for (let i=0;i<4;i++){
    xfinal[i] = z[i]/(1+Vfinal*(Kfinal[i]-1));
    yfinal[i] = Kfinal[i]*xfinal[i];
  }

  return {
    T: T,
    V: Vfinal,
    x: xfinal,
    y: yfinal
  };
}

/* Helper to compute K values for given T and P_flash */
function Klist(T, P_flash){
  const K = [];
  for (let i=0;i<4;i++){
    const {A,B,C} = Kconst[component[i]];
    const P_sat = Math.pow(10, A - B/(T + C)); // kPa
    K.push(P_sat / P_flash);
  }
  return K;
}

/* Solve Rachford-Rice for V via bisection */
function rachfordV(K){
  const func = (V)=>{
    return z.reduce((s,zi,i)=>s + zi*(K[i]-1)/(1 + V*(K[i]-1)),0);
  };
  let a=0, b=1, fa=func(a), fb=func(b);
  if (fa*fb>0) return 0.5; // no sign change
  for (let i=0;i<30;i++){
    const mid=(a+b)/2;
    const fm=func(mid);
    if (Math.abs(fm)<1e-8) return mid;
    if (fa*fm<0){b=mid; fb=fm;}
    else{a=mid; fa=fm;}
  }
  return (a+b)/2;
}

/* Energy balance solver for V (inner loop, not used here separately) */
function solVEnergy(T,Q_hr,P_flash){
  const K = Klist(T,P_flash);
  const func = (V)=>{
    const x = [];
    const y = [];
    for (let i=0;i<4;i++){
      x[i] = z[i]/(1+V*(K[i]-1));
      y[i] = K[i]*x[i];
    }
    const lhs = Q_hr/F + z.reduce((s,zi,i)=>s+zi*Cp_L[i]*(T_F - T),0);
    const rhs = V * y.reduce((s,yi,i)=>s+yi*DeltaH_vap[i],0);
    return lhs - rhs;
  };
  let a=0, b=1, fa=func(a), fb=func(b);
  if (fa*fb>0) return 0.5;
  for(let i=0;i<30;i++){
    const mid=(a+b)/2;
    const fm=func(mid);
    if (Math.abs(fm)<1e-4) return mid;
    if (fa*fm<0){b=mid; fb=fm;}
    else{a=mid; fa=fm;}
  }
  return (a+b)/2;
}

/* Slider event handling */
function initSliders(){
  const sp = document.getElementById('slider-pressure');
  const sv = document.getElementById('slider-heat');
  const sprv = document.getElementById('pressure-value');
  const svrv = document.getElementById('heat-value');

  function update(){
    flashParams.pressure = parseInt(sp.value);
    flashParams.heat = parseInt(sv.value);
    sprv.textContent = sp.value + " kPa";
    svrv.textContent = sv.value + " kJ/hr";
    const res = calculateFlash(flashParams.pressure, flashParams.heat);
    flashParams.T = res.T;
    flashParams.V = res.V;
    flashParams.x = res.x;
    flashParams.y = res.y;
  }
  sp.addEventListener('input', update);
  sv.addEventListener('input', update);
  window.addEventListener('load', update);
}

/* p5.js sketch */
let sketch = new p5((p)=>{

  p.setup = ()=> {
    const cnv = p.createCanvas(800,500);
    cnv.parent('visualization-area');
    p.textSize(12);
    p.textAlign(p.LEFT, p.TOP);
