  };

  p.draw = ()=> {
    p.background('#F0F0F0');

    // Draw flash vessel
    p.fill('#E6E6FA');
    p.stroke('#000');
    p.rect(350,200,100,100);
    p.fill('#000');
    p.textAlign(p.CENTER,p.CENTER);
    p.text('Flash',400,250);

    // Feed stream
    p.stroke('#9ACD32');
    p.strokeWeight(2);
    p.line(100,250,350,250);
    drawArrow(350,250,100,250,'#9ACD32');

    // Vapor stream
    p.stroke('#9370DB');
    p.strokeWeight(2);
    p.line(400,200,400,100);
    p.line(400,100,550,100);
    drawArrow(550,100,400,100,'#9370DB');

    // Liquid stream
    p.stroke('#FFA500');
    p.strokeWeight(2);
    p.line(400,300,400,400);
    p.line(400,400,550,400);
    drawArrow(550,400,400,400,'#FFA500');

    // Heat arrow (dashed)
    p.stroke('#FF0000');
    p.strokeWeight(2);
    p.drawingContext.setLineDash([5,3]);
    p.line(280,250,350,250);
    p.drawingContext.setLineDash([]);
    drawArrow(350,250,280,250,'#FF0000');
    // Heat value near arrow
    p.fill('#000');
    p.text(`Q = ${flashParams.heat} kJ/hr`, 260,240);

    // Feed info
    p.textAlign(p.LEFT,p.TOP);
