    // Vapor info
    p.text(`Vapor (y):\n${component.map((c,i)=>`${c}: ${flashParams.y[i].toFixed(3)}`).join('\n')}`, 600, 10);

    // Liquid info
    p.text(`Liquid (x):\n${component.map((c,i)=>`${c}: ${flashParams.x[i].toFixed(3)}`).join('\n')}`, 600, 200);

    // Process info
    p.text(`Vapor fraction: ${flashParams.V.toFixed(3)}\nFlash Temp: ${flashParams.T.toFixed(2)} K\nFlash Pressure: ${flashParams.pressure} kPa`, 520, 300);
  };

});
</script>
