  cbRealistic.addEventListener('change', (e) => {
    params.realisticSize = e.target.checked;
  });

  for (let key in sliders) {
    sliders[key].addEventListener('input', (e) => {
      params[key] = parseFloat(e.target.value);
      spans[key].textContent = params[key].toFixed(2);
