      e.target.textContent = params.isAnimating[key] ? '-' : '+';
    });
  }

  // Window resize
  window.addEventListener('resize', () => {
    camera.aspect = (window.innerWidth - 320) / window.innerHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(window.innerWidth - 320, window.innerHeight);
  });

  // Animation loop
  let lastTime = performance.now();
  function loop(now) {
    const dt = (now - lastTime) / 1000; // seconds
    lastTime = now;

    // Animate sliders if active
    if (params.isAnimating.time) animateSlider('time', dt * 0.1);
    if (params.isAnimating.nodalAngle) animateSlider('nodalAngle', dt * 0.05);
    if (params.isAnimating.sunEarthAngle) animateSlider('sunEarthAngle', dt * 0.05);
    if (params.isAnimating.zodiac) animateSlider('zodiac', dt * 0.02);

    updateScene();
    requestAnimationFrame(loop);
  }
  requestAnimationFrame(loop);
})();
</script>
</body>
</html>
