</head>
<body>
    <h1>Best Effort Global Warming Trajectories</h1>
    <p>
        This interactive demo visualizes a simple climate model showing how atmospheric carbon concentration (blue curve) and annual emissions (purple curve) evolve over time. Adjust the sliders to explore different scenarios for atmospheric CO₂ residence time, transition pace, and the number of constant‑pace wedges.
    </p>

    <div id="controls-container">
        <div class="control-group">
            <label for="slider-residence-time">Atmospheric CO₂ residence time (years)</label>
            <input type="range" id="slider-residence-time" min="50" max="500" step="1" value="200">
            <span id="value-residence-time">200</span>
        </div>

        <div class="control-group">
            <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
            <input type="range" id="slider-transition-pace" min="0" max="100" step="1" value="25">
            <span id="value-transition-pace">25</span>
        </div>

        <div class="control-group">
            <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
            <input type="range" id="slider-wedges" min="0" max="10" step="0.01" value="4">
            <span id="value-wedges">4.00</span>
        </div>
    </div>

    <div id="plot-div"></div>

    <script>
        'use strict';

        // Model constants
        const C0 = 850;           // GtC
        const E0 = 8.5;           // GtC/yr
        const C_EQ = 586;         // GtC
        const E_FLOOR = 1.5;      // GtC/yr
        const RISE_RATE = 0.02;   // GtC/yr²
        const DT = 0.5;           // years
        const MAX_TIME = 300;     // years

        // Cache DOM elements
        const sliderRT = document.getElementById('slider-residence-time');
        const sliderTP = document.getElementById('slider-transition-pace');
        const sliderWedges = document.getElementById('slider-wedges');
        const valRT = document.getElementById('value-residence-time');
        const valTP = document.getElementById('value-transition-pace');
        const valWedges = document.getElementById('value-wedges');

        // Update display spans
        function updateDisplays() {
            valRT.textContent = sliderRT.value;
            valTP.textContent = sliderTP.value;
            valWedges.textContent = parseFloat(sliderWedges.value).toFixed(2);
        }

        // Core simulation and plotting
        function computeAndPlot() {
            const tRes = parseFloat(sliderRT.value);
            const tTrans = parseFloat(sliderTP.value);
            const Nwedges = parseFloat(sliderWedges.value);

            const tArr = [];
            const cargoArr = [];
            const emissionArr = [];

            let t = 0;
            let C = C0;
            const Epeak = E0 + RISE_RATE * tTrans;

            while (t <= MAX_TIME + 1e-9) { // small epsilon to include last point
                // Emission rate E(t)
                let E;
                if (t < tTrans) {
                    E = E0 + RISE_RATE * t;
                } else {
                    const reductionRate = Nwedges / 50;
                    const E_linear = Epeak - reductionRate * (t - tTrans);
                    E = Math.max(E_FLOOR, E_linear);
                }

                // Update atmospheric carbon via Euler integration
                const dC = (E - (C - C_EQ) / tRes) * DT;
                C = C + dC;

                // Store values
                tArr.push(t);
                cargoArr.push(C);
                emissionArr.push(100 * E);

                t += DT;
            }

            // Prepare Plotly data
            const data = [
                {
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Atmospheric Carbon',
                    x: tArr,
                    y: cargoArr,
                    line: { color: 'blue', width: 2 }
                },
                {
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Annual Emissions ×100',
                    x: tArr,
                    y: emissionArr,
                    line: { color: 'purple', width: 2 }
                },
                {
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Target Ceiling',
                    x: [0, MAX_TIME],
                    y: [1200, 1200],
                    line: { color: 'black', width: 2, dash: 'dash' }
                }
            ];

            const layout = {
                title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                xaxis: { title: 'years', range: [0, MAX_TIME], zeroline: true },
                yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true },
                showlegend: false,
                margin: { l: 60, r: 20, b: 40, t: 80 },
                paper_bgcolor: '#f9f9f9',
                plot_bgcolor: 'white',
                gridcolor: '#ddd'
            };

            Plotly.react('plot-div', data, layout);
        }

        // Attach event listeners
        sliderRT.addEventListener('input', () => {
            updateDisplays();
            computeAndPlot();
        });
        sliderTP.addEventListener('input', () => {
            updateDisplays();
            computeAndPlot();
        });
        sliderWedges.addEventListener('input', () => {
            updateDisplays();
            computeAndPlot();
        });

        // Initial rendering
        window.addEventListener('DOMContentLoaded', () => {
            updateDisplays();
            computeAndPlot();
        });
    </script>
</body>
</html>
