// Helper to format angle as fraction of π or decimal
function formatAngle(rad) {
  const ratio = rad / Math.PI;
  const tolerance = 0.05; // tolerance for recognition
  let best = {num:0, den:1, diff:Infinity};
  for (let den=1; den<=12; den++) {
    const num = Math.round(ratio * den);
    const diff = Math.abs(ratio - num/den);
    if (diff < best.diff) best = {num, den, diff};
  }
  if (best.diff < tolerance) {
    const {num, den} = best;
    if (num === 0) return '0';
    let str = '';
    if (num < 0) { str += '-'; }
    const absNum = Math.abs(num);
    if (den === 1) {
      str += (absNum===1 ? 'π' : `${absNum}π`);
    } else {
      str += `${absNum===1?'':'${absNum}'}π/${den}`;
    }
    return str;
  } else {
    return rad.toFixed(2);
  }
}

// Update slider displays
function updateDisplay() {
