      const cosT = p.cos(theta);
      const theta_ddot = (-m*l*sinT*cosT*thetaDot*thetaDot - g*(M+m)*sinT) /
                         (l*(M + m*sinT*sinT));
      thetaDot += theta_ddot * dt;
