  <!-- three.js CDN -->
  <script src="https://cdn.jsdelivr.net/npm/three@0.159.0/build/three.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/three@0.159.0/examples/js/controls/OrbitControls.js"></script>

  <script>
    // Utility functions
    const degToRad = d => d * Math.PI / 180;
    const radToDeg = r => r * 180 / Math.PI;

    // Get DOM elements
    const sliders = {
      strike: document.getElementById('slider-strike'),
      dip: document.getElementById('slider-dip'),
      slipAngle: document.getElementById('slider-slip-angle'),
      slipAmount: document.getElementById('slider-slip-amount')
    };
    const valuesDisplay = {
      strike: document.getElementById('value-strike'),
      dip: document.getElementById('value-dip'),
      slipAngle: document.getElementById('value-slip-angle'),
      slipAmount: document.getElementById('value-slip-amount')
    };
    const resetBtn = document.getElementById('btn-reset');
    const checkboxBox = document.getElementById('checkbox-box');

    // Scene, camera, renderer
    const scene = new THREE.Scene();
    const renderer = new THREE.WebGLRenderer({canvas: document.getElementById('webgl-canvas'), antialias:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    const camera = new THREE.PerspectiveCamera(45, 800/600, 0.1, 1000);
    camera.position.set(2, 2, 4);
    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // Lighting
    const ambient = new THREE.AmbientLight(0x555555);
    scene.add(ambient);
    const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight1.position.set(5, 10, 7.5);
    scene.add(dirLight1);
    const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
    dirLight2.position.set(-5, -5, -5);
    scene.add(dirLight2);

    // Sphere geometry
    const sphereSegments = { widthSegments: 64, heightSegments: 32 };
    const sphereRadius = 1;
    const geometry = new THREE.SphereGeometry(sphereRadius, sphereSegments.widthSegments, sphereSegments.heightSegments);
    const material = new THREE.MeshStandardMaterial({vertexColors: true, roughness:0.4, metalness:0.1});
    const sphere = new THREE.Mesh(geometry, material);
    scene.add(sphere);

    // Store original positions
    const posAttr = geometry.attributes.position;
    const origPositions = posAttr.array.slice(); // Float32Array copy

    // BoxHelper (bounding box)
    const boxHelper = new THREE.BoxHelper(sphere, 0xffffff);
    boxHelper.visible = false;
    scene.add(boxHelper);

    // Update functions
    function updateDisplay(sliderName, value) {
      if (sliderName === 'slipAmount') {
        valuesDisplay[sliderName].value = value.toFixed(2);
      } else {
        valuesDisplay[sliderName].value = value + '°';
      }
    }

    function computeOrientation() {
      const φ = degToRad(parseFloat(sliders.strike.value));   // strike
      const δ = degToRad(parseFloat(sliders.dip.value));     // dip
      const λ = degToRad(parseFloat(sliders.slipAngle.value)); // slip angle

      // Strike vector (horizontal)
      const s = new THREE.Vector3(Math.sin(φ), Math.cos(φ), 0); // (xEast, yNorth, zDown)

      // Dip direction (downward)
      const d = new THREE.Vector3(
        Math.cos(φ) * Math.sin(δ),
        Math.sin(φ) * Math.sin(δ),
        Math.cos(δ)
      ); // unit vector

      // Slip vector
      const u = new THREE.Vector3(
        Math.cos(λ) * s.x + Math.sin(λ) * d.x,
        Math.cos(λ) * s.y + Math.sin(λ) * d.y,
        Math.cos(λ) * s.z + Math.sin(λ) * d.z
      ).normalize();

      // Fault normal (pointing down along d)
      const n = d.clone().normalize();

      // P and T axes
      const P = new THREE.Vector3().addVectors(n, u).normalize();
      const T = new THREE.Vector3().subVectors(u, n).normalize();

      return { P, T, n, u };
    }

    function updateSphereColors() {
      const { P, T } = computeOrientation();

      const indices = geometry.index.array;
      const positions = origPositions;
      const colors = new Float32Array(positions.length);
      const tempVec = new THREE.Vector3();

      for (let i = 0; i < indices.length; i += 3) {
        const iA = indices[i];
        const iB = indices[i + 1];
        const iC = indices[i + 2];

        // Vertex positions
        const ax = positions[iA * 3];
        const ay = positions[iA * 3 + 1];
        const az = positions[iA * 3 + 2];
        const bx = positions[iB * 3];
        const by = positions[iB * 3 + 1];
        const bz = positions[iB * 3 + 2];
        const cx = positions[iC * 3];
        const cy = positions[iC * 3 + 1];
        const cz = positions[iC * 3 + 2];

        // Centroid
        const cxen = (ax + bx + cx) / 3;
        const cyen = (ay + by + cy) / 3;
        const czen = (az + bz + cz) / 3;

        tempVec.set(cxen, cyen, czen).normalize();

        const dotP = tempVec.dot(P);
        const dotT = tempVec.dot(T);

        const isCompressive = dotP * dotT >= 0; // Manner to classify quadrants

        const col = isCompressive ? 0xffffff : 0xffa500; // white or orange

        // Set color for each vertex of the face
        [iA, iB, iC].forEach(vi => {
          colors[vi * 3] = (col >> 16) & 0xff;
          colors[vi * 3 + 1] = (col >> 8) & 0xff;
          colors[vi * 3 + 2] = col & 0xff;
        });
      }

      if (geometry.attributes.color) {
        geometry.attributes.color.array.set(colors);
        geometry.attributes.color.needsUpdate = true;
      } else {
        geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));
      }
    }

    function updateSphereDeformation() {
      const sAmount = parseFloat(sliders.slipAmount.value);
      const { P, T } = computeOrientation();

      const positions = posAttr.array;
      const tempVec = new THREE.Vector3();

      for (let i = 0; i < positions.length; i += 3) {
        const original = new THREE.Vector3(
          origPositions[i],
          origPositions[i + 1],
          origPositions[i + 2]
        );

        const dp = original.dot(P);
        const dt = original.dot(T);
        const scale = (dt * dt - dp * dp);

        const deformation = original.clone().multiplyScalar(scale * sAmount * 0.5); // 0.5 is deformation_scale

        positions[i]     = original.x + deformation.x;
        positions[i + 1] = original.y + deformation.y;
        positions[i + 2] = original.z + deformation.z;
      }

      posAttr.needsUpdate = true;
      geometry.computeVertexNormals();
    }

    function updateScene() {
      updateSphereColors();
      updateSphereDeformation();
    }

    // Event listeners
    Object.keys(sliders).forEach(name => {
      sliders[name].addEventListener('input', () => {
        updateDisplay(name, sliders[name].value);
        updateScene();
      });
    });

    checkboxBox.addEventListener('change', () => {
      boxHelper.visible = checkboxBox.checked;
    });

    resetBtn.addEventListener('click', () => {
      sliders.strike.value = 180;
      sliders.dip.value = 60;
      sliders.slipAngle.value = 90;
      sliders.slipAmount.value = 0.5;

      Object.keys(sliders).forEach(name => {
        updateDisplay(name, sliders[name].value);
      });
      updateScene();
    });

    // Resize handling
    function onWindowResize() {
      const canvas = renderer.domElement;
      const width = canvas.clientWidth;
      const height = canvas.clientHeight;
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
      renderer.setSize(width, height, false);
    }
    window.addEventListener('resize', onWindowResize);
    onWindowResize(); // initial set

    // Animation loop
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();

    // Initial setup
    resetBtn.click(); // to set displays and initial scene
  </script>
</body>
</html>
