<body>
<div id="app-container">
  <div id="control-panel">
    <div class="control-group">
      <label for="slider-db-size">database size</label>
      <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
      <div class="value-display">+<span id="display-db-size">10</span></div>
    </div>
    <div class="control-group">
      <label for="slider-iterations">number of iterations</label>
      <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
      <div class="value-display">+<span id="display-iterations">2</span></div>
    </div>
  </div>
  <div id="canvas-container"></div>
  <div id="probability-display">99.9% success probability</div>
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
let dbSizeSlider, iterSlider;
let dbSizeDisplay, iterDisplay;
let probDisplay;
const targetIndex = 6; // fixed
const barColor = '#808080';
const targetColor = '#FFA500';
const bgColor = '#ffffff';
const plotHeight = 120; // Height allocated for each stage
const barScale = 60; // Pixel scaling per amplitude unit
let canvas;

function setup() {
  let container = select('#canvas-container');
  canvas = createCanvas(800, plotHeight * 4 + 80);
  canvas.parent('canvas-container');
  canvas.clear();
  noLoop();

  // Grab elements
  dbSizeSlider = select('#slider-db-size');
  iterSlider = select('#slider-iterations');
  dbSizeDisplay = select('#display-db-size');
  iterDisplay = select('#display-iterations');
  probDisplay = select('#probability-display');

  // Initialize displays
  dbSizeDisplay.html(dbSizeSlider.value());
  iterDisplay.html(iterSlider.value());
  updateProbability();

  // Event listeners
  dbSizeSlider.input(() => {
    const val = dbSizeSlider.value();
    dbSizeDisplay.html(val);
    redraw();
    updateProbability();
  });

  iterSlider.input(() => {
    const val = iterSlider.value();
    iterDisplay.html(val);
    redraw();
    updateProbability();
  });
}

function draw() {
  background(bgColor);
  const N = int(dbSizeSlider.value());
  const k = int(iterSlider.value());

  // Draw each stage
  let yOffset = 40; // Space from top
  // Stage Titles
  textAlign(CENTER, TOP);
  textSize(14);
  text('Original Amplitudes', width / 2, yOffset - 22);
  drawStage(yOffset, N, getStageState(N, 0));
  yOffset += plotHeight;

  text('After First Inversion', width / 2, yOffset - 22);
  drawStage(yOffset, N, getStageState(N, 1, true));
  yOffset += plotHeight;

  text('After First Diffusion', width / 2, yOffset - 22);
  drawStage(yOffset, N, getStageState(N, 1, false));
  yOffset += plotHeight;

  text('After ' + k + ' Iterations', width / 2, yOffset - 22);
  drawStage(yOffset, N, getFinalState(N, k));
}

// Helper to get state array for stage
function getStageState(N, stage, afterInversion = true) {
  let state = initState(N);
  if (stage >= 1) {
    if (afterInversion) {
      invert(state);
    } else {
      // no inversion
    }
  } else {
    // stage 0: original
  }

  if (stage >= 2) {
    diff(state);
  }
  return state;
}

// Final state after k iterations
function getFinalState(N, k) {
  let state = initState(N);
  for (let i = 0; i < k; i++) {
    invert(state);
    diff(state);
  }
  return state;
}

// p5 drawing of a single stage
function drawStage(yOffset, N, stateArray) {
  const margin = 40;
  const usableWidth = width - 2 * margin;
  const barW = usableWidth / N;
  const midY = yOffset + plotHeight / 2;
  // Baseline
  stroke(0);
  line(margin, midY, width - margin, midY);
  noStroke();

  // Bars
  for (let i = 0; i < N; i++) {
    const amp = stateArray[i];
    const h = amp * barScale;
    const barX = margin + i * barW + barW / 2;
    const color = (i === targetIndex) ? targetColor : barColor;
    fill(color);
    rect(barX, midY - h / 2, barW * 0.9, h);
  }

  // Parentheses only for first stage
  if (yOffset === 40) {
    // Left parenthesis
    fill(0);
    arc(margin - 20, midY, 40, plotHeight - 20, PI, TWO_PI, CHORD);
    // Right parenthesis
    arc(width - margin + 20, midY, 40, plotHeight - 20, 0, PI, CHORD);
  }
}

// Quantum operations
function initState(N) {
  const initVal = 1 / Math.sqrt(N);
  return Array.from({length: N}, () => initVal);
}

function invert(state) {
  if (targetIndex < state.length) {
    state[targetIndex] *= -1;
  }
}

function diff(state) {
  const mean = state.reduce((a, b) => a + b, 0) / state.length;
  for (let i = 0; i < state.length; i++) {
    state[i] = 2 * mean - state[i];
  }
}

function updateProbability() {
  const N = parseInt(dbSizeSlider.value());
  const k = parseInt(iterSlider.value());
  const sinTerm = Math.asin(1 / Math.sqrt(N));
  const P = Math.pow(Math.sin((2 * k + 1) * sinTerm), 2) * 100;
  const Pstr = P.toFixed(1);
  probDisplay.html(Pstr + '% success probability');
}
</script>
</body>
</html>
