bindButton('btn-distillate-plus','slider-distillate');
bindButton('btn-bottom-plus','slider-bottom');
bindButton('btn-feed-comp-plus','slider-feed-comp');
bindButton('btn-feed-quality-plus','slider-feed-quality');

/* ---------- Slider input handlers ----------
   Recalculate and redraw diagram on any value change
*/
['slider-volatility','slider-distillate','slider-bottom','slider-feed-comp','slider-feed-quality']
  .forEach(id=>{
    document.getElementById(id).addEventListener('input',()=>{
        updateConstraints();
        drawDiagram();
    });
  });

/* ---------- p5.js sketch ----------
   Global mode: use setup, draw functions directly
*/
let canvasWidth=600, canvasHeight=600;
const mLeft=50, mRight=20, mTop=20, mBottom=50;
const {floor}=Math; // for later

function setup(){
    let cnv=createCanvas(canvasWidth, canvasHeight);
    cnv.parent('canvas-container');
    noLoop();
    drawDiagram();
}

function draw(){/* no auto-loop*/}

/* ---------- Helper functions for plot calculations ---------- */
function equilibriumCurve(a,x){
    return (a*x)/(1+(a-1)*x);
}
function solveXForYEq(a,target){
    const step=0.001;
    let prev=equilibriumCurve(a,0)-target;
    for(let X=0; X<=1; X+=step){
        let cur=equilibriumCurve(a,X)-target;
        if(prev*cur<=0){
            let lo=X-step, hi=X;
            for(let i=0;i<20;i++){
                let mid=(lo+hi)/2;
                let fLo=equilibriumCurve(a,lo)-target;
                let fMid=equilibriumCurve(a,mid)-target;
                if(fLo*fMid<=0) hi=mid; else lo=mid;
            }
            return (lo+hi)/2;
        }
        prev=cur;
    }
    return 0;
}
function findIntersection(a,xF,q){
    const eps=1e-8;
    if(Math.abs(q-1)<eps){
        return {x:xF, y:equilibriumCurve(a,xF)};
    }else if(Math.abs(q)<eps){
        const xint=solveXForYEq(a,xF);
        return {x:xint, y:xF};
    }else{
        const m=q/(q-1);
        const step=0.001;
        let prev=equilibriumCurve(a,xF)-(m*(xF-xF)+xF); // f(xF) = 0
        for(let X=xF; X<=1; X+=step){
            let cur=equilibriumCurve(a,X)-(m*(X-xF)+xF);
            if(prev*cur<=0){
                let lo=X-step, hi=X;
                for(let i=0;i<20;i++){
                    let mid=(lo+hi)/2;
                    let fLo=equilibriumCurve(a,lo)-(m*(lo-xF)+xF);
                    let fMid=equilibriumCurve(a,mid)-(m*(mid-xF)+xF);
                    if(fLo*fMid<=0) hi=mid; else lo=mid;
                }
                let xint=(lo+hi)/2;
                return {x:xint, y:equilibriumCurve(a,xint)};
            }
            prev=cur;
        }
        return {x:xF, y:equilibriumCurve(a,xF)};
    }
}

/* ---------- Drawing utility ----------
   Convert logical [0,1] coords to pixel positions
*/
function toPixelX(x){
    return map(x,0,1,mLeft,canvasWidth-mRight);
}
function toPixelY(y){
    return map(y,0,1,canvasHeight-mBottom,mTop);
}

/* ---------- Main draw function ----------
   Reads control values, calculates stages, and renders diagram
*/
function drawDiagram(){
    // Read slider values
    const a=parseFloat(document.getElementById('slider-volatility').value);
    const xD=parseFloat(document.getElementById('slider-distillate').value);
    const xB=parseFloat(document.getElementById('slider-bottom').value);
    const xF=parseFloat(document.getElementById('slider-feed-comp').value);
    const q=parseFloat(document.getElementById('slider-feed-quality').value);

    // Compute intersection of q‑line and equilibrium curve
    const intP=findIntersection(a,xF,q);
    const xInt=intP.x, yInt=intP.y;

    // Compute operating line slopes
    const slopeRect=(yInt - xD)/(xInt - xD);
    const slopeStrip=(yInt - xB)/(xInt - xB);

    // Stage calculation
    let stages=0;
    let currX=xD, currY=xD;
    const horiz=[], vert=[];
    while(currX> xB){
        const yCur=currY;
        const newX = yCur/(a - yCur*(a-1));
        horiz.push({x1:currX, y1:currY, x2:newX, y2:currY});
        stages++;
        if(newX <= xB){
            currX=newX;
            currY=yCur;
            break;
        }
        const xVert=newX;
        const yNew = (xVert > xInt) ? (slopeRect*(xVert - xD)+xD) : (slopeStrip*(xVert - xB)+xB);
        vert.push({x1:xVert, y1:currY, x2:xVert, y2:yNew});
        currX=xVert; currY=yNew;
    }

    // ---------- Render ----------
    background(255);
    stroke('#808080'); strokeWeight(1);
    // Axes
    line(toPixelX(0), toPixelY(0), toPixelX(1), toPixelY(1)); // diag for reference
    line(toPixelX(0), toPixelY(0), toPixelX(0), toPixelY(1)); // Y axis
    line(toPixelX(0), toPixelY(0), toPixelX(1), toPixelY(0)); // X axis

    // Tick marks and labels
    textSize(12); fill('#808080'); noStroke();
    for(let i=0;i<=10;i++){
        const val=(i/10).toFixed(2);
        const pxX=toPixelX(i/10);
        const pxY=toPixelY(i/10);
        // X axis
        line(pxX, toPixelY(0)-5, pxX, toPixelY(0)+5);
        textAlign(CENTER,TOP); text(val, pxX, toPixelY(0)+8);
        // Y axis
        line(toPixelX(0)-5, pxY, toPixelX(0)+5, pxY);
        textAlign(RIGHT,CENTER); text(val, toPixelX(0)-6, pxY);
    }
    // Axis labels
    textAlign(CENTER,TOP); text('x = 1', toPixelX(1)+10, toPixelY(0)+8);
    textAlign(RIGHT,TOP); text('y = 1', toPixelX(0)-8, toPixelY(1)-8);

    // Equilibrium curve (red)
    stroke('#FF0000'); strokeWeight(2); noFill();
    beginShape();
    for(let X=0; X<=1; X+=0.005){
        const y=equilibriumCurve(a, X);
        vertex(toPixelX(X), toPixelY(y));
    }
    endShape();

    // Diagonal line (green)
    stroke('#228B22'); strokeWeight(2);
    line(toPixelX(0), toPixelY(0), toPixelX(1), toPixelY(1));

    // Feed line (dark blue)
    stroke('#00008B'); strokeWeight(2);
    if(Math.abs(q-1)<1e-8){
        // vertical
        line(toPixelX(xF), toPixelY(0), toPixelX(xF), toPixelY(1));
    }else if(Math.abs(q)<1e-8){
        // horizontal
        line(toPixelX(0), toPixelY(xF), toPixelX(1), toPixelY(xF));
    }else{
        line(toPixelX(xF), toPixelY(xF), toPixelX(xInt), toPixelY(yInt));
    }

    // Rectifying line (cyan)
    stroke('#00FFFF'); strokeWeight(2);
    line(toPixelX(xD), toPixelY(xD), toPixelX(xInt), toPixelY(yInt));

    // Stripping line (magenta)
    stroke('#FF00FF'); strokeWeight(2);
    line(toPixelX(xB), toPixelY(xB), toPixelX(xInt), toPixelY(yInt));

    // Stage lines (black)
    stroke('#000000'); strokeWeight(1);
    horiz.forEach(s=>{ line(toPixelX(s.x1), toPixelY(s.y1), toPixelX(s.x2), toPixelY(s.y2)); });
    vert.forEach(s=>{ line(toPixelX(s.x1), toPixelY(s.y1), toPixelX(s.x2), toPixelY(s.y2)); });

    // Update stage count display
    document.getElementById('stages-count-display')
        .textContent = `The number of stages is ${stages}.`;
}
</script>
</body>
</html>
