<body>
    <!-- Main container for the demo -->
    <div id="demo-container">

        <!-- Section for user controls -->
        <div id="control-panel">
            <label for="slider-month">month</label>
            <input type="range" id="slider-month">

            <label for="perspective-buttons">perspective</label>
            <div id="perspective-buttons">
                <button id="btn-2d" class="active">2D</button>
                <button id="btn-3d">3D</button>
            </div>
        </div>

        <!-- Section for the visualization canvas -->
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>

    </div>

    <!-- CDN Includes -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        /* ====================== GLOBAL STATE ====================== */
        const slider = document.getElementById('slider-month');
        const btn2d = document.getElementById('btn-2d');
        const btn3d = document.getElementById('btn-3d');
        const canvas = document.getElementById('main-canvas');

        let currentMonth = new Date().getMonth(); // 0-11
        let view = '2d'; // '2d' or '3d'

        /* 2D (p5) */
        let p5Instance = null;

        /* 3D (Three.js) */
        let scene, camera, renderer, controls, earthMesh, magnetosphereMesh;
        let animationId = null;

        /* ====================== UTILITY ====================== */
        function setMonth(value) {
            currentMonth = Math.max(0, Math.min(11, value));
            slider.value = currentMonth;
            if (view === '2d' && p5Instance) {
                p5Instance.redraw();
            } else if (view === '3d') {
                update3DSimulation();
            }
        }

        function resizeCanvas() {
            if (view === '3d' && renderer) {
                renderer.setSize(canvas.clientWidth, canvas.clientHeight);
                camera.aspect = canvas.clientWidth / canvas.clientHeight;
                camera.updateProjectionMatrix();
            }
        }

        /* ====================== 2D VIEW ====================== */
        function init2D() {
            if (p5Instance) return; // already running

            const sketch = (s) => {
                s.setup = () => {
                    s.createCanvas(canvas.clientWidth, canvas.clientHeight, s.WEBGL);
                    s.pixelDensity(2);
                    s.noLoop(); // we'll redraw only on slider change
                };

                s.draw = () => {
                    s.background(0);

                    /* Compute tilt angle */
                    const tiltDeg = 23.5 * Math.cos(2 * Math.PI * currentMonth / 12);
                    const tiltRad = s.radians(tiltDeg);

                    /* Draw solar wind (static red lines) */
                    s.push();
                    s.stroke('#990000');
                    s.strokeWeight(2);
                    for (let i = 0; i < 10; i++) {
                        const y = s.map(i, 0, 9, -s.height / 2 + 20, s.height / 2 - 20);
                        s.line(-s.width / 2, y, s.width / 6, y);
                    }
                    s.pop();

                    /* Draw magnetosphere boundaries (approximated) */
                    s.push();
                    s.translate(0, 0);
                    s.rotate(tiltRad);
                    // Earth
                    s.noStroke();
                    s.fill('#4169E1');
                    s.push();
                    s.translate(0, 0);
                    s.scale(0.3, 0.3); // scale down for view
                    s.sphere(100, 30, 30); // placeholder for Earth
                    s.pop();

                    // Magnetopause (cylindrical shape)
                    s.push();
                    s.stroke('#00FFFF');
                    s.strokeWeight(2);
                    s.noFill();
                    const mpRadius = 150;
                    s.ellipse(0, 0, 2 * mpRadius, 2 * mpRadius);
                    s.pop();

                    // Bow Shock
                    s.stroke('#FFA500');
                    const bsRadius = 200;
                    s.ellipse(0, 0, 2 * bsRadius, 2 * bsRadius);
                    s.pop();

                    // Simple magnetic field lines (few arcs)
                    s.stroke('#808080');
                    s.strokeWeight(1);
                    for (let i = -2; i <= 2; i++) {
                        s.moveTo(-70, i * 20);
                        s.bezierTo(-30, i * 20, -30, i * 20, 0, i * 20);
                    }
                    // Magnetotail shaping
                    s.stroke('#00008B');
                    s.fill('#00008B');
                    s.strokeWeight(0);
                    s.beginShape();
                    s.vertex(70, -120);
                    s.bezierVertex(120, -150, 120, 150, 70, 120);
                    s.closeShape();
                    s.pop();

                    // Van Allen belts (simplified as arcs)
                    s.push();
                    s.stroke('#006400');
                    s.strokeWeight(8);
                    s.noFill();
                    s.arc(0, 0, 120, 120, s.PI + s.PI / 4, 2 * s.PI - s.PI / 4);
                    s.stroke('#32CD32');
                    s.arc(0, 0, 140, 140, s.PI + s.PI / 4, 2 * s.PI - s.PI / 4);
                    s.pop();

                    // Polar cusps
                    s.push();
                    s.fill('#F0E68C');
                    s.stroke('#F0E68C');
                    s.triangle(-70, -30, -80, 0, -70, 30);
                    s.triangle(-70, -30, -55, -60, -40, -30);
                    s.pop();

                    // Earth axis
                    s.push();
                    s.stroke('#00FF00');
                    s.strokeWeight(2);
                    const axisLength = 150;
                    s.line(0, 0, 0, -axisLength / 2, 0);
                    s.pop();
                };
            };

            p5Instance = new p5(sketch, canvas);
            p5Instance.redraw(); // initial draw
        }

        function stop2D() {
            if (p5Instance) {
                p5Instance.remove();
                p5Instance = null;
            }
        }

        /* ====================== 3D VIEW ====================== */
        function init3D() {
            if (scene) return; // already initialized

            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setClearColor(0x000000);

            camera = new THREE.PerspectiveCamera(45, canvas.clientWidth / canvas.clientHeight, 0.1, 2000);
            camera.position.set(0, 200, 400);

            controls = new THREE.OrbitControls(camera, canvas);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            /* Lights */
            const ambLight = new THREE.AmbientLight(0x555555);
            scene.add(ambLight);

            const sunLight = new THREE.PointLight(0xFFFFFF, 1.5);
            sunLight.position.set(0, 0, 0);
            scene.add(sunLight);

            /* Sun */
            const sunGeom = new THREE.SphereGeometry(30, 32, 32);
            const sunMat = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
            const sunMesh = new THREE.Mesh(sunGeom, sunMat);
            sunMesh.position.set(0, 0, 0);
            scene.add(sunMesh);

            /* Orbit */
            const a = 250; // semi-major axis
            const b = 180; // semi-minor axis
            const ellipseCurve = new THREE.EllipseCurve(
                0, 0, // ax, aY
                a, b, // xRadius, yRadius
                0, 2 * Math.PI, // startAngle, endAngle
                false, // clockwise
                0 // rotation
            );
            const points = ellipseCurve.getPoints(200);
            const orbitGeom = new THREE.BufferGeometry().setFromPoints(points);
            const orbitMat = new THREE.LineBasicMaterial({ color: 0xFFFFFF });
            const orbitLine = new THREE.Line(orbitGeom, orbitMat);
            scene.add(orbitLine);

            /* Earth */
            const earthGeom = new THREE.SphereGeometry(20, 32, 32);
            const earthMat = new THREE.MeshStandardMaterial({ color: 0x4169E1 });
            earthMesh = new THREE.Mesh(earthGeom, earthMat);
            scene.add(earthMesh);

            /* Earth's axis (ArrowHelper) */
            const axisDir = new THREE.Vector3(0, 0, 1).applyAxisAngle(new THREE.Vector3(1, 0, 0), THREE.Math.degToRad(0));
            const axisEl = new THREE.ArrowHelper(axisDir, new THREE.Vector3(0, 0, 0), 50, 0x00FF00);
            earthMesh.add(axisEl);

            /* Magnetosphere (lathe geometry) */
            const profilePoints = [];
            for (let i = 0; i < 10; i++) {
                const t = i / 9;
                const radius = THREE.Math.lerp(20, 6, t) * (0.8 + 0.2 * Math.sin(t * Math.PI));
                const z = THREE.Math.lerp(0, -120, t);
                profilePoints.push(new THREE.Vector2(radius, z));
            }
            const latheGeom = new THREE.LatheGeometry(profilePoints, 32);
            const latheMat = new THREE.MeshStandardMaterial({
                color: 0x0000FF,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });
            magnetosphereMesh = new THREE.Mesh(latheGeom, latheMat);
            earthMesh.add(magnetosphereMesh);
            magnetosphereMesh.position.set(0, 0, 0);

            /* Solar wind arrows */
            const numWind = 12;
            for (let i = 0; i < numWind; i++) {
                const dir = new THREE.Vector3(-1, 0, 0);
                const length = 80;
                const arrow = new THREE.ArrowHelper(dir, new THREE.Vector3(0, 0, 0), length, 0xFF0000);
                scene.add(arrow);
            }

            update3DSimulation(); // place Earth
            animate();
        }

        function update3DSimulation() {
            if (!scene || !earthMesh || !controls) return;

            const a = 250, b = 180;
            const t = 2 * Math.PI * currentMonth / 12;
            const x = a * Math.cos(t);
            const z = b * Math.sin(t);
            const y = 0;

            earthMesh.position.set(x, y, z);

            /* Earth axis orientation: tilt 23.5° relative to the orbital plane */
            const tiltDeg = 23.5;
            const tiltRad = THREE.Math.degToRad(tiltDeg);
            const axis = new THREE.Vector3(0, 0, 1);
            const quaternion = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1, 0, 0), tiltRad);
            axis.applyQuaternion(quaternion);
            earthMesh.children[0].setDirection(axis); // arrow for Earth axis

            /* Magnetosphere orientation: tail points away from Sun */
            const dirAway = earthMesh.position.clone().normalize();
            magnetosphereMesh.rotation.set(0, 0, 0); // reset
            const temp = new THREE.Vector3(0, 0, -1); // magnetosphere's default tail orientation
            const target = dirAway.clone();
            const quat = new THREE.Quaternion().setFromUnitVectors(temp, target);
            magnetosphereMesh.setRotationFromQuaternion(quat);

            /* Update orbit Line to align with new parameters if needed */
        }

        function animate() {
            animationId = requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function stop3D() {
            if (animationId) cancelAnimationFrame(animationId);
            if (renderer) renderer.dispose();
            renderer = null;
            scene = null;
            camera = null;
            controls = null;
            earthMesh = null;
            magnetosphereMesh = null;
            animationId = null;
        }

        /* ====================== EVENT HANDLERS ====================== */
        btn2d.addEventListener('click', () => {
            if (view === '2d') return;
            btn3d.classList.remove('active');
            btn2d.classList.add('active');
            view = '2d';
            stop3D();
            init2D();
        });

        btn3d.addEventListener('click', () => {
            if (view === '3d') return;
            btn2d.classList.remove('active');
            btn3d.classList.add('active');
            view = '3d';
            stop2D();
            init3D();
        });

        slider.addEventListener('input', (e) => {
            setMonth(parseInt(e.target.value, 10));
        });

        window.addEventListener('resize', () => {
            resizeCanvas();
            if (view === '2d' && p5Instance) {
                p5Instance.resizeCanvas(canvas.clientWidth, canvas.clientHeight);
            }
        });

        /* ====================== INITIAL LOAD ====================== */
        // set slider bounds
        slider.min = 0;
        slider.max = 11;
        slider.step = 1;
        slider.value = currentMonth;

        // Start with 2D view
        init2D();
    </script>
