<body>
<div id="main-container">
  <!-- Control Panel -->
  <div id="control-panel">
    <div class="slider-container">
      <label for="slider-alpha">parameter α</label>
      <input type="range" id="slider-alpha" min="0.1" max="5" step="0.005" value="1.825">
      <span id="display-alpha" class="slider-value">1.825</span>
    </div>
    <div class="slider-container">
      <label for="slider-beta">parameter β</label>
      <input type="range" id="slider-beta" min="0.1" max="5" step="0.005" value="1.945">
      <span id="display-beta" class="slider-value">1.945</span>
    </div>
  </div>

  <!-- Visualization -->
  <div id="visualization">
    <div class="plot-title"><span style="color:blue">density function</span> <span style="color:black">and</span> <span style="color:#FF4500">distribution function</span></div>
    <div id="plot-container"></div>
  </div>

  <!-- Statistics Display -->
  <div id="statistics">
    <p>Mean: <span id="mean-value">3.550</span></p>
    <p>Variance: <span id="variance-value">6.904</span></p>
    <p>Moment generating function: <span id="mgf-formula" class="math-container"></span></p>
  </div>
</div>

<script>
/* Helper functions for Gamma distribution */
function gamma(z) {
  // Lanczos approximation
  const p = [
    676.5203681218851,
    -1259.1392167224028,
    771.32342877765313,
    -176.61502916214059,
    12.507343278686905,
    -0.13857109526572012,
    9.9843695780195716e-6,
    1.5056327351493116e-7
  ];
  const g = 7;
  if (z < 0.5) return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
  z -= 1;
  let x = 0.99999999999980993;
  for (let i = 0; i < p.length; i++) {
    x += p[i] / (z + i + 1);
  }
  const t = z + g + 0.5;
  return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
}

function gammaPdf(x, a, b) {
  if (x <= 0) return 0;
  const numerator = Math.pow(x, a - 1) * Math.exp(-x / b);
  const denominator = Math.pow(b, a) * gamma(a);
  return numerator / denominator;
}

/* Numerical integration for CDF */
function computeCDF(pdfValues, dx) {
  const cdf = [0];
  for (let i = 1; i < pdfValues.length; i++) {
    const integral = (pdfValues[i] + pdfValues[i - 1]) * dx / 2;
    cdf.push(cdf[i - 1] + integral);
  }
  return cdf;
}

/* Main updating function */
function update() {
  const alpha = parseFloat(document.getElementById('slider-alpha').value);
  const beta = parseFloat(document.getElementById('slider-beta').value);

  /* Update slider displays */
  document.getElementById('display-alpha').textContent = alpha.toFixed(3);
  document.getElementById('display-beta').textContent = beta.toFixed(3);

  /* Compute statistics */
  const mean = alpha * beta;
  const variance = alpha * Math.pow(beta, 2);
  const meanPdf = gammaPdf(mean, alpha, beta);

  document.getElementById('mean-value').textContent = mean.toFixed(3);
  document.getElementById('variance-value').textContent = variance.toFixed(3);

  /* Update MGF formula */
  const mgf = `$$ (1 - ${beta.toFixed(3)} t)^{-${alpha.toFixed(3)}} = \\frac{1}{(1 - ${beta.toFixed(3)} t)^{${alpha.toFixed(3)}}} $$`;
  document.getElementById('mgf-formula').innerHTML = mgf;
  MathJax.typeset();

  /* Prepare data for Plotly */
  const xArray = [];
  const pdfArray = [];
  const dx = 0.1;
  for (let x = 0; x <= 25; x += dx) {
    xArray.push(x);
    pdfArray.push(gammaPdf(x, alpha, beta));
  }
  const cdfArray = computeCDF(pdfArray, dx);

  /* Update plot */
  Plotly.react('plot-container', [
    {
      x: xArray,
      y: pdfArray,
      type: 'scatter',
      mode: 'lines',
      line: { color: 'blue', width: 2.5 },
      name: 'PDF'
    },
    {
      x: xArray,
      y: cdfArray,
      type: 'scatter',
      mode: 'lines',
      line: { color: 'orange', width: 2.5 },
      name: 'CDF'
    }
  ], {
    xaxis: { range: [0, 25], title: '' },
    yaxis: { range: [0, 1.1], title: '' },
    margin: { l: 50, r: 20, t: 20, b: 50 },
    showlegend: false,
    shapes: [
      {
        type: 'line',
        x0: mean,
        x1: mean,
        y0: 0,
        y1: meanPdf,
        line: { color: 'red', width: 3 }
      }
    ]
  });
}

/* Event listeners */
document.getElementById('slider-alpha').addEventListener('input', update);
document.getElementById('slider-beta').addEventListener('input', update);

/* Initial rendering */
document.addEventListener('DOMContentLoaded', () => {
  update();
});
</script>
</body>
