<script>
(() => {
  const range = 2.0; // scales x,y in parametric function
  const renderer = new THREE.WebGLRenderer({antialias:true});
  const canvasContainer = document.getElementById('canvas-container');
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
  renderer.setClearColor(0x000000);
  canvasContainer.appendChild(renderer.domElement);

  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0x000000);

  const camera = new THREE.PerspectiveCamera(45, canvasContainer.clientWidth/canvasContainer.clientHeight, 0.1, 1000);
  camera.position.set(5,5,5);
  camera.lookAt(0,0,0);

  const controls = new THREE.OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.dampingFactor = 0.05;

  // Lights
  const ambient = new THREE.AmbientLight(0x404040, 0.8);
  scene.add(ambient);
  const dirLight = new THREE.DirectionalLight(0xffffff, 1);
  dirLight.position.set(5, 10, 7.5);
  scene.add(dirLight);

  // Axes
  const axisLength = 5;
  const axesMaterial = new THREE.LineBasicMaterial({color: 0x888888});
  const axesGeom = new THREE.BufferGeometry();
  const axesVertices = new Float32Array([
    -axisLength,0,0, axisLength,0,0,
    0,-axisLength,0, 0,axisLength,0,
    0,0,-axisLength, 0,0,axisLength
  ]);
  axesGeom.setAttribute('position', new THREE.BufferAttribute(axesVertices, 3));
  const axes = new THREE.LineSegments(axesGeom, axesMaterial);
  scene.add(axes);

  // Variables for paraboloid meshes
  let solidMesh, wireMesh;

  // Utility: Hex to RGB
  const hexToRgb = (hex) => {
    const r = (hex >> 16) & 255;
    const g = (hex >> 8) & 255;
    const b = hex & 255;
    return { r, g, b };
  };
  const rgbToFloat = (r,g,b) => [r/255,g/255,b/255];

  // Create paraboloid
  function createParaboloid(a,b,c) {
    const zmax = c * range * range;
    const func = function(u,v,target) {
      const theta = u * 2 * Math.PI;
      const r = v * range;
      const x = a * r * Math.cos(theta);
      const y = b * r * Math.sin(theta);
      const z = c * r * r;
      target.set(x,y,z);
    };
    const geometry = new THREE.ParametricGeometry(func, 50, 50); // 50x50 segments

    // Vertex colors based on height
    const posAttr = geometry.attributes.position;
    const colors = new Float32Array(posAttr.count * 3);
    const colorLow = hexToRgb(0xFFD700);   // yellow/orange
    const colorHigh = hexToRgb(0xFF4500);  // darker orange/red

    for(let i=0;i<posAttr.count;i++){
      const z = posAttr.array[i*3+2];
      const t = Math.min(1, z / zmax); // normalise 0..1
      const r = colorLow.r + t * (colorHigh.r - colorLow.r);
      const g = colorLow.g + t * (colorHigh.g - colorLow.g);
      const b = colorLow.b + t * (colorHigh.b - colorLow.b);
      colors[i*3] = r;
      colors[i*3+1] = g;
      colors[i*3+2] = b;
    }
    geometry.setAttribute('color', new THREE.BufferAttribute(colors,3));

    const materialSolid = new THREE.MeshPhongMaterial({
      side: THREE.DoubleSide,
      vertexColors: true
    });
    const solid = new THREE.Mesh(geometry, materialSolid);
    solid.position.set(0,0,0);

    const materialWire = new THREE.MeshBasicMaterial({
      color: 0xcccccc,
      wireframe: true
    });
    const wire = new THREE.Mesh(geometry, materialWire);
    wire.position.set(0,0,0);

    return {solid, wire};
  }

  // Initial mesh
  function initParaboloid() {
    const a = parseFloat(document.getElementById('slider-a').value);
    const b = parseFloat(document.getElementById('slider-b').value);
    const c = parseFloat(document.getElementById('slider-c').value);
    const meshes = createParaboloid(a,b,c);
    scene.add(meshes.solid);
    scene.add(meshes.wire);
    solidMesh = meshes.solid;
    wireMesh = meshes.wire;
  }

  initParaboloid();

  // Synchronize inputs
  function attachSync(slider, input) {
    slider.addEventListener('input', () => {
      input.value = slider.value;
      updateParaboloid();
    });
    input.addEventListener('input', () => {
      const val = parseFloat(input.value);
      if(isNaN(val)) return;
      slider.value = val;
      updateParaboloid();
    });
  }
  attachSync(document.getElementById('slider-a'), document.getElementById('input-a'));
  attachSync(document.getElementById('slider-b'), document.getElementById('input-b'));
  attachSync(document.getElementId('slider-c'), document.getElementById('input-c'));

  // Update paraboloid on change
  function updateParaboloid() {
    scene.remove(solidMesh);
    scene.remove(wireMesh);
    const a = parseFloat(document.getElementById('slider-a').value);
    const b = parseFloat(document.getElementById('slider-b').value);
    const c = parseFloat(document.getElementById('').value);
    const meshes = createParaboloid(a,b,c);
    scene.add(meshes.solid);
    scene.add(meshes.wire);
    solidMesh = meshes.solid;
    wireMesh = meshes.wire;
  }

  // Animation loop
  function animate() {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }
  animate();

  // Handle resize
  window.addEventListener('resize', () => {
    const w = canvasContainer.clientWidth;
    const h = canvasContainer.clientHeight;
    renderer.setSize(w, h);
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
  });
})();
</script>
