  }
  function updateMSelectors(){
    const m1Vals = [...Array(Math.round(2*j1)+1)].map((_,i)=>-j1 + i);
    const m2Vals = [...Array(Math.round(2*j2)+1)].map((_,i)=>-j2 + i);
    populateSelector(m1Sel, m1Vals, m1, val=>{m1=val; m=m1+m2; redraw(); resetAnim();});
    populateSelector(m2Sel, m2Vals, m2, val=>{m2=val; m=m1+m2; redraw(); resetAnim();});
  }
  function updateJSelector(){
    const minJ = Math.abs(j1 - j2);
    const maxJ = j1 + j2;
    const jVals = [...Array(Math.round(2*minJ)+1)].map((_,i)=>minJ + i);
    populateSelector(jSel, jVals, j, val=>{j=val; m=m1+m2; redraw(); resetAnim();});
  }
  function resetAnim(){
    isAnimating=false; playButton.textContent='▶';
    phi_z=0; phi_j=0;
  }
  function initSelectors(){
    const jVals = [0.5,1.0,1.5,2.0];
    populateSelector(j1Sel, jVals, j1, val=>{j1=val; updateMSelectors(); updateJSelector(); redraw(); resetAnim();});
    populateSelector(j2Sel, jVals, j2, val=>{j2=val; updateMSelectors(); updateJSelector(); redraw(); resetAnim();});
    updateMSelectors(); updateJSelector(); redraw(); resetAnim();
  }
  function createArrow(dir, len, color){
    const headSize = len*0.1;
    const arw = new THREE.ArrowHelper(dir.clone().normalize(), new THREE.Vector3(0,0,0), len, color, headSize, headSize/2);
    scene.add(arw); return arw;
  }
  function removeObject(obj){
    if(obj){scene.remove(obj); if(obj.geometry) obj.geometry.dispose(); if(obj.material) obj.material.dispose();}
  }
  function recomputeVectors(){
    removeObject(arrowJ); arrowJ=null;
    removeObject(arrowJ1); arrowJ1=null;
    removeObject(arrowJ2); arrowJ2=null;
    removeObject(coneJ); removeObject(coneJ1); removeObject(coneJ2);
    removeObject(coneJ1Un); removeObject(coneJ2Un);
    const L1 = L(j1), L2 = L(j2), LJ = L(j);
    const m_tot = m1 + m2;
    // Determine coupled or uncoupled
    if(j >= j1 && j >= j2){
      /*** Coupled Model ***/
      // J vector
      const cosThetaJ = m_tot / LJ; const thetaJ = Math.acos(Math.min(1, Math.max(-1, cosThetaJ)));
      const dirJ = sphericalDir(thetaJ, phi_z); // initial orientation
      arrowJ = createArrow(dirJ, LJ*1.2, 0x000000);
      // conical cones for J
      const coneGeom = new THREE.ConeGeometry(1, 1, 32, 1, true);
      const coneMat = new THREE.MeshBasicMaterial({color:0x000000, opacity:0.2, transparent:true});
      // J cone along Z
      coneJ = new THREE.Mesh(coneGeom.clone(),coneMat.clone());
      coneJ.scale.set(LJ*1.2, LJ*1.2, LJ*1.2);
      coneJ.rotation.x = Math.PI/2;
      scene.add(coneJ);
      /* J1 and J2 vectors */
      // angles between J and J1/J2 via law of cosines
      const cosAlpha1 = (LJ*LJ + L1*L1 - L2*L2)/(2*LJ*L1);
      const alpha1 = Math.acos(Math.min(1, Math.max(-1, cosAlpha1)));
      const cosAlpha2 = (LJ*LJ + L2*L2 - L1*L1)/(2*LJ*L2);
      const alpha2 = Math.acos(Math.min(1, Math.max(-1, cosAlpha2)));
      // J1 in J-frame
      const dirJ1_local = sphericalDir(alpha1, phi_j);
      // rotate to world: align Z to dirJ
      const quat = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), dirJ);
      const dirJ1_world = dirJ1_local.clone().applyQuaternion(quat);
      arrowJ1 = createArrow(dirJ1_world, L1*1.2, 0xff0000);
      // J2
      const dirJ2_local = sphericalDir(alpha2, phi_j + Math.PI);
      const dirJ2_world = dirJ2_local.clone().applyQuaternion(quat);
      arrowJ2 = createArrow(dirJ2_world, L2*1.2, 0x0000ff);
      // Cones for J1, J2 along J
      const coneMat1 = coneMat.clone(); coneMat1.color.set(0xff0000);
      const coneMat2 = coneMat.clone(); coneMat2.color.set(0x0000ff);
      // J1 cone
      coneJ1 = new THREE.Mesh(coneGeom.clone(),coneMat1);
      coneJ1.scale.set(L1*1.2, L1*1.2, L1*1.2);
      const quatJ1 = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), dirJ1_world);
      coneJ1.quaternion.multiplyQuaternions(quat, quatJ1);
      scene.add(coneJ1);
      // J2 cone
      coneJ2 = new THREE.Mesh(coneGeom.clone(),coneMat2);
      coneJ2.scale.set(L2*1.2, L2*1.2, L2*1.2);
      const quatJ2 = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), dirJ2_world);
      coneJ2.quaternion.multiplyQuaternions(quat, quatJ2);
      scene.add(coneJ2);
    }else{
      /*** Uncoupled Model ***/
      // J vector not drawn
      // J1 cone
      const coneGeom = new THREE.ConeGeometry(1,1,32,1,true);
      const coneMat = new THREE.MeshBasicMaterial({color:0xff0000, opacity:0.2, transparent:true});
      const coneJ1u = new THREE.Mesh(coneGeom.clone(),coneMat.clone());
      coneJ1u.scale.set(L1*1.2, L1*1.2, L1*1.2);
      coneJ1u.rotation.x = Math.PI/2;
      scene.add(coneJ1u); coneJ1Un = coneJ1u;
      // J2 cone
      const coneMat2 = coneMat.clone(); coneMat2.color.set(0x0000ff);
      const coneJ2u = new THREE.Mesh(coneGeom.clone(),coneMat2.clone());
      coneJ2u.scale.set(L2*1.2, L2*1.2, L2*1.2);
      coneJ2u.rotation.x = Math.PI/2;
      scene.add(coneJ2u); coneJ2Un = coneJ2u;
      // J1 arrow
      const cosTheta1 = m1 / L1; const theta1 = Math.acos(Math.min(1, Math.max(-1, cosTheta1)));
      const dirJ1 = sphericalDir(theta1, phi_z);
      arrowJ1 = createArrow(dirJ1, L1*1.2, 0xff0000);
      // J2 arrow
      const cosTheta2 = m2 / L2; const theta2 = Math.acos(Math.min(1, Math.max(-1, cosTheta2)));
      const dirJ2 = sphericalDir(theta2, phi_z);
      arrowJ2 = createArrow(dirJ2, L2*1.2, 0x0000ff);
    }
  }
  function sphericalDir(theta, phi){
    const x = Math.sin(theta) * Math.cos(phi);
    const y = Math.sin(theta) * Math.sin(phi);
    const z = Math.cos(theta);
    return new THREE.Vector3(x,y,z);
  }
  function redraw(){
    recomputeVectors();
    render();
  }
  function render(){
    renderer.render(scene,camera);
  }
  /* ----- Animation Loop ----- */
  let lastTime=0;
  function animate(now){
    requestAnimationFrame(animate);
    const dt = (now- lastTime)/1000; lastTime=now;
    if(isAnimating){
      phi_z += phaseSpeed * dt;
      phi_j += phaseSpeed * dt;
      if(j >= j1 && j >= j2){ // coupled: update both phi_z and phi_j
        // Update J direction
        const L1=L(j1), L2=L(j2), LJ=L(j);
        const cosThetaJ = (m1+m2)/LJ;
        const thetaJ=Math.acos(Math.min(1, Math.max(-1, cosThetaJ)));
        const dirJ = sphericalDir(thetaJ, phi_z);
        arrowJ.setDirection(dirJ);
        // Update J1, J2 using phi_j
        const cosAlpha1 = (LJ*LJ + L1*L1 - L2*L2)/(2*LJ*L1);
        const alpha1 = Math.acos(Math.min(1, Math.max(-1, cosAlpha1)));
        const cosAlpha2 = (LJ*LJ + L2*L2 - L1*L1)/(2*LJ*L2);
        const alpha2 = Math.acos(Math.min(1, Math.max(-1, cosAlpha2)));
        const dirJ1_local = sphericalDir(alpha1, phi_j);
        const quat = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), dirJ);
        const dirJ1_world = dirJ1_local.clone().applyQuaternion(quat);
        arrowJ1.setDirection(dirJ1_world);
        const dirJ2_local = sphericalDir(alpha2, phi_j + Math.PI);
        const dirJ2_world = dirJ2_local.clone().applyQuaternion(quat);
        arrowJ2.setDirection(dirJ2_world);
        // Update cones orientation
        const quatJ1 = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), dirJ1_world);
        coneJ1.quaternion.copy(quat).multiply(quatJ1);
        const quatJ2 = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,0,1), dirJ2_world);
        coneJ2.quaternion.copy(quat).multiply(quatJ2);
      }else{
        // Uncoupled: only phi_z
        const L1=L(j1), L2=L(j2);
        const cosTheta1 = m1 / L1;
        const theta1 = Math.acos(Math.min(1, Math.max(-1, cosTheta1)));
        const dirJ1 = sphericalDir(theta1, phi_z);
        arrowJ1.setDirection(dirJ1);
        const cosTheta2 = m2 / L2;
        const theta2 = Math.acos(Math.min(1, Math.max(-1, cosTheta2)));
        const dirJ2 = sphericalDir(theta2, phi_z);
        arrowJ2.setDirection(dirJ2);
      }
    }
    render();
  }
  /* ----- Event Listeners ----- */
  playButton.onclick = ()=>{
    isAnimating = !isAnimating;
    playButton.textContent = isAnimating ? '❚❚' : '▶';
  };
  resetButton.onclick = ()=>{
    phi_z=0; phi_j=0; isAnimating=false; playButton.textContent='▶'; render();
  };
  /* ----- Initialize ----- */
  initSelectors();
  animate(0);
})();
</script>
</body>
</html>
