<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Recursive vs Iterative Flood Fill Demo</title>
<style>
  body { font-family: Arial, sans-serif; background:#f0f0f0; margin:0; padding:0; }
  #main-container { width:100%; max-width:1200px; margin:auto; padding:1rem; }
  #control-panel { display:flex; align-items:center; gap:1rem; margin-bottom:1rem; }
  #control-panel label { margin-right:.5rem; }
  #control-panel button { padding:.4rem 1rem; border:none; cursor:pointer; background:#ececec; border-radius:4px; }
  #control-panel button.active { background:#007BFF; color:#fff; }
  #viz-container { display:flex; gap:1rem; justify-content:space-between; }
  .grid-container { flex:1; background:#fff; padding:10px; border:1px solid #ddd; border-radius:4px; box-shadow:0 2px 4px rgba(0,0,0,.1); }
  .grid-container h3 { text-align:center; margin-top:0; }
  #canvas-recursive, #canvas-iterative { width:201px; height:201px; margin:0 auto; display:block; }
  #steps-value { font-weight:bold; }
</style>
<script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <label for="steps-slider">steps</label>
    <input type="range" id="steps-slider" min="0" max="61" step="1" value="61">
    <span id="steps-value">+ 61</span>
    <label>box</label>
    <button id="btn-none">None</button>
    <button id="btn-box">Box</button>
    <button id="btn-scattered" class="active">Scattered</button>
  </div>
  <div id="viz-container">
    <div id="recursive-container" class="grid-container">
      <h3>Recursive</h3>
      <div id="canvas-recursive"></div>
    </div>
    <div id="iterative-container" class="grid-container">
      <h3>Iterative</h3>
      <div id="canvas-iterative"></div>
    </div>
  </div>
</div>

<script>
// ────── GLOBAL CONFIG & STATE ──────
const GRID_SIZE = 10;
const CELL_SIZE = 20;
const CANVAS_SIZE = GRID_SIZE * CELL_SIZE + 1;  // +1 for borders

let obstacleGrid = [];        // 0 empty, 1 obstacle
let recursiveHistory = [];    // array of {x,y} in order
let iterativeHistory = [];    // array of {x,y} in order

let recursiveInstance;
let iterativeInstance;

// ────── UTILITIES ──────
function cloneGrid(grid) {
  return grid.map(row => row.slice());
}

function initGrid(pattern) {
  let grid = Array.from({length: GRID_SIZE}, () => Array(GRID_SIZE).fill(0));
  if (pattern === 'box') {
    for (let i=0;i<GRID_SIZE;i++){
      grid[0][i] = 1;
      grid[GRID_SIZE-1][i] = 1;
      grid[i][0] = 1;
      grid[i][GRID_SIZE-1] = 1;
    }
  } else if (pattern === 'scattered') {
    for (let y=0; y<GRID_SIZE; y++){
      for (let x=0; x<GRID_SIZE; x++){
        if (!seededRand() < 0.25) continue; // 25% probability
        // Leave start cell empty
        if (x===4 && y===4) continue;
        grid[y][x] = 1;
      }
    }
  }
  // Ensure start cell empty
  grid[4][4] = 0;
  return grid;
}

// Simple LCG seeded RNG for deterministic scattered pattern
let rngSeed = 123456789;
function seededRand() {
  rngSeed = (rngSeed * 1664525 + 1013904223) % 4294967296;
  return rngSeed / 4294967296;
}

// ────── ALGORITHMS ──────
function floodFillRecursive(grid, x0, y0, visited, history) {
  if (x0 < 0 || x0 >= GRID_SIZE || y0 < 0 || y0 >= GRID_SIZE) return;
  if (grid[y0][x0] === 1) return; // obstacle
  if (visited[y0][x0]) return;
  visited[y0][x0] = true;
  history.push({x: x0, y: y0});

  // N, W, S, E order
  floodFillRecursive(grid, x0, y0-1, visited, history);
  floodFillRecursive(grid, x0-1, y0, visited, history);
  floodFillRecursive(grid, x0, y0+1, visited, history);
  floodFillRecursive(grid, x0+1, y0, visited, history);
}

function floodFillIterative(grid) {
  const history = [];
  const visited = Array.from({length: GRID_SIZE}, () => Array(GRID_SIZE).fill(false));
  const queue = [];
  if (grid[4][4] === 1) return history; // start blocked

  queue.push({x:4, y:4});
  visited[4][4] = true;

  const dirs = [
    {dx:0, dy:-1}, // N
    {dx:-1, dy:0}, // W
    {dx:0, dy:1},  // S
    {dx:1, dy:0}   // E
  ];

  while (queue.length) {
    const {x,y} = queue.shift();
    history.push({x,y});
    for (const d of dirs) {
      const nx = x + d.dx;
      const ny = y + d.dy;
      if (nx<0||nx>=GRID_SIZE||ny<0||ny>=GRID_SIZE) continue;
      if (grid[ny][nx] === 1) continue;
      if (visited[ny][nx]) continue;
      visited[ny][nx] = true;
      queue.push({x:nx, y:ny});
    }
  }
  return history;
}

// ────── P5 SKETCHES ──────
function createGridSketch(containerId) {
  return function(sketch) {
    let canvas;
    sketch.setup = () => {
      canvas = sketch.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
      canvas.parent(containerId);
      sketch.noLoop();
    };

    const drawGrid = (step) => {
      sketch.background(255); // white
      sketch.stroke(0);      // black grid lines
      sketch.strokeWeight(1);

      // Draw obstacles
      sketch.fill(0); // black
      for (let y=0; y<GRID_SIZE; y++) {
        for (let x=0; x<GRID_SIZE; x++) {
          if (obstacleGrid[y][x] === 1) {
            sketch.rect(x*CELL_SIZE+0.5, y*CELL_SIZE+0.5, CELL_SIZE, CELL_SIZE);
          }
        }
      }

      // Draw filled cells up to 'step'
      sketch.fill(0,0,255); // blue
      const history = this.currentHistory || [];
      const end = Math.min(step, history.length);
      for (let i=0; i<end; i++) {
        const {x,y} = history[i];
        sketch.rect(x*CELL_SIZE+0.5, y*CELL_SIZE+0.5, CELL_SIZE, CELL_SIZE);
      }
    };
    sketch.drawAt = drawGrid;
  };
}

// Create two separate instance modes
recursiveInstance = new p5(createGridSketch('canvas-recursive'), 'canvas-recursive');
iterativeInstance = new p5(createGridSketch('canvas-iterative'), 'canvas-iterative');

// ────── UI HANDLERS ──────
const stepsSlider = document.getElementById('steps-slider');
const stepsValue = document.getElementById('steps-value');

const btnNone = document.getElementById('btn-none');
const btnBox = document.getElementById('btn-box');
const btnScattered = document.getElementById('btn-scattered');

function setActiveButton(btn) {
  [btnNone, btnBox, btnScattered].forEach(b => b.classList.remove('active'));
  btn.classList.add('active');
}

function generateAndRender(pattern) {
  // Reset RNG seed for deterministic scattered pattern
  rngSeed = 123456789;
  obstacleGrid = initGrid(pattern);

  // Record histories
  const visitedRec = Array.from({length: GRID_SIZE}, () => Array(GRID_SIZE).fill(false));
  recursiveHistory = [];
  floodFillRecursive(obstacleGrid, 4, 4, visitedRec, recursiveHistory);

  iterativeHistory = floodFillIterative(obstacleGrid);

  // Tell sketches which history to use
  recursiveInstance.currentHistory = recursiveHistory;
  iterativeInstance.currentHistory = iterativeHistory;

  // Update slider max
  const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
  stepsSlider.max = maxSteps;

  // Reset slider
  stepsSlider.value = 0;
  stepsValue.textContent = '+ 0';

  // Draw initial state
  recursiveInstance.drawAt(0);
  iterativeInstance.drawAt(0);
}

function setupEventListeners() {
  stepsSlider.addEventListener('input', () => {
    const val = parseInt(stepsSlider.value, 10);
    stepsValue.textContent = `+ ${val}`;
    recursiveInstance.drawAt(val);
    iterativeInstance.drawAt(val);
  });

  btnNone.addEventListener('click', () => {
    setActiveButton(btnNone);
    generateAndRender('none');
  });

  btnBox.addEventListener('click', () => {
    setActiveButton(btnBox);
    generateAndRender('box');
  });

  btnScattered.addEventListener('click', () => {
    setActiveButton(btnScattered);
    generateAndRender('scattered');
  });
}

// ────── PAGE LOAD ──────
window.addEventListener('load', () => {
  setupEventListeners();
  // Initial load with scattered pattern
  generateAndRender('scattered');
});
</script>
</body>
</html>
