```html
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Bouncing Ball Stroboscopic Visualization</title>
<style>
  body { margin: 0; font-family: Arial, sans-serif; background:#222; color:#ddd; }
  #control-panel { display:flex; flex-wrap:wrap; gap:20px; padding:20px; background:#333; }
  #control-panel div { display:flex; align-items:center; }
  #control-panel label { margin-right:8px; width:120px; }
  #control-panel input[type=range] { width:200px; }
  #canvas-container { display:flex; justify-content:center; align-items:center; padding:10px; }
  canvas { border:1px solid #555; }
</style>
</head>
<body>

<div id="control-panel">
  <div>
    <label for="slider-k">k</label>
    <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
  </div>
  <div>
    <label for="slider-time">time</label>
    <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
  </div>
  <div>
    <label for="slider-vx">initial velocity in x</label>
    <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
  </div>
  <div>
    <label for="slider-dt">Δt</label>
    <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
  </div>
</div>

<div id="canvas-container"></div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<script>
let sliderK, sliderTime, sliderVx, sliderDt;
let padding = 40;         // canvas padding
let g = 9.8;              // gravity
let y0 = 10;              // initial height
let vy0 = 0;
let x0 = 0;

function setup() {
  const canvas = createCanvas(700, 400);
  canvas.parent('canvas-container');
  noLoop();                   // static drawing
  sliderK   = select('#slider-k');
  sliderTime = select('#slider-time');
  sliderVx  = select('#slider-vx');
  sliderDt  = select('#slider-dt');

  // Redraw on slider change
  [sliderK, sliderTime, sliderVx, sliderDt].forEach(slider => {
    slider.elt.addEventListener('input', () => redraw());
  });
}

function draw() {
  // Read current slider values
  const k    = parseFloat(sliderK.value());
  const time = parseFloat(sliderTime.value());
  const vx   = parseFloat(sliderVx.value());
  const dt   = parseFloat(sliderDt.value());

  // Simulation
  let currentTime = 0;
  let currentX = x0;
  let currentY = y0;
  let currentVy = vy0;

  const pathPoints = [];
  const strobePoints = [];
  let lastStrobe = 0;

  const timeStep = 0.01;

  while (currentTime < time) {
    // Time to ground (solve quadratic: y=0)
    const t_flight = (currentVy + Math.sqrt(currentVy * currentVy + 2 * g * currentY)) / g;
    const vyImpact = currentVy - g * t_flight;          // downward

    // Trajectory for this bounce (continuous line)
    for (let t = 0; t <= t_flight; t += timeStep) {
      const x = currentX + vx * t;
      const y = currentY + currentVy * t - 0.5 * g * t * t;
      pathPoints.push({ x, y });
    }
    // Ensure the final point at impact
    const xEnd = currentX + vx * t_flight;
    const yEnd = currentY + currentVy * t_flight - 0.5 * g * t_flight * t_flight;
    pathPoints.push({ x: xEnd, y: yEnd });

    // Stroboscopic flashes during this arc
    while (lastStrobe < currentTime + t_flight + 1e-9) {
      const t = lastStrobe - currentTime;
      const x = currentX + vx * t;
      const y = currentY + currentVy * t - 0.5 * g * t * t;
      strobePoints.push({ x, y });
      lastStrobe += dt;
    }

    // Prepare next bounce
    currentTime += t_flight;
    currentX += vx * t_flight;
    const vyNew = Math.sqrt(k) * Math.abs(vyImpact);
    currentX = currentX;          // remains
    currentY = 0;                 // back on ground
    currentVy = vyNew;
  }

  // Scale to fit drawing area
  const maxX = Math.max(...pathPoints.map(p=>p.x));
  const maxY = Math.max(...pathPoints.map(p=>p.y));
  const scaleFactor = Math.min(
    (width - 2 * padding) / maxX,
    (height - 2 * padding) / maxY
  );

  // Drawing
  background(0);
  push();

  // Transform to cartesian coordinates: origin bottom-left
  translate(padding, height - padding);
  scale(1, -1);
  scale(scaleFactor, scaleFactor);

  // Draw axes
  stroke(100, 100, 150);
  strokeWeight(1);
  line(0, 0, width - 2 * padding, 0);  // x-axis
  line(0, 0, 0, height - 2 * padding); // y-axis

  // Tick marks (1 m spacing)
  const tickLenPx = 5;                      // pixels
  const tickLenWorld = tickLenPx / scaleFactor; // world units
  for (let x = 1; x <= maxX; x++) {
    const px = x;
    line(px, 0, px, -tickLenWorld);
  }
  for (let y = 1; y <= maxY; y++) {
    const py = y;
    line(0, py, tickLenWorld, py);
  }

  // Draw path
  noFill();
  stroke(255, 0, 0);
  strokeWeight(2);
  beginShape();
  pathPoints.forEach(pt => vertex(pt.x, pt.y));
  endShape();

  // Draw stroboscopic points
  noStroke();
  fill(255, 255, 0);
  const dotRadiusWorld = 4 / scaleFactor; // world units for ~8-pixel radius
  strobePoints.forEach(pt => ellipse(pt.x, pt.y, dotRadiusWorld, dotRadiusWorld));

  pop();
}
</script>
</body>
</html>
