<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Angle Bisectors on the Circumcircle</title>
<style>
    body {font-family: Arial, sans-serif; text-align:center; margin:0; padding:0; background:#FFFFFF;}
    header {padding:20px; background:#F5F5F5;}
    #canvas-container {display:flex; justify-content:center; align-items:center; padding:20px;}
    canvas {border:1px solid #CCC;}
</style>
</head>
<body>
<header>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
</header>

<div id="canvas-container">
    <canvas id="p5-canvas"></canvas>
</div>

<!-- p5.js library CDN -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.7.0/lib/p5.min.js"></script>
<script>
let cx, cy, r;
let angleA = 210 * PI / 180;
let angleB = 330 * PI / 180;
let angleC = 90 * PI / 180;
let posA, posB, posC;
let posAp, posBp, posCp;
let activePoint = null;
const tolerance = 15;

function setup() {
    const canvas = createCanvas(500, 500);
    canvas.id('p5-canvas');
    canvas.parent('canvas-container');
    cx = width / 2;
    cy = height / 2;
    r = 200;
    updatePositions();
}

function draw() {
    background('#F0F0F0');
    // Circumcircle
    noFill();
    stroke('#6A5ACD');
    strokeWeight(4);
    ellipse(cx, cy, r * 2, r * 2);

    // Triangle ABC
    stroke('#6A5ACD');
    strokeWeight(4);
    line(posA.x, posA.y, posB.x, posB.y);
    line(posB.x, posB.y, posC.x, posC.y);
    line(posC.x, posC.y, posA.x, posA.y);

    // Extended Bisectors & Triangle A'B'C'
    stroke('#FFA500');
    strokeWeight(4);
    line(posA.x, posA.y, posAp.x, posAp.y);
    line(posB.x, posB.y, posBp.x, posBp.y);
    line(posC.x, posC.y, posCp.x, posCp.y);

    line(posAp.x, posAp.y, posBp.x, posBp.y);
    line(posBp.x, posBp.y, posCp.x, posCp.y);
    line(posCp.x, posCp.y, posAp.x, posAp.y);

    // Points
    drawPoint(posA, true);
    drawPoint(posB, true);
    drawPoint(posC, true);
    drawPoint(posAp, false);
    drawPoint(posBp, false);
    drawPoint(posCp, false);
}

function updatePositions() {
    posA = polarToCartesian(angleA);
    posB = polarToCartesian(angleB);
    posC = polarToCartesian(angleC);
    posAp = calcDerived(angleB, angleC);
    posBp = calcDerived(angleA, angleC);
    posCp = calcDerived(angleA, angleB);
}

function polarToCartesian(angle) {
    return {
        x: cx + r * cos(angle),
        y: cy + r * sin(angle)
    };
}

function calcDerived(o1, o2) {
    const s = sin(o1) + sin(o2);
    const c = cos(o1) + cos(o2);
    const ang = atan2(s, c);
    return polarToCartesian(ang);
}

function drawPoint(pos, interactive) {
    const sz = 6; // radius
    fill('#FFA500');
    stroke('#000000');
    strokeWeight(1);
    ellipse(pos.x, pos.y, sz * 2, sz * 2);

    // crosshair
    if (interactive) {
        strokeWeight(1);
        line(pos.x - sz, pos.y, pos.x + sz, pos.y);
        line(pos.x, pos.y - sz, pos.x, pos.y + sz);
    }

    // label
    noStroke();
    fill('#000000');
    textSize(16);
    textAlign(LEFT, CENTER);
    // offset radially outward
    const vx = pos.x - cx;
    const vy = pos.y - cy;
    const len = sqrt(vx*vx + vy*vy) || 1;
    const ox = (vx / len) * 12;
    const oy = (vy / len) * 12;
    const label = interactive ? (pos === posA ? 'A' : pos === posB ? 'B' : 'C')
                              : (pos === posAp ? "A'" : pos === posBp ? "B'" : "C'");
    text(label, pos.x + ox, pos.y + oy);
}

function mousePressed() {
    if (mouseX < cx - r - tolerance || mouseX > cx + r + tolerance ||
        mouseY < cy - r - tolerance || mouseY > cy + r + tolerance) return;

    if (dist(mouseX, mouseY, posA.x, posA.y) < tolerance) activePoint = 'A';
    else if (dist(mouseX, mouseY, posB.x, posB.y) < tolerance) activePoint = 'B';
    else if (dist(mouseX, mouseY, posC.x, posC.y) < tolerance) activePoint = 'C';
}

function mouseDragged() {
    if (!activePoint) return;
    const angle = atan2(mouseY - cy, mouseX - cx);
    if (activePoint === 'A') angleA = angle;
    else if (activePoint === 'B') angleB = angle;
    else if (activePoint === 'C') angleC = angle;
    updatePositions();
}

function mouseReleased() {
    activePoint = null;
}
</script>
</body>
</html>
