<body>
<div id="container">
  <div id="controls">
    <label for="slider-steps">Carbon cycle steps:</label>
    <input type="range" id="slider-steps" min="0" max="7" step="1" value="0">
  </div>
  <div id="canvas-container"></div>
</div>

<!-- Include p5.js library -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.min.js"></script>
<script>
let sliderSteps;
let hoveredDesc = '';
let tooltipX = 0;
let tooltipY = 0;

// Helper to draw an arrow
function drawArrow(x1, y1, x2, y2, arrowSize = 10) {
  push();
  stroke(0);
  strokeWeight(2);
  line(x1, y1, x2, y2);
  // Arrow head
  const angle = atan2(y2 - y1, x2 - x1);
  const arrowX = x2 - arrowSize * cos(angle - PI / 6);
  const arrowY = y2 - arrowSize * sin(angle - PI / 6);
  const arrowX2 = x2 - arrowSize * cos(angle + PI / 6);
  const arrowY2 = y2 - arrowSize * sin(angle + PI / 6);
  fill(0);
  triangle(x2, y2, arrowX, arrowY, arrowX2, arrowY2);
  pop();
}

// Static scene drawing
function drawStaticScene() {
  // Sky
  noStroke();
  fill('#4169E1');
  rect(0, 0, 600, 200);

  // Sun
  fill('#FFD700');
  ellipse(550, 50, 60, 60);

  // Clouds
  fill(255);
  noStroke();
  ellipse(200, 50, 100, 60);
  ellipse(260, 60, 80, 50);
  ellipse(220, 70, 90, 70);

  // Grass
  fill('#228B22');
  rect(0, 200, 600, 150);

  // Soil
  fill('#8B4513');
  rect(0, 350, 600, 150);

  // Water
  fill('#0000CD');
  beginShape();
  vertex(400, 350);
  vertex(500, 350);
  vertex(600, 450);
  vertex(350, 450);
  endShape(CLOSE);

  // Trees
  // Left tree
  fill('#8B4513');
  rect(100, 250, 20, 80);
  fill('#006400');
  ellipse(110, 230, 60, 60);
  // Right tree
  fill('#8B4513');
  rect(480, 250, 20, 80);
  fill('#006400');
  ellipse(490, 230, 60, 60);

  // Sheep
  fill(255);
  ellipse(200, 260, 80, 60); // body
  ellipse(180, 260, 40, 40); // head
  fill(0);
  ellipse(170, 255, 5, 5); // eye

  // Factory
  fill('#696969');
  rect(460, 200, 80, 80); // building
  fill('#A9A9A9');
  rect(480, 140, 40, 50); // smokestack

  // Fossil deposits
  fill(0);
  for (let i = 0; i < 12; i++) {
    const x = random(50, 550);
    const y = random(350, 520);
    ellipse(x, y, 6, 6);
  }
}

// Process drawing functions

function drawCellularRespiration() {
  drawArrow(200, 240, 250, 180);
  const x = 150, y = 180;
  fill(0);
  textSize(12);
  textAlign(LEFT, TOP);
  text('Cellular Respiration', x, y);
  // Hover detection
  const tw = textWidth('Cellular Respiration');
  const labelHeight = textSize();
  if (mouseX >= x && mouseX <= x + tw && mouseY >= y && mouseY <= y + labelHeight) {
    hoveredDesc = 'Animals and plants break down sugars for energy, releasing carbon dioxide.';
    tooltipX = mouseX + 10;
    tooltipY = mouseY + 10;
  }
}

function drawOrganismsWasteAndDecay() {
  // Arrow from ground below sheep
  drawArrow(220, 280, 220, 350);
  const x = 240, y = 320;
  fill(0);
  textSize(12);
  textAlign(LEFT, TOP);
  text("Organisms' Waste and Decay", x, y);
  const tw = textWidth("Organisms' Waste and Decay");
  const labelHeight = textSize();
  if (mouseX >= x && mouseX <= x + tw && mouseY >= y && mouseY <= y + labelHeight) {
    hoveredDesc = "Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.";
    tooltipX = mouseX + 10;
    tooltipY = mouseY + 10;
  }
}

function drawFossilsAndFossilFuels() {
  // Arrow from waste area to soil
  drawArrow(220, 370, 220, 440);
  const x = 240, y = 440;
  fill(0);
  textSize(12);
  textAlign(LEFT, TOP);
  text('Fossils and Fossil Fuels', x, y);
  const tw = textWidth('Fossils and Fossil Fuels');
  const labelHeight = textSize();
  if (mouseX >= x && mouseX <= x + tw && mouseY >= y && mouseY <= y + labelHeight) {
    hoveredDesc = 'Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.';
    tooltipX = mouseX + 10;
    tooltipY = mouseY + 10;
  }
}

function drawPhotosynthesis() {
  // Arrow from sky to tree
  drawArrow(350, 80, 300, 180);
  const x = 360, y = 120;
  fill(0);
  textSize(12);
  textAlign(LEFT, TOP);
  text('Photosynthesis', x, y);
  const tw = textWidth('Photosynthesis');
  const labelHeight = textSize();
  if (mouseX >= x && mouseX <= x + tw && mouseY >= y && mouseY <= y + labelHeight) {
    hoveredDesc = 'Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.';
    tooltipX = mouseX + 10;
    tooltipY = mouseY + 10;
  }
}

function drawFossilFuelEmissions() {
  drawArrow(480, 200, 430, 120);
  const x = 440, y = 150;
  fill(0);
  textSize(12);
  textAlign(LEFT, TOP);
  text('Fossil Fuel Emissions', x, y);
  const tw = textWidth('Fossil Fuel Emissions');
  const labelHeight = textSize();
  if (mouseX >= x && mouseX <= x + tw && mouseY >= y && mouseY <= y + labelHeight) {
    hoveredDesc = 'Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.';
    tooltipX = mouseX + 10;
    tooltipY = mouseY + 10;
  }
}

function drawFossilFuelExtraction() {
  drawArrow(450, 420, 480, 280);
  const x = 400, y = 350;
  fill(0);
  textSize(12);
  textAlign(LEFT, TOP);
  text('Fossil Fuel Extraction', x, y);
  const tw = textWidth('Fossil Fuel Extraction');
  const labelHeight = textSize();
  if (mouseX >= x && mouseX <= x + tw && mouseY >= y && mouseY <= y + labelHeight) {
    hoveredDesc = 'Humans extract fossil fuels from the ground for energy.';
    tooltipX = mouseX + 10;
    tooltipY = mouseY + 10;
  }
}

function drawCO2Diffusion() {
  // Two arrows, both in water/sky
  drawArrow(520, 250, 550, 400);
  drawArrow(570, 400, 540, 250);
  const x = 480, y = 420;
  fill(0);
  textSize(12);
  textAlign(LEFT, TOP);
  text('CO2 Diffusion', x, y);
  const tw = textWidth('CO2 Diffusion');
  const labelHeight = textSize();
  if (mouseX >= x && mouseX <= x + tw && mouseY >= y && mouseY <= y + labelHeight) {
    hoveredDesc = 'Carbon dioxide dissolves into and is released from bodies of water like oceans.';
    tooltipX = mouseX + 10;
    tooltipY = mouseY + 10;
  }
}

function drawTooltip() {
  if (hoveredDesc) {
    push();
    const pad = 8;
    const w = textWidth(hoveredDesc) + pad * 2;
    const h = textSize() + pad * 2;
    fill(0, 0, 0, 150);
    noStroke();
    rect(tooltipX, tooltipY, w, h, 5);
    fill(255);
    textAlign(LEFT, TOP);
    text(hoveredDesc, tooltipX + pad, tooltipY + pad);
    pop();
  }
}

function setup() {
  let c = createCanvas(600, 500);
  c.parent('canvas-container');
  sliderSteps = select('#slider-steps');
  textFont('Arial');
  textSize(12);
  textAlign(LEFT, TOP);
  rectMode(CORNER);
}

function draw() {
  // Clear hover state each frame
  hoveredDesc = '';
  tooltipX = 0;
  tooltipY = 0;

  background(220);
  drawStaticScene();

  const val = parseInt(sliderSteps.value());

  if (val >= 1) drawCellularRespiration();
  if (val >= 2) drawOrganismsWasteAndDecay();
  if (val >= 3) drawFossilsAndFossilFuels();
  if (val >= 4) drawPhotosynthesis();
  if (val >= 5) drawFossilFuelEmissions();
  if (val >= 6) drawFossilFuelExtraction();
  if (val >= 7) drawCO2Diffusion();

  drawTooltip();
}
</script>
</body>
</html>
