<script>
/* ==================== Global State ==================== */
const state = {
  t: 0.4,
  position: false,
  velocity: false,
  acceleration: true,
  coords: 'rectangular',
  circle: false
};

/* ==================== UI Event Listeners ==================== */
const slider = document.getElementById('slider-time');
slider.addEventListener('input', () => {
  state.t = parseFloat(slider.value);
  redraw();
});

document.getElementById('btn-reset-time').addEventListener('click', () => {
  slider.value = 0.4;
  state.t = 0.4;
  redraw();
});

document.querySelectorAll('input[name="position"]').forEach(rb => rb.addEventListener('change', () => {
  state.position = rb.checked && rb.value === 'on';
  redraw();
}));
document.querySelectorAll('input[name="velocity"]').forEach(rb => rb.addEventListener('change', () => {
  state.velocity = rb.checked && rb.value === 'on';
  redraw();
}));
document.querySelectorAll('input[name="acceleration"]').forEach(rb => rb.addEventListener('change', () => {
  state.acceleration = rb.checked && rb.value === 'on';
  redraw();
}));
document.querySelectorAll('input[name="circle"]').forEach(rb => rb.addEventListener('change', () => {
  state.circle = rb.checked && rb.value === 'on';
  redraw();
}));

document.getElementById('select-coords').addEventListener('change', (e) => {
  state.coords = e.target.value;
  redraw();
});

/* ==================== p5.js Sketch ==================== */
let canvas;
function setup() {
  canvas = createCanvas(800, 800);
  canvas.parent('canvas-container');
  angleMode(RADIANS);
  noLoop(); // We'll redraw on demand
}

function draw() {
  background(255);
  translate(width/2, height/2);
  scale(1, -1); // flip Y to make math y-axis upward

  const scale = 100; // pixels per unit (unit is within [-2,2])

  /* Helper Functions */
  function mapToScreen(p) {
    return {x: p[0]*scale, y: p[1]*scale};
  }
  function drawArrow(start, end, col, lw=2) {
    stroke(col); strokeWeight(lw);
    line(start.x, start.y, end.x, end.y);
    // Arrow head
    const angle = atan2(end.y - start.y, end.x - start.x);
    const size = 8;
    push();
    translate(end.x, end.y);
    rotate(angle);
    fill(col); noStroke();
    triangle(0,0, -size, size/2, -size, -size/2);
    pop();
  }
  function vecAdd(a,b){ return [a[0]+b[0], a[1]+b[1]]; }
  function vecSub(a,b){ return [a[0]-b[0], a[1]-b[1]]; }
  function vecMul(a,scalar){ return [a[0]*scalar, a[1]*scalar]; }
  function vecLen(v){ return Math.hypot(v[0], v[1]); }
  function vecNorm(v){ const l=vecLen(v); return l? [v[0]/l, v[1]/l] : [0,0]; }
  function dot(a,b){ return a[0]*b[0]+a[1]*b[1]; }
  function cross(a,b){ return a[0]*b[1]-a[1]*b[0]; }

  /* 1. Draw Axes */
  stroke(0); strokeWeight(1);
  line(-2*scale,0,2*scale,0);
  line(0,-2*scale,0,2*scale);
  // ticks
  for(let t=-2;t<=2;t+=0.5){
    if(t!==0) { line(scale*t,-scale*0.05,scale*t,scale*0.05); line(-scale*0.05,scale*t,scale*0.05,scale*t); }
  }
  // Labels
  textSize(14); fill(0); noStroke();
  textAlign(CENTER, BOTTOM); text('x(t)', 2*scale, 4);
  textAlign(CENTER, TOP); text('y(t)', -4, 2*scale);

  /* 2. Curve */
  noFill(); stroke(0,0,255); strokeWeight(2);
  beginShape();
  for(let tt=0; tt<=TWO_PI; tt+=0.02){
    let x=Math.cos(tt);
    let y=Math.sin(2*tt);
    let p=mapToScreen([x,y]);
    vertex(p.x,p.y);
  }
  endShape();

  /* 3. Current Time Position */
  const t = state.t;
  const P=[Math.cos(t), Math.sin(2*t)];
  const pt=mapToScreen(P);
  fill(0); noStroke(); ellipse(pt.x,pt.y,6,6);

  /* 4. State vectors */
  const V=[-Math.sin(t), 2*Math.cos(2*t)];
  const A=[-Math.cos(t), -4*Math.sin(2*t)];

  // Position vector
  if(state.position){
    const end=mapToScreen(vecAdd(P, [1,0])); // Not used directly
    const start=mapToScreen([0,0]);
    drawArrow(start, pt, color(0));
    // text label
    textSize(12); fill(0); textAlign(CENTER, CENTER); text('P', pt.x, pt.y+10);
  }
  // Velocity vector
  if(state.velocity){
    const vEnd=mapToScreen(vecAdd(P,V));
    drawArrow(pt, vEnd, color(200,0,0));
  }
  // Acceleration vector
  if(state.acceleration){
    const aEnd=mapToScreen(vecAdd(P,A));
    drawArrow(pt, aEnd, color(150,0,150));
  }

  /* 5. Basis vectors in selected coordinate system */
  const basisLen=0.5; // units
  let figUnit=[1,0]; // placeholder
  let textPos;
  if(state.coords==='rectangular'){
    // î
    const eI=[1,0];
    const end=mapToScreen(vecAdd(P, vecMul(eI,basisLen)));
    drawArrow(pt, end, color(0,0,0),1);
    textPos=mapToScreen(vecAdd(P, vecMul(eI,basisLen+0.05)));
    fill(0); textSize(14); textAlign(CENTER, CENTER); text('î', textPos.x, textPos.y);
    // ĵ
    const eJ=[0,1];
    const endJ=mapToScreen(vecAdd(P, vecMul(eJ,basisLen)));
    drawArrow(pt, endJ, color(0,0,0),1);
    textPos=mapToScreen(vecAdd(P, vecMul(eJ,basisLen+0.05)));
    text('ĵ', textPos.x, textPos.y);
  } else if(state.coords==='normal-tangential'){
    const vNorm=vecNorm(V);
    const eT=vNorm;
    const aProj=dot(A,eT);
    const aNormal=vecSub(A,vecMul(eT,aProj));
    const eN=vecNorm(aNormal);
    // ê_T
    const eTend=mapToScreen(vecAdd(P, vecMul(eT,basisLen)));
    drawArrow(pt, eTend, color(0,0,0),1);
    textPos=mapToScreen(vecAdd(P, vecMul(eT,basisLen+0.05)));
    text('ê_T', textPos.x, textPos.y);
    // ê_N
    const eNend=mapToScreen(vecAdd(P, vecMul(eN,basisLen)));
    drawArrow(pt, eNend, color(0,0,0),1);
    textPos=mapToScreen(vecAdd(P, vecMul(eN,basisLen+0.05)));
    text('ê_N', textPos.x, textPos.y);
  } else if(state.coords==='polar'){
    const rLen=vecLen(P);
    const eR= vecNorm(P);
    const theta = Math.atan2(P[1],P[0]) + Math.PI/2; // derivative of polar angle
    const eTheta = [ -eR[1], eR[0] ]; // perpendicular to eR
    // ê_r
    const eRed=mapToScreen(vecAdd(P,vecMul(eR,basisLen)));
    drawArrow(pt, eRed, color(0,0,0),1);
    textPos=mapToScreen(vecAdd(P,vecMul(eR,basisLen+0.05)));
    text('ê_r', textPos.x, textPos.y);
    // ê_θ
    const eThet=mapToScreen(vecAdd(P,vecMul(eTheta,basisLen)));
    drawArrow(pt, eThet, color(0,0,0),1);
    textPos=mapToScreen(vecAdd(P,vecMul(eTheta,basisLen+0.05)));
    text('ê_θ', textPos.x, textPos.y);
  }

  /* 6. Instantaneous Circle */
  if(state.circle){
    const vLen=vecLen(V);
    const crossVal = Math.abs(cross(V, A));
    if(crossVal>1e-6){ // avoid divide by zero
      const rho = Math.pow(vLen,3)/crossVal;
      // Normal direction (towards center of curvature)
      const vNorm=vecNorm(V);
      const aProj=dot(A,vNorm);
      const aNormal=vecSub(A,vecMul(vNorm,aProj));
      const eN=vecNorm(aNormal);
      const center=vecAdd(P, vecMul(eN, rho));

      // draw circle
      noFill(); stroke(128); strokeWeight(1);
      push();
      scale(scale); // need to account positive y? we already flipped y.
      ellipse(center[0], center[1], rho*2, rho*2);
      pop();
      // maybe a small radius annotation
      fill(50); noStroke(); textSize(12); textAlign(CENTER, CENTER);
      text('ρ', mapToScreen(vecAdd(center, [0,0])).x, mapToScreen(vecAdd(center, [0,0])).y);
    }
  }
}

/* Ensure p5 draws after the canvas is created */
</script>
</body>
</html>
