<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Spring-Mass Oscillation Demo</title>
<style>
  body{margin:0;font-family:Arial,Helvetica,sans-serif;background:#f0f0f0;}
  #app{display:flex;flex-direction:column;height:100vh;}
  /* Control panel */
  #control-panel{display:flex;flex-wrap:wrap;align-items:center;background:#fff;padding:10px;}
  #control-panel > div{margin-right:15px;margin-bottom:8px;}
  #control-panel label{margin-right:5px;}
  #control-panel input[type=range]{vertical-align:middle;}
  #reset-btn{margin-left:auto;margin-right:0;padding:4px 8px;background:red;color:white;border:none;font-size:12px;border-radius:4px;}
  /* Visualization area */
  #viz-container{flex:1;display:flex;overflow:hidden;}
  #plot-canvas-container, #animation-canvas-container{flex:1;background:#fff;display:flex;align-items:center;justify-content:center;}
  #plot-canvas-container{flex:2;}
  #animation-canvas-container{flex:1;}
</style>
</head>
<body>
<div id="app">
  <!-- Control Panel -->
  <div id="control-panel">
    <div id="control-amplitude">
      <label for="slider-amplitude">Amplitude</label>
      <input type="range" id="slider-amplitude" min="0.1" max="1.0" step="0.001" value="0.3">
      <span id="amplitude-value">0.300</span>
    </div>
    <div id="control-stiffness">
      <label for="slider-stiffness">Stiffness</label>
      <input type="range" id="slider-stiffness" min="0.1" max="10.0" step="0.01" value="1.0">
      <span id="stiffness-value">1.00</span>
    </div>
    <div id="control-mass">
      <label for="slider-mass">Mass</label>
      <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
      <span id="mass-value">1.00</span>
    </div>
    <div id="control-time">
      <label>Time</label>
      <button id="btn-step-back" title="Step Back">&larr;</button>
      <button id="btn-play-pause" title="Play/Pause">▶</button>
      <button id="btn-step-forward" title="Step Forward">&rarr;|</button>
    </div>
    <button id="reset-btn" title="Reset">✖</button>
  </div>

  <!-- Visualization Container -->
  <div id="viz-container">
    <div id="plot-canvas-container"></div>
    <div id="animation-canvas-container"></div>
  </div>
</div>

<!-- p5.js CDN -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.8.0/lib/p5.min.js"></script>
<script>
/* --- Global State Variables --- */
let amplitude = 0.3;
let stiffness = 1.0;
let mass = 1.0;
let angularFrequency = Math.sqrt(stiffness / mass);
let time = 0;
let isPlaying = false;
let positionHistory = [];
let maxTime = 25.0; // initial X axis limit

/* --- Helper Functions --- */
function updateAngularFrequency() {
  angularFrequency = Math.sqrt(stiffness / mass);
}
function resetState() {
  amplitude = 0.3; stiffness = 1.0; mass = 1.0; time = 0; isPlaying = false; maxTime = 25;
  positionHistory = [];
  updateAngularFrequency();
  updateUI();
  redrawAll();
}
function updateUI() {
  document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
  document.getElementById('stiffness-value').textContent = stiffness.toFixed(2);
  document.getElementById('mass-value').textContent = mass.toFixed(2);
  document.getElementById('slider-amplitude').value = amplitude;
  document.getElementById('slider-stiffness').value = stiffness;
  document.getElementById('slider-mass').value = mass;
  document.getElementById('btn-play-pause').textContent = isPlaying ? '||' : '▶';
}
function recordPosition() {
  const pos = amplitude * Math.sin(angularFrequency * time);
  positionHistory.push({t: time, y: pos});
}
function stepTime(delta) {
  if(isPlaying) return;
  time = Math.max(0, time + delta);
  recordPosition();
  if(time > maxTime) maxTime = time + 25;
  redrawAll();
}
function playPause() {
  isPlaying = !isPlaying;
  document.getElementById('btn-play-pause').textContent = isPlaying ? '||' : '▶';
}
function redrawAll() {
  if(plotSketchInstance) plotSketchInstance.redraw();
  if(aniSketchInstance) aniSketchInstance.redraw();
}

/* --- Event Listeners --- */
document.getElementById('slider-amplitude').addEventListener('input', e=>{
  amplitude = parseFloat(e.target.value);
  updateAngularFrequency();
  time = 0; positionHistory=[]; isPlaying=false; maxTime=25;
  updateUI();
  redrawAll();
});
document.getElementById('slider-stiffness').addEventListener('input', e=>{
  stiffness = parseFloat(e.target.value);
  updateAngularFrequency();
  time = 0; positionHistory=[]; isPlaying=false; maxTime=25;
  updateUI();
  redrawAll();
});
document.getElementById('slider-mass').addEventListener('input', e=>{
  mass = parseFloat(e.target.value);
  updateAngularFrequency();
  time = 0; positionHistory=[]; isPlaying=false; maxTime=25;
  updateUI();
  redrawAll();
});
document.getElementById('btn-play-pause').addEventListener('click', playPause);
document.getElementById('btn-step-forward').addEventListener('click', ()=>stepTime(0.1));
document.getElementById('btn-step-back').addEventListener('click', ()=>stepTime(-0.1));
document.getElementById('reset-btn').addEventListener('click', resetState);

/* --- Plots Sketch --- */
let plotSketchInstance = new p5((p)=>{
  let w = document.getElementById('plot-canvas-container').clientWidth;
  let h = document.getElementById('plot-canvas-container').clientHeight;
  p.setup = function(){
    p.createCanvas(w,h);
    p.frameRate(60);
  };
  p.draw = function(){
    p.background(255);
    if(isPlaying){
      stepTime(0.05); // approximate time step
    }
    // Draw axes
    p.stroke(0);
    p.noFill();
    let yZero = p.map(0, -1, 1, p.height, 0);
    let xZero = 0;
    p.line(0, yZero, p.width, yZero); // X-axis
    p.line(0,0,0,p.height); // Y-axis
    // Grid lines
    p.stroke(220);
    // Horizontal grids
    [ -1, 0, 1 ].forEach(v=>{
      let y = p.map(v, -1, 1, p.height, 0);
      p.line(0, y, p.width, y);
    });
    // Vertical grids
    let stepX = 5;
    for(let vx=0; vx<=maxTime; vx+=stepX){
      let x = p.map(vx, 0, maxTime, 0, p.width);
      p.line(x,0,x,p.height);
    }
    // Axis labels
    p.fill(0); p.noStroke(); p.textSize(12); p.textAlign(p.RIGHT,p.CENTER);
    [-1,0,1].forEach(v=>{
      let y = p.map(v, -1, 1, p.height, 0);
      p.text(v.toFixed(0), p.width-5, y);
    });
    p.textAlign(p.CENTER,p.TOP);
    for(let vx=0; vx<=maxTime; vx+=stepX){
      let x = p.map(vx, 0, maxTime, 0, p.width);
      p.text(vx.toFixed(0), x, p.height-10);
    }
    // Plot waveform
    if(positionHistory.length>1){
      p.stroke('#6363B2'); p.noFill(); p.beginShape();
      positionHistory.forEach(pt=>{
        let x = p.map(pt.t, 0, maxTime, 0, p.width);
        let y = p.map(pt.y, -1, 1, p.height, 0);
        p.vertex(x,y);
      });
      p.endShape();
    }
  };
});

/* --- Animation Sketch --- */
let aniSketchInstance = new p5((p)=>{
  let w = document.getElementById('animation-canvas-container').clientWidth;
  let h = document.getElementById('animation-canvas-container').clientHeight;
  p.setup = function(){
    p.createCanvas(w,h);
    p.frameRate(60);
  };
  p.draw = function(){
    const pos = amplitude * Math.sin(angularFrequency * time);
    const canvasHeight = p.height;
    const canvasWidth = p.width;
    const yRangeMin = -1.2, yRangeMax = 1.2;
    const anchorY = p.map(1.2, yRangeMin, yRangeMax, canvasHeight, 0);
    const blockTopY = p.map(pos, yRangeMin, yRangeMax, canvasHeight, 0);
    // Background
    p.background(255);
    // Draw vertical axis on left
    p.stroke(0);
    let axisX = 50;
    p.line(axisX,0,axisX,canvasHeight);
    p.fill(0); p.textSize(12); p.textAlign(p.RIGHT,p.CENTER);
    [-1,0,1].forEach(v=>{
      let y = p.map(v, yRangeMin, yRangeMax, canvasHeight, 0);
      p.text(v.toFixed(0), axisX-5, y);
    });
    // Anchor point (small horizontal line)
    const anchorX = canvasWidth/2;
    p.stroke(0); p.fill(200);
    p.rect(anchorX-10, anchorY-2, 20, 4);
    // Spring
    const nSegments = 20;
    const segHeight = (anchorY - blockTopY)/nSegments;
    let prevX = anchorX, prevY = anchorY;
    p.stroke(0);
    for(let i=1;i<=nSegments;i++){
      let curX = anchorX + (i%2==0 ? -10 : 10);
      let curY = prevY - segHeight;
      p.line(prevX, prevY, curX, curY);
      prevX = curX; prevY = curY;
    }
    // Mass block
    const blockW = 30, blockH = 20;
    p.fill('#6363B2');
    p.noStroke();
    p.rect(anchorX - blockW/2, blockTopY, blockW, blockH);
  };
});

</script>
</body>
</html>
