<body>
<div id="main-container">
  <div id="control-panel">
    <span>distance function</span>
    <button id="btn-manhattan" class="distance-btn active">Manhattan</button>
    <button id="btn-euclidean" class="distance-btn">squared Euclidean</button>
    <button id="btn-chebyshev" class="distance-btn">Chebyshev</button>
  </div>
  <div id="grid-container"></div>
</div>

<script>
const ROWS = 9;
const COLS = 7;

// State variables
let currentDistanceFunction = 'manhattan';
let gridState = Array.from({length: ROWS}, () => Array(COLS).fill(false));
gridState[2][1] = true;
gridState[6][4] = true;

// Create grid
const gridContainer = document.getElementById('grid-container');
for (let r = 0; r < ROWS; r++) {
  for (let c = 0; c < COLS; c++) {
    const cell = document.createElement('div');
    cell.className = 'grid-cell cell-off';
    cell.dataset.row = r;
    cell.dataset.col = c;
    const span = document.createElement('span');
    cell.appendChild(span);
    cell.addEventListener('click', () => toggleCell(r, c));
    gridContainer.appendChild(cell);
  }
}

// Distance calculation functions
function manhattan(r1, c1, r2, c2) {
  return Math.abs(r1 - r2) + Math.abs(c1 - c2);
}
function squaredEuclidean(r1, c1, r2, c2) {
  const dr = r1 - r2, dc = c1 - c2;
  return dr*dr + dc*dc;
}
function chebyshev(r1, c1, r2, c2) {
  return Math.max(Math.abs(r1 - r2), Math.abs(c1 - c2));
}

function calculateDistances() {
  // Gather sources
  const sources = [];
  for (let r = 0; r < ROWS; r++) {
    for (let c = 0; c < COLS; c++) {
      if (gridState[r][c]) sources.push([r, c]);
    }
  }

  const distances = Array.from({length: ROWS}, () => Array(COLS).fill(Infinity));

  for (let r = 0; r < ROWS; r++) {
    for (let c = 0; c < COLS; c++) {
      if (gridState[r][c]) {
        distances[r][c] = 0;
      } else {
        let minDist = Infinity;
        for (const [sr, sc] of sources) {
          let d;
          switch (currentDistanceFunction) {
            case 'manhattan':
              d = manhattan(r, c, sr, sc);
              break;
            case 'euclidean':
              d = squaredEuclidean(r, c, sr, sc);
              break;
            case 'chebyshev':
              d = chebyshev(r, c, sr, sc);
              break;
          }
          if (d < minDist) minDist = d;
        }
        distances[r][c] = minDist;
      }
    }
  }
  return distances;
}

// Rendering
function renderGrid() {
  const distances = calculateDistances();
  const cells = document.querySelectorAll('.grid-cell');
  cells.forEach(cell => {
    const r = +cell.dataset.row;
    const c = +cell.dataset.col;
    const span = cell.querySelector('span');
    if (gridState[r][c]) {
      cell.classList.add('cell-on');
      cell.classList.remove('cell-off');
      span.textContent = '0';
    } else {
      cell.classList.remove('cell-on');
      cell.classList.add('cell-off');
      const val = distances[r][c];
      span.textContent = val === Infinity ? '' : val;
    }
  });
}

// Cell toggle
function toggleCell(r, c) {
  gridState[r][c] = !gridState[r][c];
  renderGrid();
}

// Button handling
function setActiveButton(btnElement) {
  document.querySelectorAll('.distance-btn').forEach(b => b.classList.remove('active'));
  btnElement.classList.add('active');
}

document.getElementById('btn-manhattan').addEventListener('click', () => {
  currentDistanceFunction = 'manhattan';
  setActiveButton(document.getElementById('btn-manhattan'));
  renderGrid();
});
document.getElementById('btn-euclidean').addEventListener('click', () => {
  currentDistanceFunction = 'euclidean';
  setActiveButton(document.getElementById('btn-euclidean'));
  renderGrid();
});
document.getElementById('btn-chebyshev').addEventListener('click', () => {
  currentDistanceFunction = 'chebyshev';
  setActiveButton(document.getElementById('btn-chebyshev'));
  renderGrid();
});

// Initial render
renderGrid();
</script>
</body>
</html>
