<script>
/* Utility function to format large numbers with commas */
function formatNumber(n) {
  return n.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
}

function init() {
  // Attach event listeners
  document.getElementById('btn-model-ep').addEventListener('click', () => { toggleModel('ep'); });
  document.getElementById('btn-model-ar').addEventListener('click', () => { toggleModel('ar'); });
  document.getElementById('btn-tmax-low').addEventListener('click', () => { toggleTmax('low'); });
  document.getElementById('btn-tmax-high').addEventListener('click', () => { toggleTmax('high'); });

  ['slider-n','slider-tmin','slider-tmax','slider-tref','slider-param'].forEach(id => {
    document.getElementById(id).addEventListener('input', update);
  });

  update(); // initial rendering
}

function toggleModel(selected) {
  const epBtn = document.getElementById('btn-model-ep');
  const arBtn = document.getElementById('btn-model-ar');
  if (selected === 'ep') {
    epBtn.classList.add('active');
    arBtn.classList.remove('active');
  } else {
    arBtn.classList.add('active');
    epBtn.classList.remove('active');
  }
  update();
}

function toggleTmax(selected) {
  const lowBtn = document.getElementById('btn-tmax-low');
  const highBtn = document.getElementById('btn-tmax-high');
  const tmaxSlider = document.getElementById('slider-tmax');
  if (selected === 'low') {
    lowBtn.classList.add('active');
    highBtn.classList.remove('active');
    tmaxSlider.max = 1200;
    if (parseInt(tmaxSlider.value) > 1200) tmaxSlider.value = 1200;
  } else {
    highBtn.classList.add('active');
    lowBtn.classList.remove('active');
    tmaxSlider.max = 6000;
  }
  update();
}

function update() {
  const model = document.getElementById('btn-model-ep').classList.contains('active') ? 'ep' : 'ar';
  const tmaxRange = document.getElementById('btn-tmax-low').classList.contains('active') ? 'low' : 'high';
  const sliderTmax = document.getElementById('slider-tmax');
  if (tmaxRange === 'low' && sliderTmax.max != 1200) sliderTmax.max = 1200;
  else if (tmaxRange === 'high' && sliderTmax.max != 6000) sliderTmax.max = 6000;

  // Read slider values
  const n = parseInt(document.getElementById('slider-n').value);
  let tmin = parseInt(document.getElementById('slider-tmin').value);
  let tmax = parseInt(document.getElementById('slider-tmax').value);
  let tref = parseInt(document.getElementById('slider-tref').value);
  const paramVal = parseInt(document.getElementById('slider-param').value);

  // Clamp values to meet constraints
  if (tmin >= tmax) { tmin = tmax - 1; document.getElementById('slider-tmin').value = tmin; }
  if (tref < tmin) { tref = tmin; document.getElementById('slider-tref').value = tref; }
  if (tref > tmax) { tref = tmax; document.getElementById('slider-tref').value = tref; }

  // Update labels
  document.getElementById('label-n').textContent = n;
  document.getElementById('label-tmin').textContent = tmin;
  document.getElementById('label-tmax').textContent = tmax;
  document.getElementById('label-tref').textContent = tref;
  document.getElementById('label-param-value').textContent = paramVal;

  // Update dynamic label for param
  const paramLabel = document.getElementById('label-param-name');
  paramLabel.innerHTML = model === 'ep' ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';
  MathJax.typeset();

  // Generate data points
  const tempsC = [];
  const tempsK = [];
  const yData = [];
  const TrefK = tref + 273.15;
  for (let i = 0; i < n; i++) {
    const Ti = tmin + i * (tmax - tmin) / (n - 1);
    const TiK = Ti + 273.15;
    tempsC.push(Ti);
    tempsK.push(TiK);
    let yi;
    if (model === 'ep') {
      yi = (TiK / TrefK) * Math.exp(paramVal * (1 / TrefK - 1 / TiK));
    } else {
      yi = Math.exp(paramVal * (1 / TrefK - 1 / TiK));
    }
    yData.push(yi);
  }

  // Fit using the other model
  const fitModel = model === 'ep' ? 'ar' : 'ep';
  const paramName = fitModel === 'ep' ? 'a_{EP}' : 'a_{Ar}';
  let bestFit = 0;
  let minRSS = Infinity;
  for (let a = 0; a <= 10000; a++) {
    const rss = yData.reduce((sum, yi, idx) => {
      let yPred;
      if (fitModel === 'ep') {
        yPred = (tempsK[idx] / TrefK) * Math.exp(a * (1 / TrefK - 1 / tempsK[idx]));
      } else {
        yPred = Math.exp(a * (1 / TrefK - 1 / tempsK[idx]));
      }
      const diff = yi - yPred;
      return sum + diff * diff;
    }, 0);
    if (rss < minRSS) {
      minRSS = rss;
      bestFit = a;
    }
  }

  //2 calculation
  const meanY = yData.reduce((s, v) => s + v, 0) / yData.length;
  const ssTot = yData.reduce((s, v) => s + Math.pow(v - meanY, 2), 0);
  const r2 = 1 - (minRSS / ssTot);

  // Update fit results display
  const fitResultsDiv = document.getElementById('fit-results');
  fitResultsDiv.innerHTML = `\\(${paramName} = ${formatNumber(bestFit)}\\) \\(r^2 = ${r2.toFixed(3)}\\)`;
  MathJax.typeset();

  // Prepare data for plot
  const trace1 = {
    x: tempsC,
    y: yData,
    mode: 'markers',
    name: 'Data',
    marker: {color:'#008000', size:8, line:{color:'#0000FF', width:1}}
  };

  // Generate fit curve points
  const xFit = [];
  const yFit = [];
  for (let i = 0; i < 200; i++) {
    const Ti = tmin + i * (tmax - tmin) / 199;
    const TiK = Ti + 273.15;
    xFit.push(Ti);
    let yPred;
    if (fitModel === 'ep') {
      yPred = (TiK / TrefK) * Math.exp(bestFit * (1 / TrefK - 1 / TiK));
    } else {
      yPred = Math.exp(bestFit * (1 / TrefK - 1 / TiK));
    }
    yFit.push(yPred);
  }

  const trace2 = {
    x: xFit,
    y: yFit,
    mode: 'lines',
    name: 'Fit',
    line: {color:'#008000', width:2}
  };

  const layout = {
    title: fitModel === 'ep' ?
      'Eyring–Polanyi model fit to Arrhenius data' :
      'Arrhenius model fit to Eyring–Polanyi data',
    xaxis: {title: 'T °C'},
    yaxis: {title: 'k(T) / k(T_{ref})'},
    legend: {x:0.1, y:0.9}
  };

  Plotly.newPlot('plot-container', [trace1, trace2], layout, {responsive:true});
}

// Run init on page load
window.addEventListener('load', init);
</script>
