<div id="controls-container">
  <span id="n-display">6</span>
  <button id="btn-n-3">3</button>
  <button id="btn-n-4">4</button>
  <button id="btn-n-5">5</button>
  <button id="btn-n-6">6</button>
  <button id="btn-n-7">7</button>
  <button id="btn-n-8">8</button>
  <button id="btn-n-9">9</button>
  <button id="btn-n-10">10</button>
</div>

<div id="canvas-container"></div>

<script>
let n = 6; // Default
const canvasWidth = 800;
const canvasHeight = 400;

const canvasContainer = document.getElementById('canvas-container');
let p5Instance;

function drawScene() {
  background('#FFFFFF');

  // Colors
  const colorYellow = '#fefd00';
  const colorRed    = '#ee1f24';
  const colorBlue   = '#0048a1';

  // Left polygon
  const R = 150;
  const cy = canvasHeight / 2;
  const cxLeft = canvasWidth / 4;

  const angleStep = Math.PI / n;
  let vertices = [];
  for (let k = 0; k <= 2 * n; k++) {
    const x = cxLeft + R * Math.cos(k * angleStep - Math.PI / 2);
    const y = cy + R * Math.sin(k * angleStep - Math.PI / 2);
    vertices.push({x, y});
  }

  const triColors = [colorYellow, colorRed, colorBlue];
  noStroke();
  for (let j = 1; j <= 2 * n; j++) {
    const col = triColors[(j - 1) % 3];
    fill(col);
    beginShape();
    vertex(cxLeft, cy);
    vertex(vertices[j - 1].x, vertices[j - 1].y);
    vertex(vertices[j].x, vertices[j].y);
    endShape(CLOSE);
  }

  // Right side rectangles
  const s = 2 * R * Math.sin(angleStep / 2); // base length
  const a = R * Math.cos(angleStep / 2);     // apothem

  const rectXStart = canvasWidth / 2 + 20;
  const rectYTop   = cy - a;
  const rectSpacing = 20;
  const numFull = Math.floor(n / 2);

  for (let k = 0; k < numFull; k++) {
    const x = rectXStart + k * (s + rectSpacing);
    const y = rectYTop;
    drawFullRectangle(x, y, s, a * 2);
  }

  // Odd n half-rectangle
  if (n % 2 === 1) {
    const x = rectXStart + numFull * (s + rectSpacing);
    const y = rectYTop;
    drawHalfRectangle(x, y, s, a * 2);
  }
}

function drawFullRectangle(x, y, w, h) {
  const cx = x + w / 2;
  const cy = y + h / 2;
  noStroke();

  // Top
  fill('#fefd00');
  beginShape();
  vertex(x, y);
  vertex(x + w, y);
  vertex(cx, cy);
  endShape(CLOSE);

  // Bottom
  fill('#ee1f24');
  beginShape();
  vertex(x, y + h);
  vertex(x + w, y + h);
  vertex(cx, cy);
  endShape(CLOSE);

  // Left
  fill('#0048a1');
  beginShape();
  vertex(x, y);
  vertex(x, y + h);
  vertex(cx, cy);
  endShape(CLOSE);

  // Right
  fill('#fefd00');
  beginShape();
  vertex(x + w, y);
  vertex(x + w, y + h);
  vertex(cx, cy);
  endShape(CLOSE);

  // Outline
  stroke('#000000');
  noFill();
  rect(x, y, w, h);
}

function drawHalfRectangle(x, y, w, h) {
  const cx = x + w / 2;
  const cy = y + h / 2;
  noStroke();

  // Top (Blue)
  fill('#0048a1');
  beginShape();
  vertex(x, y);
  vertex(x + w, y);
  vertex(cx, cy);
  endShape(CLOSE);

  // Bottom (Red)
  fill('#ee1f24');
  beginShape();
  vertex(x, y + h);
  vertex(x + w, y + h);
  vertex(cx, cy);
  endShape(CLOSE);

  // Outline
  stroke('#000000');
  noFill();
  rect(x, y, w, h);
}

function setup() {
  const cnv = createCanvas(canvasWidth, canvasHeight);
  cnv.parent('canvas-container');
  noLoop();
  drawScene();
}

function windowResized() {
  // Keep canvas size fixed, no resize needed
}

function updateState(newN) {
  n = newN;
  document.getElementById('n-display').textContent = n;
  // Update button styles
  for (let i = 3; i <= 10; i++) {
    document.getElementById(`btn-n-${i}`).classList.toggle('active', i === n);
  }
  redraw();
}

function redraw() {
  drawScene();
}

// Button event listeners
for (let i = 3; i <= 10; i++) {
  const btn = document.getElementById(`btn-n-${i}`);
  btn.addEventListener('click', () => updateState(i));
}

// Initialize default active button on load
document.getElementById(`btn-n-${n}`).classList.add('active');
</script>

</body>
</html>
